INSERT INTO `creature_text` VALUES (38387, 0, 0, '$n, hop into one of those B.C. Eliminators and wipe out the pygmies!', 14, 0, 100, 25, 0, 18259, 0, 49973, 0, 'Sassy Hardwrench to Player');
