INSERT INTO `creature_text` VALUES (38387, 3, 0, 'Goblin grenades versus gnomish land mines. Show them who\'s better, $n!', 12, 0, 100, 1, 0, 0, 0, 50095, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` VALUES (38387, 4, 0, 'I forget... did you take flying lessons back on Kezan?', 12, 0, 100, 6, 0, 0, 0, 50097, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` VALUES (38387, 5, 0, 'Better hurry up, $n. I think that Thrall has news on where our people are being held.', 12, 0, 100, 273, 0, 0, 0, 50099, 0, 'Sassy Hardwrench to Player');
INSERT INTO `creature_text` VALUES (38387, 6, 0, 'First she left you for Chip, and now the Trade Prince! She was never good enough for you, $n.', 12, 0, 100, 274, 0, 0, 0, 50114, 0, 'Sassy Hardwrench to Player');
