INSERT INTO `creature_text` VALUES (38415, 1, 0, 'Let us join your father\'s force\'s, Liam.  They\'ll need our help against Sylvanas.', 14, 0, 100, 0, 0, 19500, 0, 38361, 0, 'Lord Darius Crowley to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38426, 0, 0, 'The villagers were thankful to have Emberstone back.  They brought us a little something to help against the Forsaken!', 12, 0, 100, 0, 0, 19684, 0, 38322, 0, 'Lorna Crowley to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38432, 0, 0, 'You hear that, naga? $n is on $g his : her; way to settle our \"negotiations\"!', 14, 0, 100, 15, 0, 0, 0, 49938, 0, 'Megs Dreadshredder to Player');
INSERT INTO `creature_text` VALUES (38432, 1, 0, 'Everybody loves a pool pony. It\'s our fastest selling toy behind the Kablooey Bombs: Infant Edition.', 12, 0, 100, 1, 0, 0, 0, 49951, 0, 'Megs Dreadshredder to Player');
INSERT INTO `creature_text` VALUES (38432, 2, 0, 'That pygmy helm should fool them, at least for a little while. Get back to town!', 12, 0, 100, 25, 0, 0, 0, 44571, 0, 'Megs Dreadshredder to Player');
INSERT INTO `creature_text` VALUES (38441, 0, 0, 'I never liked that guy. He stole your girlfriend. I say, good riddance!', 12, 0, 100, 273, 0, 0, 0, 50108, 0, 'Ace to Player');
