INSERT INTO `creature_text` VALUES (38448, 1, 0, 'Little goblins? I remember when your race was created.', 14, 0, 100, 1, 0, 15139, 0, 38346, 0, 'Faceless of the Deep to Player');
INSERT INTO `creature_text` VALUES (38448, 2, 0, 'I care nothing for your threats or these naga. Prepare to be removed from existence.', 14, 0, 100, 25, 0, 15140, 0, 38349, 0, 'Faceless of the Deep to Player');
INSERT INTO `creature_text` VALUES (38448, 3, 0, 'Now, young $n, you will die!', 14, 0, 100, 0, 0, 15140, 0, 38359, 0, 'Faceless of the Deep to Player');
INSERT INTO `creature_text` VALUES (38469, 0, 0, 'Enough!', 14, 0, 100, 0, 0, 23517, 0, 7381, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (38469, 1, 0, 'Let\'s see how brave Gilneas gets on without its stubborn leader!', 14, 0, 100, 0, 0, 0, 0, 38368, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (38470, 0, 0, 'Block their retreat, Liam!  We\'ve got them right where we want them!', 14, 0, 100, 0, 0, 19725, 0, 38364, 0, 'King Genn Greymane to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38470, 1, 0, 'SYLVANAS!!', 14, 0, 100, 0, 0, 19726, 0, 38365, 0, 'King Genn Greymane to Krennan Aranas');
INSERT INTO `creature_text` VALUES (38470, 2, 0, 'LIAM!!  NO!!!', 14, 0, 100, 0, 0, 19727, 0, 38485, 0, 'King Genn Greymane to Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (38472, 0, 0, 'Die, intruders! None shall interfere with the Cult\'s plans!', 14, 0, 0, 0, 0, 0, 0, 38516, 0, 'Darnavan - SAY_DARNAVAN_AGGRO');
INSERT INTO `creature_text` VALUES (38472, 1, 0, 'Wh- where am I...?  What a nightmare I have had....  But this is no time to reflect, I have much information to report!', 12, 0, 0, 0, 0, 0, 0, 38512, 0, 'Darnavan - SAY_DARNAVAN_RESCUED');
