INSERT INTO `creature_text` VALUES (38490, 0, 0, '$n is infected with the Death Plague!', 41, 0, 100, 25, 0, 0, 0, 38679, 0, 'Rotting Frost Giant - Death Plague');
INSERT INTO `creature_text` VALUES (38494, 0, 0, '$n is infected with the Death Plague!', 41, 0, 100, 25, 0, 0, 0, 38679, 0, 'Rotting Frost Giant - Death Plague');
INSERT INTO `creature_text` VALUES (38507, 0, 0, 'Let us go at once.  We will track Sylvanas down.  For Gilneas!', 12, 0, 100, 15, 0, 0, 0, 38399, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` VALUES (38507, 1, 0, 'Follow Tobias Mistmantle into the Cathedral District.', 42, 0, 100, 0, 0, 0, 0, 38400, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` VALUES (38507, 2, 0, 'Let\'s do our best not to be seen, $n.', 12, 0, 100, 0, 0, 0, 0, 38401, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` VALUES (38507, 3, 0, 'Did you hear that, $n?  Sylvanas is headed to the cathedral.  Let\'s hurry!', 12, 0, 100, 0, 0, 0, 0, 38402, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` VALUES (38507, 4, 0, 'Quick!  Hide in the water!', 12, 0, 100, 0, 0, 0, 0, 38403, 0, 'Tobias Mistmantle to Player');
INSERT INTO `creature_text` VALUES (38517, 0, 0, 'Okay. We\'re going to move away from the exploding volcano and try to hook up with the orcs for the final assault on the Trade Prince. See you on the other side... I hope.', 12, 0, 100, 1, 0, 0, 0, 39240, 0, 'Slinky Sharpshiv to Player');
INSERT INTO `creature_text` VALUES (38530, 0, 0, 'You can assure Garrosh that this is a minor setback.  Our victory in Gilneas will be absolute.', 12, 0, 100, 1, 0, 20492, 0, 38422, 0, 'Lady Sylvanas Windrunner to Player');
INSERT INTO `creature_text` VALUES (38530, 1, 0, 'You\'d do well to watch your tone, General.  Neither you nor Garrosh have anything to worry about.  We\'ve ceased all production of the Plague, as he ordered.  We\'d never deploy it without his permission.', 12, 0, 100, 0, 0, 20493, 0, 38423, 0, 'Lady Sylvanas Windrunner to Player');
INSERT INTO `creature_text` VALUES (38530, 2, 0, 'Go with honor, General.', 12, 0, 100, 0, 0, 20494, 0, 38424, 0, 'Lady Sylvanas Windrunner to Player');
INSERT INTO `creature_text` VALUES (38530, 3, 0, 'What kind of question is that?  Of course we\'re deploying the Plague as planned!  Let the Gilneans enjoy their small victory.  Not even their bones will remain by tomorrow.', 12, 0, 100, 0, 0, 20495, 0, 38425, 0, 'Lady Sylvanas Windrunner to Player');
INSERT INTO `creature_text` VALUES (38533, 0, 0, 'It appears you\'re losing control of Gilneas, Sylvanas.  Garrosh fears he\'s going to have to carry out this invasion himself.', 12, 0, 100, 1, 0, 22620, 0, 38426, 0, 'General Warhowl to Player');
INSERT INTO `creature_text` VALUES (38533, 1, 0, 'You sound very confident, your Majesty.  I seriously hope you do not plan to use the Plague.  Garrosh has explicitly forbidden it.', 12, 0, 100, 274, 0, 22621, 0, 38427, 0, 'General Warhowl to Player');
INSERT INTO `creature_text` VALUES (38533, 2, 0, 'I will deliver my report to our leader, then.  By your leave, my lady.', 12, 0, 100, 66, 0, 22622, 0, 38428, 0, 'General Warhowl to Player');
INSERT INTO `creature_text` VALUES (38537, 0, 0, 'My Lady!  Should I order my men to stop the deployment of the Plague?  Or are we to continue as planned?', 12, 0, 100, 66, 0, 20917, 0, 38440, 0, 'High Executor Crenshaw to Player');
INSERT INTO `creature_text` VALUES (38537, 1, 0, 'As you wish!', 12, 0, 100, 66, 0, 20918, 0, 14708, 0, 'High Executor Crenshaw to Player');
INSERT INTO `creature_text` VALUES (38553, 0, 0, 'It\'s time to join the fray, $n!  With you on our side the scales will surely tip in our favor!', 14, 0, 100, 0, 0, 0, 0, 38513, 0, 'Krennan Aranas to Player');
INSERT INTO `creature_text` VALUES (38579, 0, 0, 'Free at last! It is over, my son. This is the moment of reckoning.', 14, 0, 0, 1, 0, 17397, 0, 38117, 0, 'Terenas Menethil - SAY_TERENAS_OUTRO_1');
INSERT INTO `creature_text` VALUES (38579, 1, 0, 'Rise up, champions of the Light!', 14, 0, 0, 0, 0, 17398, 0, 38118, 0, 'Terenas Menethil - SAY_TERENAS_OUTRO_2');
INSERT INTO `creature_text` VALUES (38606, 0, 0, 'What\'s this!?', 12, 0, 100, 5, 0, 17383, 0, 38201, 0, 'Lady Jaina Proudmoore to Player');
INSERT INTO `creature_text` VALUES (38606, 1, 0, 'He... he kept it? All this time, he kept it!?', 12, 0, 100, 5, 0, 17384, 0, 38202, 0, 'Lady Jaina Proudmoore');
INSERT INTO `creature_text` VALUES (38606, 2, 0, 'I knew! ', 12, 0, 100, 5, 0, 17385, 0, 38203, 0, 'Lady Jaina Proudmoore');
INSERT INTO `creature_text` VALUES (38606, 3, 0, 'I sensed a part of him still alive! Trapped... struggling... Oh, Arthas!', 12, 0, 100, 396, 0, 17386, 0, 38204, 0, 'Lady Jaina Proudmoore');
INSERT INTO `creature_text` VALUES (38606, 4, 0, 'Perhaps - perhaps he might someday remember what he once was.', 12, 0, 100, 396, 0, 17387, 0, 38205, 0, 'Lady Jaina Proudmoore');
INSERT INTO `creature_text` VALUES (38606, 5, 0, 'By the Light, may he at last find rest, free from the icy grip of that terrible blade.', 12, 0, 100, 396, 0, 17388, 0, 38206, 0, 'Lady Jaina Proudmoore');
INSERT INTO `creature_text` VALUES (38607, 0, 0, 'Oh, lad...', 12, 0, 100, 396, 0, 17421, 0, 38171, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text` VALUES (38607, 1, 0, 'How I miss those endless days in Lordaeron...', 12, 0, 100, 396, 0, 17422, 0, 38172, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text` VALUES (38607, 2, 0, '...sharpenin\' yer skill with this dull blade...', 12, 0, 100, 396, 0, 17423, 0, 38173, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text` VALUES (38607, 3, 0, '...forgin\' ya into a weapon meant to withstand the demands of a great destiny.', 12, 0, 100, 396, 0, 17424, 0, 38174, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text` VALUES (38607, 4, 0, 'Heh... Yeh sure put them skills ta use, didn\'t yeh, lad?', 12, 0, 100, 6, 0, 17425, 0, 38175, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text` VALUES (38607, 5, 0, 'If only I\'da been able ta stop yeh that day, how different things mighta been.', 12, 0, 100, 396, 0, 17426, 0, 38176, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text` VALUES (38607, 6, 0, 'If only I\'da never discovered that accursed blade...', 12, 0, 100, 396, 0, 17427, 0, 38177, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text` VALUES (38607, 7, 0, 'Farewell, Arthas... my brother.', 12, 0, 100, 396, 0, 17428, 0, 38178, 0, 'Muradin Bronzebeard');
INSERT INTO `creature_text` VALUES (38608, 0, 0, 'Arthas...', 12, 0, 100, 396, 0, 17402, 0, 38160, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` VALUES (38608, 1, 0, 'Alas, hero of Azeroth, you give me a greater gift than you know.', 12, 0, 100, 396, 0, 17403, 0, 38161, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` VALUES (38608, 2, 0, 'Long have I struggled to forgive the prince for his terrible transgressions.', 12, 0, 100, 396, 0, 17404, 0, 38162, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` VALUES (38608, 3, 0, 'My soul has been wracked with unbearable anxiety, dark thoughts... distancing me from the Light.', 12, 0, 100, 396, 0, 17405, 0, 38163, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` VALUES (38608, 4, 0, 'I recall clearly the gleam of pride in his eye as he stood before me, eager to defeat the enemies of the Light...', 12, 0, 100, 396, 0, 17406, 0, 38165, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` VALUES (38608, 5, 0, 'Eager to defend his people, no matter the cost.', 12, 0, 100, 396, 0, 17407, 0, 38166, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` VALUES (38608, 6, 0, 'It is this memory of Arthas that I choose to keep in my heart.', 12, 0, 100, 396, 0, 17408, 0, 38167, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` VALUES (38608, 7, 0, 'I shall always be in your debt, friend.', 12, 0, 100, 396, 0, 17409, 0, 38168, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` VALUES (38608, 8, 0, 'Thank you.', 12, 0, 100, 0, 0, 17410, 0, 38169, 0, 'Uther the Lightbringer');
INSERT INTO `creature_text` VALUES (38609, 0, 0, 'So, it is done.', 12, 0, 100, 396, 0, 17376, 0, 38179, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (38609, 1, 0, 'I had not dared to trust my senses - too many times has the Lich King made me to be a fool.', 12, 0, 100, 396, 0, 17377, 0, 38180, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (38609, 2, 0, 'Finally, he has been made to pay for the atrocities he imposed upon my people.', 12, 0, 100, 396, 0, 17378, 0, 38181, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (38609, 3, 0, 'May Azeroth never fail to remember the terrible price we paid for our weakness... for our pride.', 12, 0, 100, 396, 0, 17379, 0, 38183, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (38609, 4, 0, 'But what now, hero? What of those freed from his grasp, but still shackled to their mortal coils?', 12, 0, 100, 6, 0, 17380, 0, 38184, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (38609, 5, 0, 'Leave me. ', 12, 0, 100, 396, 0, 17381, 0, 38185, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (38609, 6, 0, 'I have much to ponder.', 12, 0, 100, 396, 0, 17382, 0, 38186, 0, 'Lady Sylvanas Windrunner');
INSERT INTO `creature_text` VALUES (38610, 0, 0, 'Darion, my son.', 12, 0, 100, 396, 0, 17414, 0, 38152, 0, 'Highlord Alexandros Mograine');
