INSERT INTO `creature_text` VALUES (42195, 13, 0, 'The guns, man the guns! Fire everything we\'ve got!', 14, 0, 100, 5, 0, 19672, 0, 42222, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 14, 0, 'Egah - all hands aft. One\'s aboard. Shoot it! Kill it!', 12, 0, 100, 5, 0, 19673, 0, 42462, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 15, 0, 'Somebody, DO SOMETHING! That\'s an order!', 12, 0, 100, 15, 0, 19674, 0, 42463, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 16, 0, 'Tamp down the inflammants. Batten up the gear carriage. Rudder for the shore.', 12, 0, 100, 1, 0, 19675, 0, 42478, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 17, 0, 'Everyone, to the parachutes!', 14, 0, 100, 15, 0, 19677, 0, 42479, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42195, 18, 0, 'Mayday! Mayday! Mayday! Bail out everyone. BAIL!', 14, 0, 100, 5, 0, 19676, 0, 42480, 0, 'Captain Krazz');
INSERT INTO `creature_text` VALUES (42230, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 10677, 0, 'combat Enrage');
INSERT INTO `creature_text` VALUES (42231, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 2384, 0, 'combat Frenzy');
INSERT INTO `creature_text` VALUES (42243, 0, 0, 'Aye aye, sir.', 12, 0, 100, 66, 0, 19597, 0, 46927, 0, 'Fergus Gearchum');
INSERT INTO `creature_text` VALUES (42243, 1, 0, 'Approaching the Thandol Span, sir.', 12, 0, 100, 25, 0, 19599, 0, 42167, 0, 'Fergus Gearchum');
INSERT INTO `creature_text` VALUES (42243, 2, 0, 'Oh, the Goblinity!', 14, 0, 100, 15, 0, 19600, 0, 42191, 0, 'Fergus Gearchum');
INSERT INTO `creature_text` VALUES (42243, 3, 0, 'Coming up on the Twilight Highlands, Captain. ', 12, 0, 100, 25, 0, 19601, 0, 42202, 0, 'Fergus Gearchum');
INSERT INTO `creature_text` VALUES (42243, 4, 0, 'Captain, sir! Alliance naval fleet sighted off the port bow.', 12, 0, 100, 25, 0, 19602, 0, 42204, 0, 'Fergus Gearchum');
INSERT INTO `creature_text` VALUES (42243, 5, 0, 'It\'s tangled up in the rigging.', 12, 0, 100, 1, 0, 19603, 0, 42464, 0, 'Fergus Gearchum');
INSERT INTO `creature_text` VALUES (42243, 6, 0, 'She\'s breaking up, sir!', 12, 0, 100, 5, 0, 19604, 0, 42477, 0, 'Fergus Gearchum');
INSERT INTO `creature_text` VALUES (42277, 0, 0, 'Fellow warriors of the Horde. Hear me now!', 14, 0, 100, 15, 0, 20542, 0, 42194, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 1, 0, 'Ahead hides our foe. Worshippers of chaos who seek to remake the world.', 14, 0, 100, 1, 0, 20564, 0, 42195, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 2, 0, 'A new world is coming, friends. But it is not the world of their design.', 14, 0, 100, 273, 0, 0, 0, 42196, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 3, 0, 'Together, we will crush the Twilight\'s Hammer, raze their stronghold,', 14, 0, 100, 25, 0, 0, 0, 42197, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 4, 0, 'salt the earth, and burn the bodies. All will tremble at our might. ', 14, 0, 100, 5, 0, 0, 0, 42198, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 5, 0, 'Then we will stand astride this world as its masters -', 14, 0, 100, 1, 0, 0, 0, 42199, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 6, 0, 'united in our conviction, unrivaled in strength, beholden to no one. ', 14, 0, 100, 5, 0, 0, 0, 42200, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 7, 0, 'Today, WE will remake the world. OUR world. FOR THE HORDE!', 14, 0, 100, 15, 0, 0, 0, 42201, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 8, 0, 'The Alliance steams in close formation without escort.', 14, 0, 100, 1, 0, 20543, 0, 42205, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 9, 0, 'Air guard, attack!! Strafe them now, while they cannot maneuver!', 14, 0, 100, 5, 0, 0, 0, 42206, 0, 'Garrosh Hellscream to Garrosh Formation Bunny');
INSERT INTO `creature_text` VALUES (42277, 10, 0, 'GET!', 14, 0, 100, 5, 0, 20544, 0, 42430, 0, 'Garrosh Hellscream to Garrosh\'s Airship');
INSERT INTO `creature_text` VALUES (42277, 11, 0, 'OFF!', 14, 0, 100, 5, 0, 0, 0, 42431, 0, 'Garrosh Hellscream to Garrosh\'s Airship');
INSERT INTO `creature_text` VALUES (42277, 12, 0, 'MY!', 14, 0, 100, 5, 0, 0, 0, 42432, 0, 'Garrosh Hellscream to Garrosh\'s Airship');
INSERT INTO `creature_text` VALUES (42277, 13, 0, 'SHIP!', 14, 0, 100, 5, 0, 21505, 0, 42433, 0, 'Garrosh Hellscream to Garrosh\'s Airship');
INSERT INTO `creature_text` VALUES (42291, 0, 0, 'Optical sensor malfunction detected! Shutting down...', 12, 0, 100, 0, 0, 10571, 0, 42702, 0, 'Crushcog Sentry-Bot');
INSERT INTO `creature_text` VALUES (42300, 0, 0, 'Look - dwarves. Hah! They sulk like prairie dogs in holes in the ground.', 12, 0, 100, 25, 0, 21501, 0, 42203, 0, 'Kor\'kron Air Marine');
INSERT INTO `creature_text` VALUES (42300, 1, 0, 'A winner.', 12, 0, 100, 1, 0, 21502, 0, 42212, 0, 'Kor\'kron Air Marine');
INSERT INTO `creature_text` VALUES (42300, 2, 0, 'It\'s an attack!', 14, 0, 100, 5, 0, 21503, 0, 42223, 0, 'Kor\'kron Air Marine');
INSERT INTO `creature_text` VALUES (42300, 3, 0, 'They\'re after the Warchief -', 12, 0, 100, 5, 0, 21504, 0, 42437, 0, 'Kor\'kron Air Marine');
INSERT INTO `creature_text` VALUES (42300, 4, 0, 'The Warchief\'s zeppelin is in flames! It\'s going down!', 12, 0, 100, 25, 0, 21505, 0, 42438, 0, 'Kor\'kron Air Marine');
INSERT INTO `creature_text` VALUES (42308, 0, 0, 'No kidding, rookie...', 12, 7, 100, 273, 0, 0, 0, 42227, 0, 'Lieutenant Horatio Laine to Player');
INSERT INTO `creature_text` VALUES (42308, 1, 0, 'Looks like they really put the cart...', 12, 7, 100, 0, 0, 0, 0, 42228, 0, 'Lieutenant Horatio Laine to Player');
INSERT INTO `creature_text` VALUES (42308, 2, 0, '...before the horse.', 12, 7, 100, 0, 0, 0, 0, 42229, 0, 'Lieutenant Horatio Laine to Player');
INSERT INTO `creature_text` VALUES (42308, 3, 0, 'That\'s good to hear, marshal. While $n has brought the perpetrators to justice, their ringleader is still on the loose. Perhaps with the aid of the Westfall Brigade we will finally be able to solve this case.', 12, 0, 100, 1, 0, 0, 0, 42506, 0, 'Lieutenant Horatio Laine to Player');
INSERT INTO `creature_text` VALUES (42308, 4, 0, 'Might I ask, what\'s with the worgen in the stocks?', 12, 0, 100, 6, 0, 0, 0, 42507, 0, 'Lieutenant Horatio Laine to Player');
INSERT INTO `creature_text` VALUES (42308, 5, 0, 'Wow, the marshal wasn\'t kidding.', 12, 0, 100, 0, 0, 0, 0, 42510, 0, 'Lieutenant Horatio Laine to Player');
INSERT INTO `creature_text` VALUES (42308, 6, 0, 'But is your bark...', 12, 0, 100, 0, 0, 0, 0, 42511, 0, 'Lieutenant Horatio Laine to Player');
INSERT INTO `creature_text` VALUES (42308, 7, 0, 'Worse than your bite?', 12, 0, 100, 0, 0, 0, 0, 42512, 0, 'Lieutenant Horatio Laine to Player');
INSERT INTO `creature_text` VALUES (42309, 0, 0, 'It\'s a bloodbath, lieutenant. They\'ve been murdered.', 12, 7, 100, 1, 0, 0, 0, 42224, 0, 'Stormwind Investigator to Player');
INSERT INTO `creature_text` VALUES (42309, 1, 0, 'Given the body temperature, I\'d say they\'ve been dead no more than 6 hours.', 12, 7, 100, 0, 0, 0, 0, 42225, 0, 'Stormwind Investigator to Player');
INSERT INTO `creature_text` VALUES (42309, 2, 0, 'Damn shame what they did to Old Blanchy...', 12, 7, 100, 0, 0, 0, 0, 42226, 0, 'Stormwind Investigator to Player');
INSERT INTO `creature_text` VALUES (42317, 0, 0, 'We assembled a mighty force and defeated Thermaplugg\'s servants, fighting our way into the city. But, the usurper Thermaplugg had one last trick up his sleeve...', 12, 0, 100, 1, 0, 20895, 0, 42408, 0, 'High Tinker Mekkatorque');
INSERT INTO `creature_text` VALUES (42317, 1, 0, 'Take a look at this holotable. I want to show you a bit about Operation: Gnomeregan.', 12, 0, 100, 25, 0, 12148, 0, 48277, 0, 'High Tinker Mekkatorque');
INSERT INTO `creature_text` VALUES (42317, 2, 0, 'We were forced to retreat. Thermaplugg continues to hide behind the radiation, sending his crony, Crushcog, to harass us here on the surface.', 12, 0, 100, 1, 0, 20896, 0, 42411, 0, 'High Tinker Mekkatorque');
INSERT INTO `creature_text` VALUES (42333, 0, 0, 'The world will be reborn in flames!', 14, 0, 100, 0, 0, 21634, 0, 46255, 0, 'High Priestess Azil to Player');
INSERT INTO `creature_text` VALUES (42333, 1, 0, 'Witness the power bestowed upon me by Deathwing! Feel the fury of earth!', 14, 0, 100, 0, 0, 21628, 0, 46253, 0, 'High Priestess Azil to High Priestess Azil');
INSERT INTO `creature_text` VALUES (42333, 2, 0, 'For my death, countless more will fall. The burden is now yours to bear.', 14, 0, 100, 0, 0, 21633, 0, 46251, 0, 'High Priestess Azil to Player');
INSERT INTO `creature_text` VALUES (42366, 0, 0, 'We\'re going to have a hard time handling this one alone. I sent a message to the dwarven capital of Ironforge asking for their support.', 12, 0, 100, 1, 0, 0, 0, 43111, 0, 'Kelsey Steelspark to Captain Tread Sparknozzle');
INSERT INTO `creature_text` VALUES (42383, 0, 0, 'Listen, $g pal:lady;, I don\'t want any trouble, ok? I didn\'t see who murdered \'em, but I sure heard it! Lot of yelling. Human voices... you dig? Now get out of here before I change my mind about beating you up and takin\' your shoes.', 12, 0, 100, 396, 0, 0, 0, 42380, 0, 'Transient to Player');
INSERT INTO `creature_text` VALUES (42383, 1, 0, 'I didn\'t see who killed \'em, $g bub:lady;, but I got a whiff. Smelled rich, kinda like you. Damn shame too. Furlbrows were a fixture around here. Nice people, always willin\' to share a meal or a patch of dirt.', 12, 0, 100, 396, 0, 0, 0, 42381, 0, 'Transient to Player');
INSERT INTO `creature_text` VALUES (42383, 2, 0, 'Who killed the Furlbrows? I\'ll tell you who killed the Furlbrows: KING VARIAN WRYNN, THAT\'S WHO! And he\'s killin\' the rest of us too. One bum at a time. The only thing I can tell you is that I saw some gnolls leavin\' the place a few hours before the law arrived.', 12, 0, 100, 5, 0, 0, 0, 42382, 0, 'Transient to Player');
INSERT INTO `creature_text` VALUES (42383, 3, 0, 'Between you, me, and the tree, murlocs killed the Furlbrows. Yep, saw \'em with my own two eyes. Think they\'d been casin\' the joint for days, maybe months. They left in a hurry once they got wind of \"Johnny Law\" and the idiot brigade over there...', 12, 0, 100, 273, 0, 0, 0, 42383, 0, 'Transient to Player');
INSERT INTO `creature_text` VALUES (42383, 4, 0, 'Judgment day comes! The brotherhood will be reborn!', 12, 7, 100, 0, 0, 0, 0, 42379, 0, 'Transient to Player');
INSERT INTO `creature_text` VALUES (42383, 4, 1, 'Now you\'ve gone and done it! TIME FOR THE FIST!', 12, 7, 100, 0, 0, 0, 0, 42389, 0, 'Transient to Player');
INSERT INTO `creature_text` VALUES (42383, 4, 2, 'You\'ll get nothing out of me!', 12, 7, 100, 0, 0, 0, 0, 42378, 0, 'Transient to Player');
INSERT INTO `creature_text` VALUES (42383, 4, 3, 'Livin\' like an animal is hard enough without your insults!', 12, 7, 100, 0, 0, 0, 0, 42386, 0, 'Transient to Player');
INSERT INTO `creature_text` VALUES (42383, 4, 4, 'I ain\'t tellin\' you a damn thing!', 12, 7, 100, 0, 0, 0, 0, 42385, 0, 'Transient to Player');
INSERT INTO `creature_text` VALUES (42383, 4, 5, 'Maybe I can sell your liver for some gold.', 12, 7, 100, 0, 0, 0, 0, 42387, 0, 'Transient to Player');
INSERT INTO `creature_text` VALUES (42383, 4, 6, 'You want my foot in your front or in your rear? Make the call.', 12, 7, 100, 0, 0, 0, 0, 42390, 0, 'Transient to Player');
INSERT INTO `creature_text` VALUES (42383, 4, 7, 'They died of natural causes...', 12, 7, 100, 0, 0, 0, 0, 42377, 0, 'Transient to Player');
INSERT INTO `creature_text` VALUES (42383, 4, 8, 'You rich punks are all the same!', 12, 7, 100, 0, 0, 0, 0, 42384, 0, 'Transient to Player');
INSERT INTO `creature_text` VALUES (42383, 4, 9, 'You can\'t buy me! DIE!', 12, 7, 100, 0, 0, 0, 0, 42374, 0, 'Transient to Player');
