INSERT INTO `creature_text` VALUES (42383, 6, 1, 'HAHAHAH! Good one, Mr. Penguin! GOOD ONE!', 12, 7, 100, 153, 0, 0, 0, 42354, 0, 'Transient');
INSERT INTO `creature_text` VALUES (42383, 6, 2, 'What happened to me? I used to be the king of Stormwind!', 12, 7, 100, 15, 0, 0, 0, 42357, 0, 'Transient');
INSERT INTO `creature_text` VALUES (42383, 6, 3, 'I don\'t think I\'m gonna make it...', 12, 7, 100, 18, 0, 0, 0, 42356, 0, 'Transient');
INSERT INTO `creature_text` VALUES (42383, 6, 4, 'Get off my land!', 12, 7, 100, 397, 0, 0, 0, 42355, 0, 'Transient');
INSERT INTO `creature_text` VALUES (42383, 6, 5, 'I miss being robbed by the Defias. At least they\'d leave you a little something to eat from time to time.', 12, 7, 100, 396, 0, 0, 0, 42360, 0, 'Transient');
INSERT INTO `creature_text` VALUES (42383, 6, 6, 'I\'m starving!', 12, 7, 100, 5, 0, 0, 0, 42353, 0, 'Transient');
INSERT INTO `creature_text` VALUES (42383, 6, 7, 'I wonder if it\'s possible to eat rocks? Got plenty of rocks around here. Just imagine it! I\'d be the richest person in the world for making that discovery!', 12, 7, 100, 4, 0, 0, 0, 42359, 0, 'Transient');
INSERT INTO `creature_text` VALUES (42383, 7, 0, 'JACKPOT!', 12, 7, 100, 5, 0, 0, 0, 42351, 0, 'Transient');
INSERT INTO `creature_text` VALUES (42383, 8, 0, 'Looks like I found us a savory and clean piece of dirt! Tonight we eat like kings, Mr. Penguin! Of course I\'ll share it with you! You\'re my best friend!', 12, 7, 100, 18, 0, 0, 0, 42352, 0, 'Transient');
INSERT INTO `creature_text` VALUES (42383, 9, 0, 'HEY! HEY YOU! GET OFF MY PROPERTY!', 12, 7, 100, 22, 0, 0, 0, 42361, 0, 'Transient');
INSERT INTO `creature_text` VALUES (42383, 10, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 1150, 0, 'Transient');
INSERT INTO `creature_text` VALUES (42383, 11, 0, 'What the hell happened here?', 12, 7, 100, 1, 0, 0, 0, 42318, 0, 'Transient');
INSERT INTO `creature_text` VALUES (42383, 12, 0, 'What\'s happened to us? We\'re in worse shape now than when the Defias ran amok!', 12, 7, 100, 6, 0, 0, 0, 42321, 0, 'Transient');
INSERT INTO `creature_text` VALUES (42383, 13, 0, 'Let\'s get out of here before they try and pin this on us.', 12, 7, 100, 1, 0, 0, 0, 42323, 0, 'Transient');
INSERT INTO `creature_text` VALUES (42383, 14, 0, 'We\'re just trying to get a decent meal and maybe a comfortable night\'s sleep.', 12, 7, 100, 0, 0, 0, 0, 42342, 0, 'Transient to Transient');
INSERT INTO `creature_text` VALUES (42383, 15, 0, 'Dirty, rotten pig-lickers. You\'ll get your come-uppance some day.', 12, 7, 100, 396, 0, 0, 0, 42347, 0, 'Transient to Transient');
INSERT INTO `creature_text` VALUES (42383, 16, 0, 'Let\'s get the hell out of here. I hope this place burns to the ground.', 12, 7, 100, 396, 0, 0, 0, 42348, 0, 'Transient to Transient');
INSERT INTO `creature_text` VALUES (42383, 17, 0, 'Thank you, kind and gentle stranger.', 12, 7, 100, 1, 0, 0, 0, 42486, 0, 'Homeless Stormwind Citizen to Westfall Stew');
INSERT INTO `creature_text` VALUES (42383, 17, 1, 'Westfall stew? I\'ll never forget this moment!', 12, 7, 100, 5, 0, 0, 0, 42487, 0, 'Homeless Stormwind Citizen to Westfall Stew');
INSERT INTO `creature_text` VALUES (42383, 17, 2, 'I\'d know that smell anywhere! Salma\'s famous stew!', 12, 7, 100, 5, 0, 0, 0, 42488, 0, 'Homeless Stormwind Citizen to Westfall Stew');
INSERT INTO `creature_text` VALUES (42383, 17, 3, 'Maybe... maybe life is worth living.', 12, 7, 100, 5, 0, 0, 0, 42489, 0, 'Homeless Stormwind Citizen to Westfall Stew');
INSERT INTO `creature_text` VALUES (42383, 17, 4, 'Bless you, friend.', 12, 7, 100, 1, 0, 0, 0, 42490, 0, 'Homeless Stormwind Citizen to Westfall Stew');
