INSERT INTO `creature_text` VALUES (43238, 7, 0, 'But the Horde has assembled an enormous host! A vast fleet of war machines and hardened veterans. Hellscream would as soon crush you as-', 12, 0, 100, 274, 0, 0, 0, 43301, 0, 'Horde Negotiator to Player');
INSERT INTO `creature_text` VALUES (43238, 8, 0, 'This is madness!', 12, 0, 100, 15, 0, 0, 0, 21954, 0, 'Horde Negotiator to Player');
