INSERT INTO `creature_text` VALUES (43260, 0, 0, 'Quit wasting words, worm. If you want the aid of the Dragonmaw, you should\'ve come on bent knee and with offerings of gold.', 12, 0, 100, 25, 0, 0, 0, 43306, 0, 'Warchief Mor\'ghor to Player');
