INSERT INTO `creature_text` VALUES (43324, 0, 0, 'Valiona, Theralion, put them in their place.', 14, 0, 100, 0, 0, 22063, 0, 47518, 3, 'Cho\'gall - Theralion and Valiona Intro');
INSERT INTO `creature_text` VALUES (43324, 1, 0, '(Come closer, closer.  Let us see your faces. All will be humbled before his power.)', 14, 0, 100, 0, 0, 22064, 0, 47538, 3, 'Cho\'gall - Theralion and Valiona Death');
INSERT INTO `creature_text` VALUES (43324, 2, 0, 'Welcome, mortals. (Frail little children). Few have laid eyes on this inner sanctum. (They reach out to touch the fire!)', 14, 0, 100, 0, 0, 22065, 0, 47585, 3, 'Cho\'gall - Ascendant Council 1');
INSERT INTO `creature_text` VALUES (43324, 3, 0, 'It is here the chosen realize their destiny. (One with eternity). And it is here that you will realize yours.(End of the flesh. End of the flesh)!', 14, 0, 100, 0, 0, 22066, 0, 47587, 3, 'Cho\'gall - Ascendant Council 2');
INSERT INTO `creature_text` VALUES (43324, 4, 0, 'Brothers of Twilight! The Hammer calls to you! (Fire water earth air). Leave your mortal shell behind. (Fire water earth air). Embrace your new forms for here and everafter! (Burn and drown and crush and suffocate). And use your gifts to destroy the unbelievers. (Burn and drown and crush and suffocate)!!', 14, 0, 100, 0, 0, 22067, 0, 47588, 3, 'Cho\'gall - Ascendant Council 3');
INSERT INTO `creature_text` VALUES (43324, 5, 0, 'Foolish mortals-(Usurper\'s children!) nothing you have done- (Spawn of a lesser god!) I am TRYING to speak here. (Words, words, words. The Master wants murder.) ALL falls to chaos. ALL will be destroyed. (Chaos, chaos!) Your work here today changes nothing. (Chaos, chaos, all things end) No mortal may see what you have and live. Your end has come.', 14, 0, 100, 0, 0, 22068, 0, 47591, 3, 'Cho\'gall - Intro');
INSERT INTO `creature_text` VALUES (43324, 6, 0, 'Enough! (To His will all flesh succumbs. From His kingdom, chaos comes.) Yes - what he said.', 14, 0, 100, 0, 0, 22081, 0, 47859, 3, 'Cho\'gall - Aggro');
INSERT INTO `creature_text` VALUES (43324, 7, 0, 'Behold the might of the hammer!', 14, 0, 100, 0, 0, 22079, 0, 47857, 3, 'Cho\'gall - Absorb Fire Elemental 1');
INSERT INTO `creature_text` VALUES (43324, 7, 1, 'Give me your strength!', 14, 0, 100, 0, 0, 22078, 0, 47856, 3, 'Cho\'gall - Absorb Fire Elemental 1l');
INSERT INTO `creature_text` VALUES (43324, 8, 0, '(Stop. Hammertime!)', 14, 0, 100, 0, 0, 22080, 0, 47858, 3, 'Cho\'gall - Absorb Shadow Lorb');
INSERT INTO `creature_text` VALUES (43324, 9, 0, '|TInterface\\Icons\\ability_rogue_shadowdance.blp:20|t%s begins to summon |cFFFF0000Corrupted Adherents|r to aid him!\n', 41, 0, 100, 0, 0, 0, 0, 44484, 3, 'Cho\'gall - Announce Corrupting Adherents');
INSERT INTO `creature_text` VALUES (43324, 10, 0, '(*Snort*)', 14, 0, 100, 0, 0, 22083, 0, 47861, 3, 'Cho\'gall - Corrupting Adherents 1');
INSERT INTO `creature_text` VALUES (43324, 10, 1, '(Infinite suffering!)', 14, 0, 100, 0, 0, 22082, 0, 47860, 3, 'Cho\'gall - Corrupting Adherents 2');
INSERT INTO `creature_text` VALUES (43324, 10, 2, 'More. More!', 14, 0, 100, 0, 0, 22085, 0, 47863, 3, 'Cho\'gall - Corrupting Adherents 3');
INSERT INTO `creature_text` VALUES (43324, 10, 3, '(Come, brothers! Feast on hope!)', 14, 0, 100, 0, 0, 22086, 0, 47864, 3, 'Cho\'gall - Corrupting Adherents 4');
INSERT INTO `creature_text` VALUES (43324, 11, 0, 'Cho\'gall beckons and casts |cFF006EFD|Hspell:91303|h[Conversion]|h|r!', 41, 0, 100, 0, 0, 0, 0, 49084, 3, 'Cho\'gall - Announce Conversion');
INSERT INTO `creature_text` VALUES (43324, 12, 0, '|TInterface\\Icons\\spell_shadow_shadetruesight.blp:20|t%s begins to cast |cFFFF0000|Hspell:82299|h[Fester Blood]|h|r!', 41, 0, 100, 0, 0, 0, 0, 45715, 3, 'Cho\'gall - Announce Fester Blood');
INSERT INTO `creature_text` VALUES (43324, 13, 0, 'Brother, it cannot be... (Destiny has come!) My strength... (Too proud! He speaks in blood! Listen!) My mind... (It is for His glory we serve!) What... is happening... (Gone is the dawn. Come shades of Twilight. Come madness, come havoc, come Infinite Night!)', 14, 0, 100, 0, 0, 22093, 0, 47868, 3, 'Cho\'gall - Phase 2');
INSERT INTO `creature_text` VALUES (43324, 14, 0, '|TInterface\\Icons\\spell_shadow_shadesofdarkness.blp:20|t%s begins to summon |cFFFF0000Darkened Creations|r to aid him!\n', 41, 0, 100, 0, 0, 0, 0, 47897, 3, 'Cho\'gall - Announce Darkened Creations');
INSERT INTO `creature_text` VALUES (43324, 15, 0, '(The claws of fate grasp the unbelievers!)', 14, 0, 100, 0, 0, 22094, 0, 47899, 3, 'Cho\'gall - Say Darkened Creations 1');
INSERT INTO `creature_text` VALUES (43324, 15, 1, '(The Master sees, the Master sees!)', 14, 0, 100, 0, 0, 22095, 0, 47900, 3, 'Cho\'gall - Say Darkened Creations 2');
INSERT INTO `creature_text` VALUES (43324, 15, 2, 'What madness have we wrought? (Not yet enough!)', 14, 0, 100, 0, 0, 22096, 0, 47901, 3, 'Cho\'gall - Say Darkened Creations 3');
INSERT INTO `creature_text` VALUES (43324, 15, 3, 'I...  can\'t take much more... (Brother, it merely begins!)', 14, 0, 100, 0, 0, 22097, 0, 47902, 3, 'Cho\'gall - Say Darkened Creations 4');
INSERT INTO `creature_text` VALUES (43324, 15, 4, '(The Master\'s eye sees all that was and all to be, or not!)', 14, 0, 100, 0, 0, 22098, 0, 47903, 3, 'Cho\'gall - Say Darkened Creations 5');
