INSERT INTO `creature_text` VALUES (43324, 16, 1, 'Everyone succumbs in the end.', 14, 0, 100, 0, 0, 22089, 0, 47883, 3, 'Cho\'gall - Slay 1');
INSERT INTO `creature_text` VALUES (43324, 16, 2, '(Now they see the darkness! Their eyes are open!)', 14, 0, 100, 0, 0, 22090, 0, 47884, 3, 'Cho\'gall - Slay 1');
INSERT INTO `creature_text` VALUES (43324, 16, 3, 'Now the Master has them.', 14, 0, 100, 0, 0, 22091, 0, 47885, 3, 'Cho\'gall - Slay 2');
INSERT INTO `creature_text` VALUES (43324, 16, 4, '(Chaos, madness! Like a hug for your brain.)', 14, 0, 100, 0, 0, 22092, 0, 47886, 3, 'Cho\'gall - Slay 3');
INSERT INTO `creature_text` VALUES (43324, 16, 5, '(Blood for the master!)', 14, 0, 100, 0, 0, 22069, 0, 47851, 3, 'Cho\'gall - Slay 4');
INSERT INTO `creature_text` VALUES (43324, 16, 6, '(Finish it! Finish them all!)', 14, 0, 100, 0, 0, 22072, 0, 47854, 3, 'Cho\'gall - Slay 5');
INSERT INTO `creature_text` VALUES (43324, 17, 0, 'It is finished. (The fate of all.)', 14, 0, 100, 0, 0, 22101, 0, 47892, 3, 'Cho\'gall - Evade');
INSERT INTO `creature_text` VALUES (43324, 18, 0, 'Brother, we are shattered. (But the Master... I still hear... so angry... Master...?)', 14, 0, 100, 0, 0, 22099, 0, 47893, 3, 'Cho\'gall - Death Normal');
