INSERT INTO `creature_text` VALUES (43396, 0, 0, 'Ah, Maloriak. How gracious of you to finally appear from that quarantine you call a laboratory. What have you got this time?', 12, 0, 100, 1, 0, 20091, 0, 43488, 1, 'Lord Victor Nefarius');
