INSERT INTO `creature_text` VALUES (46964, 0, 0, 'Ivar\'s pack failed horribly, so now he sends mercenaries. No matter, your journey ends in agony.', 14, 0, 100, 0, 0, 0, 0, 48834, 0, 'Lord Godfrey - Aggro Alliance');
