INSERT INTO `creature_text` VALUES (46964, 3, 0, '|TInterface\\Icons\\INV_FIREARM_2H_RIFLE_CATACLYSM_B_01.BLP:20|t%s begins to cast |cFFFF0000|Hspell:93520|h[Pistol Barrage]|h|r!', 41, 0, 100, 0, 0, 0, 0, 50242, 0, 'Lord Godfrey Pistol Barrage');
INSERT INTO `creature_text` VALUES (46964, 4, 0, 'I thank you for the clean death. I would not have done the same...', 14, 0, 100, 0, 0, 0, 0, 48838, 0, 'Lord Godfrey');
INSERT INTO `creature_text` VALUES (46965, 0, 0, 'Halfus! Hear me! (The Master calls, the Master wants.) Protect our secrets, Halfus. Destroy the intruders. (Murder for His glory. Murder for His hunger.)', 14, 0, 100, 0, 0, 22055, 0, 47478, 3, 'Cho\'gall');
INSERT INTO `creature_text` VALUES (46965, 1, 0, ' Flesh and sinew, weak but proud. Dare they part the Master\'s shroud? They stumble fumble groping blind. Finding fate and chaos intertwined.', 14, 0, 100, 0, 0, 22056, 0, 47481, 3, 'Cho\'gall');
INSERT INTO `creature_text` VALUES (46965, 2, 0, 'Brothers in chaos, the Twilight has come! (Shadows lengthen, endless night.) Deathwing has sundered this world. (Doors once opened never close.) So that its true Lords may return. (The Master comes, He comes He comes.)', 14, 0, 100, 0, 0, 22057, 0, 47485, 3, 'Cho\'gall');
INSERT INTO `creature_text` VALUES (46965, 3, 0, 'Brothers of the Hammer, to me! (Their will is still their own.) The unbelievers still stand. Destroy them! (Crush their bodies, crush their will.)', 14, 0, 100, 0, 0, 22060, 0, 47497, 3, 'Cho\'gall');
INSERT INTO `creature_text` VALUES (46965, 4, 0, 'Mortals, you merely separate the wheat from the chaff. (Unworthy, unworthy).', 14, 0, 100, 0, 0, 22062, 0, 47488, 3, 'Cho\'gall');
INSERT INTO `creature_text` VALUES (46968, 0, 0, 'The gryphon muscles tense and eyes tighten as soon as the bark is extended towards it.', 16, 0, 100, 0, 0, 0, 0, 47017, 0, 'Mullan Gryphon to Player');
INSERT INTO `creature_text` VALUES (46992, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 10677, 0, 'combat Enrage');
INSERT INTO `creature_text` VALUES (47006, 0, 0, 'Pathetic.', 12, 0, 100, 1, 0, 0, 0, 7234, 0, 'Packleader Ivar Bloodfang to Player');
INSERT INTO `creature_text` VALUES (47006, 1, 0, 'Keep moving forward. My men will dispose of this sorry bag of bones.', 12, 0, 100, 1, 0, 0, 0, 47100, 0, 'Packleader Ivar Bloodfang to Player');
INSERT INTO `creature_text` VALUES (47006, 2, 0, 'Control yourselves! We\'re here for Godfrey\'s head above all else. Relish in the spoils of war later!', 12, 0, 100, 5, 0, 0, 0, 47102, 0, 'Packleader Ivar Bloodfang to Player');
INSERT INTO `creature_text` VALUES (47006, 3, 0, 'We have to push forward if we\'re to put an end to him. Follow me, I\'ll open the courtyard so that you can spearhead our assault.', 12, 0, 100, 0, 0, 0, 0, 47103, 0, 'Packleader Ivar Bloodfang to Player');
INSERT INTO `creature_text` VALUES (47006, 4, 0, 'What a pathetic lock! Godfrey is going to have to try harder than this if he wants to defeat us.', 12, 0, 100, 0, 0, 0, 0, 47365, 0, 'Packleader Ivar Bloodfang to Player');
INSERT INTO `creature_text` VALUES (47006, 5, 0, 'The Bloodfang Berserkers begin to salivate heavily.', 16, 0, 100, 0, 0, 0, 0, 47101, 0, 'Packleader Ivar Bloodfang to Player');
INSERT INTO `creature_text` VALUES (47006, 6, 0, 'The Bloodfang come for you, worgen hunters!', 14, 0, 100, 15, 0, 0, 0, 47157, 0, 'Packleader Ivar Bloodfang to Player');
INSERT INTO `creature_text` VALUES (47006, 7, 0, 'I\'ve picked up Walden\'s foul scent. Fill the air with his blood!', 12, 0, 100, 1, 0, 0, 0, 47189, 0, 'Packleader Ivar Bloodfang to Player');
INSERT INTO `creature_text` VALUES (47006, 8, 0, 'Your time has finally come, Godfrey!', 14, 0, 100, 53, 0, 0, 0, 47366, 0, 'Packleader Ivar Bloodfang to Player');
INSERT INTO `creature_text` VALUES (47006, 9, 0, 'Attack, my brothers! Make him feel the fury of the pack!', 14, 0, 100, 25, 0, 0, 0, 47378, 0, 'Packleader Ivar Bloodfang to Player');
INSERT INTO `creature_text` VALUES (47006, 10, 0, 'You\'d best hurry if you want any of the spoils.', 12, 0, 100, 0, 0, 0, 0, 47384, 0, 'Packleader Ivar Bloodfang to Player');
INSERT INTO `creature_text` VALUES (47006, 11, 0, 'I don\'t know why the door is closed, heroes. My pack should have already taken care of Godfrey by now...', 12, 0, 100, 1, 0, 0, 0, 47403, 0, 'Packleader Ivar Bloodfang to Player');
INSERT INTO `creature_text` VALUES (47006, 12, 0, 'We will stand idle no longer! The time to act is now!', 12, 0, 100, 1, 0, 0, 0, 47404, 0, 'Packleader Ivar Bloodfang to Player');
INSERT INTO `creature_text` VALUES (47006, 13, 0, 'I\'ll make sure nothing interferes. Go now heroes! Kill Godfrey!', 12, 0, 100, 0, 0, 0, 0, 47418, 0, 'Packleader Ivar Bloodfang to Player');
INSERT INTO `creature_text` VALUES (47006, 14, 0, 'Crowley is a disgrace to our kind for giving in to the Forsaken. My pack will show them the true face of the worgen and the relentless fury that comes with our presence!', 12, 0, 100, 5, 0, 0, 0, 47364, 0, 'Packleader Ivar Bloodfang');
