INSERT INTO `creature_text` VALUES (48494, 0, 0, 'Prepare the Micro Mechachicken!', 14, 0, 100, 1, 0, 0, 0, 49101, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 1, 0, 'There\'s a great deal that you do not understand, my dear Greely. That is why you are the assistant and I... I am Hobart Grapplehammer!', 12, 0, 100, 1, 0, 0, 0, 49103, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 2, 0, 'Excellent. Now back away from the Ultra Concave Fissionable Testing Platform. Slowly, if you please.', 12, 0, 100, 1, 0, 0, 0, 49107, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 3, 0, 'Thank you. Now behold as I stabilize the highly unstable miniaturization process.', 12, 0, 100, 1, 0, 0, 0, 49108, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 4, 0, 'I am going to end the horrible problem of food storage cutting into the profits of the Bilgewater Cartel!', 12, 0, 100, 1, 0, 0, 0, 49110, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 5, 0, 'I\'ll soon be revered as the greatest goblin tinker of all times! Besides, that negligence case involving the creation of the gilgoblins will never stick.', 12, 0, 100, 1, 0, 0, 0, 49111, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 6, 0, 'Flip the switch!', 12, 0, 100, 1, 0, 0, 0, 49112, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 7, 0, 'Quite right, Greely. You passed today\'s pop quiz.', 12, 0, 100, 1, 0, 0, 0, 49115, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 8, 0, 'I am now flipping the switch!', 12, 0, 100, 1, 0, 0, 0, 49116, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 9, 0, 'Greely, can you feel it? My moment of success is at hand!', 12, 0, 100, 1, 0, 0, 0, 49117, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 10, 0, 'Grocery store owners everywhere will owe me a percentage of their profits!', 12, 0, 100, 1, 0, 0, 0, 49118, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 11, 0, 'I\'m flipping the second switch!', 12, 0, 100, 1, 0, 0, 0, 49119, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 12, 0, 'The micro mechachicken begins to grow and Greely starts sneaking away from the machine.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 13, 0, 'Wait... something\'s wrong!', 12, 0, 100, 0, 0, 0, 0, 49120, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 14, 0, 'I know... I know! Where\'s the override? The override?!', 14, 0, 100, 0, 0, 0, 0, 49122, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 15, 0, 'Here goes nothing, I always loved you, Greely!', 14, 0, 100, 0, 0, 0, 0, 49124, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 16, 0, 'Flipping the override switch now!', 14, 0, 100, 0, 0, 0, 0, 49125, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 17, 0, 'The micro mechachicken quickly shrinks back to normal.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 18, 0, 'Are we still alive?', 12, 0, 100, 0, 0, 0, 0, 49127, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 19, 0, 'Well of course we are, my dear Greely. And look there. The Micro Mechachicken\'s stabilization appears to be a complete success!', 12, 0, 100, 1, 0, 0, 0, 49129, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 20, 0, 'The micro mechachicken explodes.', 16, 0, 100, 0, 0, 0, 0, 0, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48494, 21, 0, 'I haven\'t the foggiest notion what you\'re talking about, Miss Greely.', 12, 0, 100, 1, 0, 0, 0, 49133, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48496, 0, 0, 'You don\'t have to yell, Hobart, I\'m standing right here. Sheesh!', 12, 0, 100, 6, 0, 0, 0, 49102, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48496, 1, 0, 'There. One Micro Mechachicken. I\'ll never understand how you come up with these names.', 12, 0, 100, 1, 0, 0, 0, 49103, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48496, 2, 0, 'Greely sighs and rolls her eyes.', 16, 0, 100, 0, 0, 0, 0, 49105, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48496, 3, 0, 'Yes, Doctor Grapplehammer.', 12, 0, 100, 1, 0, 0, 0, 49106, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48496, 4, 0, 'The assistant shakes her head.', 16, 0, 100, 0, 0, 0, 0, 49109, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48496, 5, 0, 'Greely coughs.', 16, 0, 100, 0, 0, 0, 0, 49113, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48496, 6, 0, 'Um, Hobart, you\'re at the controls.', 12, 0, 100, 1, 0, 0, 0, 49114, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48496, 7, 0, 'Ya think? Hobart, the Micro Mechachicken is destabilizing. It\'s expanding!', 12, 0, 100, 1, 0, 0, 0, 49121, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48496, 8, 0, 'It\'s the one on the far right! Hurry, Hobart! It\'s going to eggsplode!', 14, 0, 100, 5, 0, 0, 0, 49123, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48496, 9, 0, 'WHAT?!!!', 14, 0, 100, 5, 0, 0, 0, 49126, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48496, 10, 0, 'We\'re still alive, Doctor Grapplehammer, despite your best efforts.', 12, 0, 100, 1, 0, 0, 0, 49128, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48496, 11, 0, 'Is that egg on your face?', 12, 0, 100, 1, 0, 0, 0, 49131, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48496, 12, 0, 'Hobart\'s jaw hits the ground.', 16, 0, 100, 0, 0, 0, 0, 49130, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48496, 13, 0, 'Now what\'s this about you \'always loved me\', Grapplehammer?', 12, 0, 100, 1, 0, 0, 0, 49132, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48496, 14, 0, 'We can both agree on that, doctor.', 12, 0, 100, 1, 0, 0, 0, 49134, 0, 'say Text');
INSERT INTO `creature_text` VALUES (48499, 0, 0, '$n? What are you doing here? And is that Keegan Firebeard behind you?', 12, 0, 100, 6, 0, 0, 0, 48736, 0, 'Fanny Thundermar to Player');
INSERT INTO `creature_text` VALUES (48499, 1, 0, 'Took yer sweet time, didn\'tcha?', 12, 0, 100, 5, 0, 0, 0, 48737, 0, 'Fanny Thundermar to Player');
INSERT INTO `creature_text` VALUES (48499, 2, 0, 'Aye, they was getting\' a wee handsy, so I took care of it. C\'mon then, I\'m done here.', 12, 0, 100, 1, 0, 0, 0, 48738, 0, 'Fanny Thundermar to Player');
INSERT INTO `creature_text` VALUES (48500, 0, 0, 'Aye! We\'re here to rescue you!', 12, 0, 100, 5, 0, 0, 0, 48739, 0, 'Keegan Firebeard to Player');
INSERT INTO `creature_text` VALUES (48500, 1, 0, 'The ogres - ya kilt \'em!', 12, 0, 100, 274, 0, 0, 0, 48740, 0, 'Keegan Firebeard to Player');
INSERT INTO `creature_text` VALUES (48500, 2, 0, 'Did you see that, $n? What a woman!', 12, 0, 100, 15, 0, 0, 0, 48741, 0, 'Keegan Firebeard to Player');
INSERT INTO `creature_text` VALUES (48530, 0, 0, 'Be sure to talk to the MacGraffs, inside the sanctuary. They\'ll be orchestratin\' the ceremonies.', 12, 0, 100, 1, 0, 0, 0, 48775, 0, 'Fanny Thundermar to Player');
INSERT INTO `creature_text` VALUES (48727, 0, 0, 'Leave now... you do not deserve to suffer the crystal\'s wrath...', 12, 0, 100, 0, 0, 0, 0, 48898, 0, 'Released Highborne Spirit');
INSERT INTO `creature_text` VALUES (48727, 0, 1, 'Relief at last...', 12, 0, 100, 0, 0, 0, 0, 48896, 0, 'Released Highborne Spirit');
INSERT INTO `creature_text` VALUES (48727, 0, 2, 'Thank you...', 12, 0, 100, 0, 0, 0, 0, 48897, 0, 'Released Highborne Spirit');
INSERT INTO `creature_text` VALUES (48727, 0, 3, 'The crystal has returned. You must leave...', 12, 0, 100, 0, 0, 0, 0, 48900, 0, 'Released Highborne Spirit');
INSERT INTO `creature_text` VALUES (48732, 0, 0, 'We command the power of the elements. Storm, Earth and Fire!  I\'d never dress like an oversized chicken and shoot moon beams from the sky. ', 12, 0, 100, 0, 0, 0, 0, 48984, 0, 'Stormcaller Jalara to Player');
INSERT INTO `creature_text` VALUES (48740, 0, 0, 'You don\'t understand! I had to study it! I HAD TO!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'on Aggro Text');
INSERT INTO `creature_text` VALUES (48740, 1, 0, 'Forgive... me...', 12, 0, 100, 0, 0, 0, 0, 48947, 0, 'on Death Text');
INSERT INTO `creature_text` VALUES (48743, 0, 0, 'I thought for sure I was going to be this smelly beast\'s dinner!', 14, 0, 100, 0, 0, 0, 0, 48948, 0, 'Hargoth Dimblaze to Tentacle of Iso\'rath');
INSERT INTO `creature_text` VALUES (48758, 0, 0, 'Keep yer eyes open, $n! That road is crawling with Dragonmaw.', 12, 0, 100, 5, 0, 0, 0, 49051, 0, 'Hammelhand to Player');
INSERT INTO `creature_text` VALUES (48789, 0, 0, 'That foul thing got stains all over my new robes!', 14, 0, 100, 0, 0, 0, 0, 48969, 0, 'Stormcaller Jalara to Tentacle of Iso\'rath');
INSERT INTO `creature_text` VALUES (48793, 0, 0, 'I\'m... so... dizzy. ', 14, 0, 100, 0, 0, 0, 0, 48973, 0, 'Earthmender Duarn to Tentacle of Iso\'rath');
INSERT INTO `creature_text` VALUES (48795, 0, 0, 'Thanks fer savin\' me! When you passed out, I thought we were done fer!', 14, 0, 100, 0, 0, 0, 0, 48974, 0, 'Stormcaller Mylra to Tentacle of Iso\'rath');
INSERT INTO `creature_text` VALUES (48917, 0, 0, 'Woohoo! We got us a convoy!', 12, 0, 100, 4, 0, 0, 0, 49052, 0, 'Wildhammer Lookout to Player');
INSERT INTO `creature_text` VALUES (48964, 0, 0, 'Ah, Chimaeron, truly a living testament to my scientific prowess. I reworked and twisted his form countless times over the years, and the final result is truly something to behold.', 14, 0, 100, 0, 0, 23362, 0, 49063, 3, 'Lord Victor Nefarius to Player');
INSERT INTO `creature_text` VALUES (48964, 1, 0, 'Stop fighting yourself this instant, Chimaeron!', 14, 0, 100, 0, 0, 23364, 0, 49065, 3, 'Lord Victor Nefarius to Chimaeron');
INSERT INTO `creature_text` VALUES (48964, 2, 0, 'This fight has gotten a bit stale; allow me to spice things up!', 14, 0, 100, 0, 0, 23365, 0, 49066, 3, 'Lord Victor Nefarius to Chimaeron');
INSERT INTO `creature_text` VALUES (48964, 3, 0, 'Hmm... a shame to lose that experiment.', 14, 0, 100, 0, 0, 23361, 0, 49067, 3, 'Lord Victor Nefarius to Player');
INSERT INTO `creature_text` VALUES (48964, 4, 0, 'Mortals that fancy themselves HEROES have entered the Broken Hall. Oh, I do hope this \"raid\" will amuse me more than the last.', 14, 0, 100, 0, 0, 23379, 0, 49053, 3, 'Lord Victor Nefarius to Player');
INSERT INTO `creature_text` VALUES (48964, 5, 0, 'Welcome to my personal sanctum, heroes! I hope you find the accommodations to your liking. Herein you will find many of my failed experiments and ultimately my greatest triumph!', 14, 0, 100, 0, 0, 20090, 0, 49415, 3, '');
INSERT INTO `creature_text` VALUES (48964, 6, 0, 'Finally someone to deal with that unsightly pest! You have my thanks, \"heroes.\"', 14, 0, 100, 0, 0, 20085, 0, 49410, 3, '');
INSERT INTO `creature_text` VALUES (48964, 7, 0, 'That malfunctioning piece of junk was murder on the repair bills.', 14, 0, 100, 0, 0, 20086, 0, 49411, 3, 'Lord Victor Nefarius to Player');
INSERT INTO `creature_text` VALUES (48964, 8, 0, 'Impressive. You have managed to destroy one of my most horrific creations, a task I had thought impossible until now.', 14, 0, 100, 0, 0, 20088, 0, 49413, 3, '');
INSERT INTO `creature_text` VALUES (48964, 9, 0, 'Now that was really unnecessary.', 14, 0, 100, 0, 0, 20087, 0, 49412, 3, '');
INSERT INTO `creature_text` VALUES (48964, 10, 0, 'We never did see eye to eye... Good riddance I say!', 14, 0, 100, 0, 0, 20089, 0, 49414, 3, '');
INSERT INTO `creature_text` VALUES (49020, 0, 0, 'Now hold yer breeks. The zeppelin will take a minute or so to pull around.', 12, 0, 100, 25, 0, 0, 0, 49357, 0, 'Fargo Flintlocke');
INSERT INTO `creature_text` VALUES (49020, 1, 0, 'Bah, predictable! They\'ll see it comin\'!', 12, 0, 100, 274, 0, 0, 0, 49358, 0, 'Fargo Flintlocke to \"Doc\" Schweitzer');
INSERT INTO `creature_text` VALUES (49020, 2, 0, 'But once ol\' $p here blasts through their bulkhead in a hail of splintered wood, blood, and phlegm then starts crawlin\' across the deck with both legs broken, oh aye, they\'ll not see THAT comin\' by a sweet mile, no sir!', 12, 0, 100, 5, 0, 0, 0, 50092, 0, 'Fargo Flintlocke to \"Doc\" Schweitzer');
INSERT INTO `creature_text` VALUES (49020, 3, 0, 'It\'s almost here. Get ready!', 12, 0, 100, 22, 0, 0, 0, 49359, 0, 'Fargo Flintlocke');
INSERT INTO `creature_text` VALUES (49020, 4, 0, 'She\'s here! Get in that cannon, damnit! Get ready to fire!', 12, 0, 100, 22, 0, 0, 0, 49361, 0, 'Fargo Flintlocke');
INSERT INTO `creature_text` VALUES (49026, 0, 0, 'A big day, Firebeard. The Wildhammer united can never be stopped!', 12, 0, 100, 5, 0, 0, 0, 49199, 0, 'Kurdran Wildhammer to Player');
INSERT INTO `creature_text` VALUES (49026, 1, 0, 'Never doubt it. Here, allow me to give you my wedding gift.', 12, 0, 100, 1, 0, 0, 0, 49200, 0, 'Kurdran Wildhammer to Player');
INSERT INTO `creature_text` VALUES (49026, 2, 0, 'The only surviving egg of my late gryphon, Sky\'ree.  Honor it and care for it; her child is yours.', 12, 0, 100, 273, 0, 0, 0, 49201, 0, 'Kurdran Wildhammer to Player');
INSERT INTO `creature_text` VALUES (49026, 3, 0, 'I\'ll not hear a word of it. The egg is yours. I want you to see how committed I am to this union.', 12, 0, 100, 25, 0, 0, 0, 49202, 0, 'Kurdran Wildhammer to Player');
INSERT INTO `creature_text` VALUES (49026, 4, 0, 'And how committed the Alliance is to the Wildhammer cause!', 12, 0, 100, 5, 0, 0, 0, 49203, 0, 'Kurdran Wildhammer to Player');
INSERT INTO `creature_text` VALUES (49026, 5, 0, 'Ah, look. Here comes $n, escorting your bride!', 12, 0, 100, 25, 0, 0, 0, 49204, 0, 'Kurdran Wildhammer to Player');
INSERT INTO `creature_text` VALUES (49027, 0, 0, 'I hope you\'re right, Kurdran old friend.', 12, 0, 100, 6, 0, 0, 0, 49205, 0, 'Keegan Firebeard to Player');
INSERT INTO `creature_text` VALUES (49027, 1, 0, 'Kurdran - no, I couldn\'t! Sky\'ree was a legend, I can\'t-', 12, 0, 100, 274, 0, 0, 0, 49206, 0, 'Keegan Firebeard to Player');
INSERT INTO `creature_text` VALUES (49027, 2, 0, 'Aye. Thank you, Kurdran.', 12, 0, 100, 2, 0, 0, 0, 49207, 0, 'Keegan Firebeard to Player');
INSERT INTO `creature_text` VALUES (49027, 3, 0, 'I do.', 12, 0, 100, 273, 0, 0, 0, 49208, 0, 'Keegan Firebeard to Grundy MacGraff');
INSERT INTO `creature_text` VALUES (49028, 0, 0, 'Where\'s your \"honor\" now, Horde scum?', 12, 0, 100, 0, 0, 0, 0, 50166, 0, 'Tidebreaker Sailor to Axebite Infantry');
INSERT INTO `creature_text` VALUES (49034, 0, 0, 'Ladies and gentledwarves, we gather here in the shadow of Thunderstrike to honor the union of Fanny Thundermar and Keegan Firebeard.', 12, 0, 100, 1, 0, 0, 0, 49209, 0, 'Grundy MacGraff to Player');
INSERT INTO `creature_text` VALUES (49034, 1, 0, 'May their bond be as eternal as the wind, waves, stone and fire. ', 12, 0, 100, 274, 0, 0, 0, 49210, 0, 'Grundy MacGraff to Player');
INSERT INTO `creature_text` VALUES (49034, 2, 0, 'Do you, Keegan and Fanny, accept one another into your lives, that two become as one, in union everlasting?', 12, 0, 100, 6, 0, 0, 0, 49211, 0, 'Grundy MacGraff to Player');
INSERT INTO `creature_text` VALUES (49034, 3, 0, 'Then, by the powers vested in me... the powers vested in me...', 12, 0, 100, 1, 0, 0, 0, 49212, 0, 'Grundy MacGraff to Player');
INSERT INTO `creature_text` VALUES (49034, 4, 0, '...BY BLOOD AND SHADOW!', 12, 0, 100, 15, 0, 0, 0, 49213, 0, 'Grundy MacGraff to Player');
INSERT INTO `creature_text` VALUES (49034, 5, 0, 'I shall consume your lifeblood in the name of the master!!', 12, 0, 100, 5, 0, 0, 0, 49214, 0, 'Grundy MacGraff to Player');
INSERT INTO `creature_text` VALUES (49045, 0, 0, 'Augh appears from the distance!', 41, 0, 0, 0, 0, 0, 0, 50638, 0, 'Augh - Intro 1');
INSERT INTO `creature_text` VALUES (49045, 1, 0, 'GAAAH! How you kill croc?!', 12, 0, 0, 53, 0, 0, 0, 49169, 0, 'Augh - Intro 2');
INSERT INTO `creature_text` VALUES (49045, 2, 0, 'Augh smart! Augh already steal treasure while you no looking!', 12, 0, 0, 1, 0, 0, 0, 49170, 0, 'Augh - Intro 3');
INSERT INTO `creature_text` VALUES (49045, 3, 0, 'Augh da boss! Oh yeah!', 12, 0, 0, 0, 0, 0, 0, 49171, 0, 'Augh Taunt');
INSERT INTO `creature_text` VALUES (49045, 4, 0, 'Augh steal your treasure. Uhn uhn uhnnn!', 12, 0, 0, 0, 0, 0, 0, 49173, 0, 'Augh Taunt');
INSERT INTO `creature_text` VALUES (49045, 5, 0, 'Who bad?! Augh bad!! Ugn!', 12, 0, 0, 0, 0, 0, 0, 49172, 0, 'Augh Taunt');
INSERT INTO `creature_text` VALUES (49139, 0, 0, 'Blowin\' em\' to bits, boss!', 12, 0, 100, 0, 0, 0, 0, 49191, 0, 'Helix Crew - Throw Bomb');
INSERT INTO `creature_text` VALUES (49141, 0, 0, 'Greetings, $n.', 12, 0, 100, 0, 0, 0, 0, 49305, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49141, 1, 0, 'The Shadow Grave is this way.  Follow me, $n.', 12, 0, 100, 1, 0, 0, 0, 49295, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49141, 2, 0, 'This way!', 12, 0, 100, 1, 0, 0, 0, 49296, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49141, 3, 0, 'Now, where could those supplies be?', 12, 0, 100, 1, 0, 0, 0, 49297, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49141, 4, 0, 'Maybe they\'re over here?', 12, 0, 100, 1, 0, 0, 0, 49298, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49141, 5, 0, 'Hmm...', 12, 0, 100, 1, 0, 0, 0, 49299, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49141, 6, 0, 'No, not over here.', 12, 0, 100, 1, 0, 0, 0, 49300, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49141, 7, 0, 'Hey, give me a hand, $n!  I can\'t find the supplies that Mordo needed!', 12, 0, 100, 1, 0, 0, 0, 49301, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49141, 8, 0, 'Let\'s see now... where could they be...', 12, 0, 100, 1, 0, 0, 0, 49302, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49141, 9, 0, 'Nice work!  You\'ve found them.  Let\'s bring these back to Mordo.', 12, 0, 100, 1, 0, 0, 0, 49303, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49141, 10, 0, 'I saw someone up there whose jaw fell off.  I wonder if Mordo can fix him up?', 12, 0, 100, 1, 0, 0, 0, 49304, 0, 'Darnell');
INSERT INTO `creature_text` VALUES (49163, 0, 0, 'SAVE THE WEDDING!', 41, 0, 100, 0, 0, 0, 0, 49223, 0, 'Kirthaven Wedding Vehicle to Player');
INSERT INTO `creature_text` VALUES (49176, 0, 0, 'You gained Freeza seeds!', 42, 0, 100, 0, 0, 0, 0, 49686, 0, 'Brazie the Botanist to Player');
INSERT INTO `creature_text` VALUES (49176, 1, 0, 'You gained Strangler Vine seeds!', 42, 0, 100, 0, 0, 0, 0, 49630, 0, 'Brazie the Botanist to Player');
INSERT INTO `creature_text` VALUES (49176, 2, 0, 'Zombies ate your brains!', 41, 0, 100, 0, 0, 14560, 0, 49692, 0, 'Brazie the Botanist');
INSERT INTO `creature_text` VALUES (49178, 0, 0, 'FOOD FOR GROLNAR!', 14, 0, 100, 0, 0, 0, 0, 49256, 0, 'Grolnar the Berserk');
INSERT INTO `creature_text` VALUES (49212, 0, 0, 'The bombs are set! Run to the balcony and jump to escape!', 42, 0, 100, 0, 0, 0, 0, 49290, 0, 'Skyshredder Bomb Controller Bunny to Player');
INSERT INTO `creature_text` VALUES (49212, 1, 0, 'BOOM!', 42, 0, 100, 0, 0, 0, 0, 15395, 0, 'Skyshredder Bomb Controller Bunny to Player');
INSERT INTO `creature_text` VALUES (49226, 0, 0, 'Hmm... the Omnotron Defense System. Centuries ago these constructs were considered the Dwarves\' greatest technical achievements. With so many counters to each construct\'s attacks, I\'ll have to rectify these designs for them to provide me any entertainment.', 14, 0, 100, 0, 0, 23374, 0, 49054, 0, 'Lord Victor Nefarius to Player');
INSERT INTO `creature_text` VALUES (49226, 1, 0, 'Lightning Conductor works much better in a crowd, yes?', 14, 0, 100, 0, 0, 23377, 0, 49057, 0, 'Lord Victor Nefarius to Player');
INSERT INTO `creature_text` VALUES (49226, 2, 0, 'Were you planning on using Toxitron\'s chemicals to damage the other constructs? Clever plan, let me ruin that for you.', 14, 0, 100, 0, 0, 23378, 0, 49058, 0, 'Lord Victor Nefarius to Toxitron');
INSERT INTO `creature_text` VALUES (49226, 3, 0, 'You would like to move away from that, wouldn\'t you? Too bad.', 14, 0, 100, 0, 0, 23375, 0, 49055, 0, 'Lord Victor Nefarius to Toxitron');
INSERT INTO `creature_text` VALUES (49226, 4, 0, 'Stupid Dwarves and your fascination with runes! Why would you create something that would help your enemy?', 14, 0, 100, 0, 0, 23376, 0, 49056, 0, 'Lord Victor Nefarius to Toxitron');
INSERT INTO `creature_text` VALUES (49230, 0, 0, 'Who are you calling a monster? You\'re the monster! I\'m just a man who died.', 12, 0, 100, 1, 0, 0, 0, 49345, 0, 'Marshal');
INSERT INTO `creature_text` VALUES (49231, 0, 0, 'I see. Well then, let\'s get to work, $n! The Dark Lady needs us, right?', 12, 0, 100, 1, 0, 0, 0, 49351, 0, 'Valdred');
INSERT INTO `creature_text` VALUES (49231, 1, 0, 'Valdred Moray, reporting for duty, sir!', 14, 0, 100, 1, 0, 0, 0, 49408, 0, 'Valdred');
INSERT INTO `creature_text` VALUES (49234, 0, 0, '|TInterface\\Icons\\spell_holy_prayerofshadowprotection.blp:20|t The Beast Unleashed begins to emit an |cFFFF0000|Hspell:93717|h[Aura of Darkness]|h|r!', 41, 0, 100, 53, 0, 14046, 0, 50286, 0, 'The Beast Unleashed');
INSERT INTO `creature_text` VALUES (49234, 1, 0, 'Uulwi gag erh\'ongg w\'ssh.', 14, 0, 100, 53, 0, 14046, 0, 32599, 0, 'The Beast Unleashed');
INSERT INTO `creature_text` VALUES (49252, 0, 0, 'Oh, like you coulda done any better.', 12, 0, 100, 5, 0, 0, 0, 50085, 0, 'Fargo Flintlocke to Player');
INSERT INTO `creature_text` VALUES (49275, 0, 0, 'Get it! Kill it!', 12, 0, 100, 5, 0, 14046, 0, 49403, 0, 'Keely Dunwald');
INSERT INTO `creature_text` VALUES (49277, 0, 0, 'It\'s one of them!', 12, 0, 100, 0, 0, 14046, 0, 49218, 0, 'Iain Firebeard');
INSERT INTO `creature_text` VALUES (49278, 0, 0, 'Stop that thing!', 12, 0, 100, 0, 0, 14046, 0, 49221, 0, 'Cliff Thundermar');
INSERT INTO `creature_text` VALUES (49279, 0, 0, 'For the Wildhammer!', 14, 0, 100, 0, 0, 14046, 0, 50337, 0, 'Russell Brower to The Beast Unleashed');
