INSERT INTO `creature_text` VALUES (49427, 0, 0, 'I found this fascinating specimen in the lava underneath this very room. Magmaw should provide an adequate challenge for your pathetic little band.', 14, 0, 100, 0, 0, 23367, 0, 49060, 0, 'Nefarian - Intro 1');
INSERT INTO `creature_text` VALUES (49427, 1, 0, 'On second thought, what fun is an \"adequate\" challenge?', 14, 0, 100, 0, 0, 23369, 0, 49061, 0, 'Nefarian - Intro 2');
INSERT INTO `creature_text` VALUES (49427, 2, 0, 'Inconceivable! You may actually defeat my lava worm! Perhaps I can help... tip the scales.', 14, 0, 100, 0, 0, 23368, 0, 49782, 0, 'Nefarian - Low Health Heroic');
INSERT INTO `creature_text` VALUES (49427, 3, 0, 'You won? How... disappointing. I could have easily watched you all continue to stand in the fire.', 14, 0, 100, 0, 0, 23366, 0, 49062, 0, 'Nefarian - Death Heroic');
INSERT INTO `creature_text` VALUES (49429, 0, 0, 'You hear a noise from above the cabin door!', 41, 0, 100, 0, 0, 0, 0, 49476, 0, 'Vanessa van Cleef to Player');
INSERT INTO `creature_text` VALUES (49429, 1, 0, 'I\'ve been waiting a long time for this, you know.', 12, 0, 100, 1, 0, 24613, 0, 49477, 0, 'Vanessa van Cleef to Player');
INSERT INTO `creature_text` VALUES (49429, 2, 0, 'Biding my time, building my forces, studying the minds of my enemies.', 12, 0, 100, 1, 0, 24614, 0, 49478, 0, 'Vanessa van Cleef to Player');
INSERT INTO `creature_text` VALUES (49429, 3, 0, 'I was never very good at hand-to-hand combat, you know.  Not like my father.', 12, 0, 100, 1, 0, 24615, 0, 49479, 0, 'Vanessa van Cleef to Player');
INSERT INTO `creature_text` VALUES (49429, 4, 0, 'But I always excelled at poisons.', 12, 0, 100, 1, 0, 24616, 0, 49480, 0, 'Vanessa van Cleef to Player');
INSERT INTO `creature_text` VALUES (49429, 5, 0, 'Especially venoms that affect the mind.', 12, 0, 100, 1, 0, 24617, 0, 49481, 0, 'Vanessa van Cleef to Player');
INSERT INTO `creature_text` VALUES (49429, 6, 0, 'Vanessa injects you with the Nightmare Elixir!', 41, 0, 100, 0, 0, 0, 0, 49483, 0, 'Vanessa van Cleef to Player');
INSERT INTO `creature_text` VALUES (49454, 0, 0, 'Activate the steam valves to free yourself!', 41, 0, 100, 0, 0, 0, 0, 49680, 0, 'Vanessa\'s Trap Bunny to Player');
