INSERT INTO `creature_text` VALUES (49477, 0, 0, 'I fear time may not be on our side, Ilthalaine. The battle may carry to Teldrassil itself if we do not act soon.', 12, 0, 100, 0, 0, 0, 0, 49682, 0, 'Huntress Sandrya Moonfall');
INSERT INTO `creature_text` VALUES (49478, 0, 0, 'We will leave you to your training, Ilthalaine. This is your area of expertise.', 12, 0, 100, 0, 0, 0, 0, 49683, 0, 'Dentaria Silverglade');
