INSERT INTO `creature_text` VALUES (49479, 3, 0, 'The ramp up to Aldrassil is just in sight over there. Circle around and find Tenaron up top.', 12, 0, 100, 0, 0, 0, 0, 49667, 0, 'Dentaria Silverglade');
INSERT INTO `creature_text` VALUES (49480, 0, 0, 'You\'ve come to help, $c? Let us stay together for a while.', 12, 0, 100, 0, 0, 0, 0, 49550, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 1, 0, 'The corruption goes much further than these spiders. I fear the true source is not within this cave.', 12, 0, 100, 0, 0, 0, 0, 49673, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 1, 1, 'His body screams for death, but his soul', 12, 0, 100, 0, 0, 0, 0, 49672, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 1, 2, 'I weep at these atrocities we commit. It is almost unbearable.', 12, 0, 100, 0, 0, 0, 0, 49674, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 1, 3, 'Corruption this deep could not have come from outside.', 12, 0, 100, 0, 0, 0, 0, 49675, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 1, 4, 'It almost seems as if he was born with this corruption inside of him.', 12, 0, 100, 0, 0, 0, 0, 49676, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 1, 5, 'They are surely unredeemable, but it still hurts to see them die.', 12, 0, 100, 0, 0, 0, 0, 49670, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 1, 6, 'The corruption is buried deep within him.', 12, 0, 100, 0, 0, 0, 0, 49669, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 2, 0, 'This totem has been corrupting the eggs! It seems a greater threat looms. The Gnarlpine remain tainted by something most foul.', 12, 0, 100, 0, 0, 0, 0, 49697, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 3, 0, 'My dear friends... I\'m sorry..', 12, 0, 100, 0, 0, 0, 0, 49671, 0, 'Tarindrella');
INSERT INTO `creature_text` VALUES (49480, 4, 0, 'I\'m going to stay and do what I can to free these spiders. Thank you for your help, $n.', 12, 0, 100, 0, 0, 0, 0, 49549, 0, 'Tarindrella');
