INSERT INTO `creature_text` VALUES (51140, 4, 0, '|TInterface\\Icons\\spell_nature_healingway.blp:32|t  Your Lifebinder Boon will heal others while restoring your health and mana.', 42, 0, 100, 0, 0, 0, 0, 49877, 0, 'Calen to Player');
INSERT INTO `creature_text` VALUES (51159, 0, 0, 'There - they\'ve fallen!', 12, 0, 100, 5, 0, 21514, 0, 49450, 0, 'Calen to Player');
INSERT INTO `creature_text` VALUES (51159, 1, 0, 'Mother, stay still. Your wounds are great.', 12, 0, 100, 1, 0, 21515, 0, 49451, 0, 'Calen to Player');
INSERT INTO `creature_text` VALUES (51159, 2, 0, 'Deathwing - he lives!', 12, 0, 100, 0, 0, 0, 0, 49452, 0, 'Calen to Player');
INSERT INTO `creature_text` VALUES (51159, 3, 0, 'Mortal, take the dragon queen. Carry her to safety. Now! RUN! Get her away from here!', 12, 0, 100, 5, 0, 21520, 0, 49453, 0, 'Calen to Player');
INSERT INTO `creature_text` VALUES (51159, 4, 0, 'I will delay the Aspect of Death. Take her to safety. GO!', 12, 0, 100, 25, 0, 21521, 0, 49454, 0, 'Calen to Player');
INSERT INTO `creature_text` VALUES (51171, 0, 0, 'Calen, no!', 12, 0, 100, 0, 0, 0, 0, 49439, 0, 'Alexstrasza the Life-Binder to Calen');
INSERT INTO `creature_text` VALUES (51186, 0, 0, 'The black aspect\'s blood is cursed. Wherever it was shed - nothing will grow for ten thousand years.', 12, 0, 100, 0, 0, 21050, 0, 49435, 0, 'Alexstrasza the Life-Binder to Calen');
INSERT INTO `creature_text` VALUES (51186, 1, 0, 'The Earth Warder... he is dead.', 12, 0, 100, 0, 0, 21050, 0, 49434, 0, 'Alexstrasza the Life-Binder to Calen');
INSERT INTO `creature_text` VALUES (51186, 2, 0, 'But it is over. We can work now to bottle up the horrors he has unleashed.', 12, 0, 100, 0, 0, 21051, 0, 49437, 0, 'Alexstrasza the Life-Binder to Calen');
INSERT INTO `creature_text` VALUES (51186, 3, 0, 'Impossible!', 12, 0, 100, 0, 0, 0, 0, 5673, 0, 'Alexstrasza the Life-Binder to Calen');
INSERT INTO `creature_text` VALUES (51354, 0, 0, 'Mortal, I want you to bear witness to this, but for your own safety stay back. Calen, you too.', 12, 0, 100, 1, 0, 21045, 0, 49429, 0, 'Alexstrasza the Life-Binder to Deathwing Intro Cutscene Vehicle [INTERNAL ONLY]');
INSERT INTO `creature_text` VALUES (51354, 1, 0, 'Your life isn\'t yours to throw away, Calen. Should I fail, the fate of this world may rest on your shoulders.', 12, 0, 100, 5, 0, 21046, 0, 49430, 0, 'Alexstrasza the Life-Binder to Deathwing Intro Cutscene Vehicle [INTERNAL ONLY]');
INSERT INTO `creature_text` VALUES (51354, 2, 0, 'It pains me, Neltharion - but I must end you, as I have ended Malygos.', 12, 0, 100, 1, 0, 21047, 0, 49431, 0, 'Alexstrasza the Life-Binder to Deathwing Intro Cutscene Vehicle [INTERNAL ONLY]');
INSERT INTO `creature_text` VALUES (51354, 3, 0, 'I see the hollow metal shell of a once great ally, and the precious gift of the Titans wasted.', 12, 0, 100, 274, 0, 21048, 0, 49432, 0, 'Alexstrasza the Life-Binder to Deathwing Intro Cutscene Vehicle [INTERNAL ONLY]');
INSERT INTO `creature_text` VALUES (51355, 0, 0, 'Lifebinder! Do you presume that I am at your beck and call? I have a world to unmake.', 12, 0, 100, 1, 0, 20275, 0, 49441, 0, 'Deathwing to Player');
INSERT INTO `creature_text` VALUES (51355, 1, 0, '\"End\" me? Lifebinder, you think life is yours to take away?', 12, 0, 100, 11, 0, 20276, 0, 49442, 0, 'Deathwing to Deathwing Intro Cutscene Vehicle [INTERNAL ONLY]');
INSERT INTO `creature_text` VALUES (51355, 2, 0, 'Life is weak, mortal, fleeting ... fragile.', 12, 0, 100, 274, 0, 0, 0, 49637, 0, 'Deathwing to Deathwing Intro Cutscene Vehicle [INTERNAL ONLY]');
INSERT INTO `creature_text` VALUES (51355, 3, 0, 'Death is final. Death is eternal. Death is ... my realm.', 12, 0, 100, 5, 0, 0, 0, 49638, 0, 'Deathwing to Deathwing Intro Cutscene Vehicle [INTERNAL ONLY]');
INSERT INTO `creature_text` VALUES (51355, 4, 0, 'Look upon me and you see death incarnate, the unmaker of worlds.', 12, 0, 100, 6, 0, 0, 0, 49639, 0, 'Deathwing to Deathwing Intro Cutscene Vehicle [INTERNAL ONLY]');
INSERT INTO `creature_text` VALUES (51355, 5, 0, 'Then witness my new gifts, bestowed by this world\'s true masters.', 12, 0, 100, 5, 0, 20277, 0, 49443, 0, 'Deathwing to Deathwing Intro Cutscene Vehicle [INTERNAL ONLY]');
INSERT INTO `creature_text` VALUES (51356, 0, 0, 'I will not leave your side.', 12, 0, 100, 274, 0, 21510, 0, 49447, 0, 'Calen to Deathwing Intro Cutscene Vehicle [INTERNAL ONLY]');
INSERT INTO `creature_text` VALUES (51356, 1, 0, 'Yes, mother.', 12, 0, 100, 2, 0, 21512, 0, 49448, 0, 'Calen to Deathwing Intro Cutscene Vehicle [INTERNAL ONLY]');
