INSERT INTO `creature_text` VALUES (51434, 1, 0, 'Of course, ma\'am.  At once!', 12, 0, 100, 66, 0, 20888, 0, 45014, 0, 'Millhouse Manastorm to Player');
INSERT INTO `creature_text` VALUES (51434, 2, 0, 'If you don\'t mind me asking.  How did we manage to recover the fragment?  How did we infiltrate our enemies\' forces without their knowledge?', 12, 0, 100, 6, 0, 20887, 0, 45015, 0, 'Millhouse Manastorm to Player');
INSERT INTO `creature_text` VALUES (51493, 0, 0, 'This changes nothing. (Nothing nothing changes. Crush them, crush.)', 12, 0, 100, 1, 0, 22102, 0, 47430, 0, 'Cho\'gall to Player');
INSERT INTO `creature_text` VALUES (51493, 1, 0, 'What of our agents in Stormwind? (Stormwind! The eye, the eye sees into the city heart.) Bring me the eye.', 12, 0, 100, 1, 0, 22103, 0, 47431, 0, 'Cho\'gall to Player');
INSERT INTO `creature_text` VALUES (51493, 2, 0, '(Blaspheme! Blaspheme disgust hate hate...) No matter. Where one eye closes, another opens. (The Master sees all! The Master knows all!) We will know our enemy\'s hearts, for they are weak. The eye. Now.', 12, 0, 100, 36, 0, 22105, 0, 47433, 0, 'Cho\'gall to Player');
INSERT INTO `creature_text` VALUES (51493, 3, 0, 'The shadow of the Master covers this world... (Darkness, all darkness.) Our enemies fight one another, across the Highland. Fools. (Blood, blood.) WAIT! There is another!', 12, 0, 100, 0, 0, 22106, 0, 47436, 0, 'Cho\'gall to Eye of Twilight Vehicle');
