INSERT INTO `creature_text` VALUES (55869, 2, 0, 'Feel my hatred!', 14, 0, 100, 0, 0, 25787, 0, 56326, 0, 'Alizabal - Seething Hate 1');
INSERT INTO `creature_text` VALUES (55869, 2, 1, 'My hate will consume you!', 14, 0, 100, 0, 0, 25789, 0, 56328, 0, 'Alizabal - Seething Hate 2');
