INSERT INTO `creature_text` VALUES (55869, 6, 1, 'I still hate you.', 14, 0, 100, 0, 0, 25781, 0, 56318, 0, 'Alizabal - Slay 2');
INSERT INTO `creature_text` VALUES (55869, 6, 2, 'Do you hate me? Good.', 14, 0, 100, 0, 0, 25782, 0, 56319, 0, 'Alizabal - Slay 3');
INSERT INTO `creature_text` VALUES (55869, 6, 3, 'I didn\'t hate that.', 14, 0, 100, 0, 0, 25784, 0, 56321, 0, 'Alizabal - Slay 4');
INSERT INTO `creature_text` VALUES (55869, 7, 0, 'I hate... every one of you...', 14, 0, 100, 0, 0, 25778, 0, 56323, 0, 'Alizabal - Death');
INSERT INTO `creature_text` VALUES (55869, 8, 0, 'I hate incompetent raiders.', 14, 0, 100, 0, 0, 25780, 0, 56317, 0, 'Alizabal - Disengage');
INSERT INTO `creature_text` VALUES (56099, 0, 0, 'I will cleanse whatever corruption I can; my fire will not harm you.', 14, 0, 100, 0, 0, 26498, 0, 56772, 1, 'Alexstrasza - Introduce Ability');
INSERT INTO `creature_text` VALUES (56099, 1, 0, 'No! Such power! Deathwing\'s summoning of the final Cataclysm will destroy all life on Azeroth. Quickly, we must interrupt him!', 14, 0, 100, 0, 0, 26500, 0, 55773, 1, 'Alexstrasza - Cataclysm');
INSERT INTO `creature_text` VALUES (56099, 2, 0, 'He\'s completely mad!!', 14, 0, 100, 0, 0, 26499, 0, 56774, 1, 'Alexstrasza - Phase 2');
INSERT INTO `creature_text` VALUES (56099, 3, 0, 'Excellent work. The fire of my heart glows with a brilliant purity unmatched; every spark of it I will channel into the Dragon Soul.', 14, 0, 100, 0, 0, 26497, 0, 56773, 1, 'Alexstrasza - Charge Dragon Soul');
INSERT INTO `creature_text` VALUES (56100, 0, 0, 'I will bring you closer to the Emerald Dream. Seek safety there when the fight becomes too intense.', 14, 0, 100, 0, 0, 26142, 0, 56776, 1, 'Ysera - Introduce Ability');
INSERT INTO `creature_text` VALUES (56100, 1, 0, 'Deathwing is conjuring the final Cataclysm; even the Emerald Dream trembles. If we are to stop the spell, we must attack him together.', 14, 0, 100, 0, 0, 26144, 0, 56781, 1, 'Ysera - Cataclysm');
INSERT INTO `creature_text` VALUES (56100, 2, 0, 'Such rage I have never seen...', 14, 0, 100, 0, 0, 26143, 0, 56777, 1, 'Ysera - Phase 2');
INSERT INTO `creature_text` VALUES (56100, 3, 0, 'We are one step closer. The unknowable, transcendent power of the Emerald Dream I now give unto the Dragon Soul.', 14, 0, 100, 0, 0, 26141, 0, 56775, 1, 'Ysera - Charge Dragon Soul');
INSERT INTO `creature_text` VALUES (56101, 0, 0, 'I will charge you with arcane energy to blast your foes.', 14, 0, 100, 0, 0, 26259, 0, 56752, 1, 'Kalecgos - Introduce Ability');
INSERT INTO `creature_text` VALUES (56101, 1, 0, 'The Destroyer is gathering all his might for a blow that will split the world. Attack him, now! We must stop the final Cataclysm!', 14, 0, 100, 0, 0, 26261, 0, 55776, 1, 'Kalecgos - Cataclysm');
INSERT INTO `creature_text` VALUES (56101, 2, 0, 'Is he... coming apart?', 14, 0, 100, 0, 0, 26260, 0, 56753, 1, 'Kalecgos - Phase 2');
INSERT INTO `creature_text` VALUES (56101, 3, 0, 'Well done! I will realign the flow of mana and fill the Dragon Soul with all my arcane might.', 14, 0, 100, 0, 0, 26258, 0, 56751, 1, 'Kalecgos - Charge Dragon Soul');
INSERT INTO `creature_text` VALUES (56102, 0, 0, 'I will slow the Destroyer\'s attacks when I can.', 14, 0, 100, 0, 0, 25949, 0, 55786, 1, 'Nozdormu - Introduce Ability');
INSERT INTO `creature_text` VALUES (56102, 1, 0, 'Hurry, heroes. In mere moments Deathwing\'s Cataclysm will complete what he begun and end the world. Join me in the attack, now!', 14, 0, 100, 0, 0, 25951, 0, 55774, 1, 'Nozdormu - Cataclysm');
INSERT INTO `creature_text` VALUES (56102, 2, 0, 'Press the attack, heroes.', 14, 0, 100, 0, 0, 25950, 0, 56770, 1, 'Nozdormu - Phase 2');
INSERT INTO `creature_text` VALUES (56102, 3, 0, 'It is time. I will expend everything to bind every thread here, now, around the Dragon Soul. What comes to pass will NEVER be undone.', 14, 0, 100, 0, 0, 26600, 0, 56769, 1, 'Nozdormu - Charge Dragon Soul');
INSERT INTO `creature_text` VALUES (56103, 0, 0, 'It is done; at last the Destroyer has met his end. Now, we can begin to heal our world.', 14, 0, 100, 0, 0, 25901, 0, 55768, 0, 'Thrall - Pre Fight');
INSERT INTO `creature_text` VALUES (56103, 1, 0, 'Aspects! Aid the heroes as best you can.', 14, 0, 100, 0, 0, 25895, 0, 56737, 0, 'Thrall - Saay Phase Two');
