INSERT INTO `creature_text` VALUES (8421, 0, 0, 'Fools, I knew that if I played upon my brother\'s feeble emotions, he would send \'rescuers.\'', 12, 0, 100, 0, 0, 0, 0, 4394, 0, 'Dorius');
INSERT INTO `creature_text` VALUES (8421, 1, 0, 'How easy it was to manipulate you into recovering the last Suntara stone from those imbeciles of the Twilight\'s Hammer.', 12, 0, 100, 0, 0, 0, 0, 4395, 0, 'Dorius');
INSERT INTO `creature_text` VALUES (8421, 2, 0, 'When I stumbled upon the Suntara stones at the Grimesilt Digsite, the power of Ragnaros surged through my being. It was Ragnaros that gave me a purpose.', 12, 0, 100, 0, 0, 0, 0, 4396, 0, 'Dorius');
INSERT INTO `creature_text` VALUES (8421, 3, 0, 'It was the will of Ragnaros that Obsidion be built. Obsidion will destroy the Blackrock orcs of Blackrock Spire, uniting us with our bretheren in the fiery depths.', 12, 0, 100, 0, 0, 0, 0, 4397, 0, 'Dorius');
INSERT INTO `creature_text` VALUES (8421, 4, 0, 'And ultimately, it was Ragnaros that named me when I was reborn as an acolyte of fire: Lathoric... Lathoric the Black.', 12, 0, 100, 0, 0, 0, 0, 4398, 0, 'Dorius');
INSERT INTO `creature_text` VALUES (8421, 5, 0, '%s\'s laughter trails off...', 16, 0, 100, 0, 0, 0, 0, 4399, 0, 'Dorius');
INSERT INTO `creature_text` VALUES (8421, 6, 0, 'Bravo! Bravo! Good show. What? You thought I was dead?', 12, 0, 100, 0, 0, 0, 0, 4393, 0, 'Dorius');
