INSERT INTO `creature_text` VALUES (10541, 0, 1, 'It\'s 428,000 degrees Kraklenheit... What\'s happening, hot stuff!?', 12, 0, 100, 0, 0, 0, 0, 6006, 0, 'Krakle\'s Thermometer');
INSERT INTO `creature_text` VALUES (10541, 0, 2, 'DING! 428,000 degrees Kraklenheit, exactly! Well, approximately. Almost. Somewhere around there...', 12, 0, 100, 0, 0, 0, 0, 6007, 0, 'Krakle\'s Thermometer');
INSERT INTO `creature_text` VALUES (10541, 1, 0, '8536 degrees Kraklenheit is the reading... Tired of me yet?', 12, 0, 100, 0, 0, 0, 0, 6414, 0, 'Krakle\'s Thermometer');
INSERT INTO `creature_text` VALUES (10541, 2, 0, '*sigh* ...113 degrees Kraklenheit. Can\'t you do any better?', 12, 0, 100, 0, 0, 0, 0, 6412, 0, 'Krakle\'s Thermometer');
