INSERT INTO `creature_text` VALUES (11832, 0, 0, 'We will locate the origin of the Nightmare through the fragments you collected, $n. From there we will pull Eranikus through a rift in the Dream. Steel yourself, $c. We are inviting the embodiment of the Nightmare into our world.', 12, 0, 100, 0, 0, 0, 0, 11282, 0, 'Keeper Remulos Say 0');
INSERT INTO `creature_text` VALUES (11832, 1, 1, 'The rift will be opened there, above Lake Elun\'ara. Prepare yourself, $n. Eranikus\'s entry into our world will be wrought with chaos and strife.\n', 12, 0, 100, 0, 0, 0, 0, 11290, 0, 'Keeper Remulos Say 1');
INSERT INTO `creature_text` VALUES (11832, 2, 2, 'He will stop at nothing to get to Malfurion\'s physical manifestation. That must not happen... We must keep the beast occupied long enough for Tyrande to arrive.', 12, 0, 100, 0, 0, 0, 0, 11291, 0, 'Keeper Remulos Say 2');
INSERT INTO `creature_text` VALUES (11832, 3, 3, 'Defend Nighthaven, hero...', 12, 0, 100, 0, 0, 0, 0, 11292, 0, 'Keeper Remulos Say 3');
INSERT INTO `creature_text` VALUES (11832, 4, 4, 'Fiend! Face the might of Cenarius!', 14, 0, 100, 0, 0, 0, 0, 11293, 0, 'Keeper Remulos Say 4');
INSERT INTO `creature_text` VALUES (11832, 5, 5, 'Who is the predictable one, beast? Surely you did not think that we would summon you on top of Malfurion? Your redemption comes, Eranikus. You will be cleansed of this madness - this corruption.\n', 14, 0, 100, 0, 0, 0, 0, 11295, 0, 'Keeper Remulos Say 4');
INSERT INTO `creature_text` VALUES (11832, 6, 6, 'Hurry, $n! We must find protective cover!', 12, 0, 100, 0, 0, 0, 0, 11300, 0, 'Keeper Remulos Say 6');
INSERT INTO `creature_text` VALUES (11832, 7, 7, 'Please, champion, protect our people.', 12, 0, 100, 0, 0, 0, 0, 11301, 0, 'Keeper Remulos Say 7');
INSERT INTO `creature_text` VALUES (11832, 8, 8, 'It will be done, Eranikus. Be well, ancient one.', 12, 0, 100, 0, 0, 0, 0, 11303, 0, 'Keeper Remulos Say 8');
INSERT INTO `creature_text` VALUES (11832, 9, 9, 'Let us leave Nighthaven, hero. Seek me out at the grove.', 12, 0, 100, 0, 0, 0, 0, 11329, 0, 'Keeper Remulos Say 9');
INSERT INTO `creature_text` VALUES (11832, 10, 10, 'Come, $n. The lake is just around the bend.', 12, 0, 100, 1, 0, 0, 0, 10866, 0, 'Keeper Remulos - say Text 0');
INSERT INTO `creature_text` VALUES (11832, 11, 11, 'Stand near me, $n. I will protect you should anything go wrong.', 12, 0, 100, 1, 0, 0, 0, 10867, 0, 'Keeper Remulos - say Text 1');
INSERT INTO `creature_text` VALUES (11832, 12, 12, 'Malfurion!', 12, 0, 100, 5, 0, 0, 0, 10868, 0, 'Keeper Remulos - say Text 2');
INSERT INTO `creature_text` VALUES (11832, 13, 13, 'It was shrouded in nightmares, Malfurion. What is happening in the Dream? What could cause such atrocities?', 12, 0, 100, 6, 0, 0, 0, 10870, 0, 'Keeper Remulos - say Text 3');
INSERT INTO `creature_text` VALUES (11832, 14, 14, 'I sensed as much, Malfurion. Dark days loom ahead.', 12, 0, 100, 1, 0, 0, 0, 10872, 0, 'Keeper Remulos - say Text 4');
INSERT INTO `creature_text` VALUES (11832, 15, 15, 'You have been gone too long, Malfurion. Peace between the children of Azeroth is tenuous at best. What of my father? Of your brother? Have you any news?\n', 12, 0, 100, 6, 0, 0, 0, 10874, 0, 'Keeper Remulos - say Text 5');
INSERT INTO `creature_text` VALUES (11832, 16, 16, 'Farewell, old friend... Farewell...', 12, 0, 100, 1, 0, 0, 0, 10877, 0, 'Keeper Remulos - say Text 6');
INSERT INTO `creature_text` VALUES (11832, 17, 17, 'Let us return to the grove, mortal.', 12, 0, 100, 1, 0, 0, 0, 10879, 0, 'Keeper Remulos - say Text 7');
INSERT INTO `creature_text` VALUES (72561, 0, 0, 'I see the Dragonmaw have thrown in their lot with Garrosh.', 12, 0, 100, 1, 0, 38301, 0, 75263, 0, '');
INSERT INTO `creature_text` VALUES (71965, 13, 0, 'The light will not suffer your taint.', 14, 0, 100, 0, 0, 38881, 0, 75040, 0, '');
INSERT INTO `creature_text` VALUES (72560, 0, 0, 'Well done. The first brigade has made landfall.', 12, 0, 100, 1, 0, 38167, 0, 75245, 0, '');
INSERT INTO `creature_text` VALUES (12997, 0, 0, 'Into the box me pretties! Thas it. One by one ye go.', 12, 0, 0, 0, 0, 0, 0, 8465, 0, 'Monty');
INSERT INTO `creature_text` VALUES (14267, 0, 1, 'Me smash! You die!', 12, 0, 100, 0, 0, 0, 0, 1926, 0, 'Emogg the Crusher');
INSERT INTO `creature_text` VALUES (14267, 0, 2, 'I\'ll crush you!', 12, 0, 100, 0, 0, 0, 0, 1925, 0, 'Emogg the Crusher');
INSERT INTO `creature_text` VALUES (54485, 0, 1, 'Simple game, for simple folk. Think you can manage it?', 12, 0, 100, 70, 0, 0, 0, 53450, 0, 'Jessica Rogers');
INSERT INTO `creature_text` VALUES (54485, 0, 2, 'Toss a ring, win a prize!', 12, 0, 100, 70, 0, 0, 0, 53451, 0, 'Jessica Rogers');
INSERT INTO `creature_text` VALUES (54485, 0, 0, 'Hey, hey $c! Think you can land a ring on a slow moving turtle?', 12, 0, 100, 70, 0, 0, 0, 53448, 0, 'Jessica Rogers');
INSERT INTO `creature_text` VALUES (15214, 0, 0, 'Let the battle begin!', 41, 0, 100, 0, 0, 0, 0, 10014, 2, 'Invisible Stalker');
INSERT INTO `creature_text` VALUES (15491, 0, 0, 'Pitiful, predictable mortals... You know not what you have done! The Master\'s will fulfilled. The Moonglade shall be destroyed and Malfurion along with it!', 14, 0, 100, 0, 0, 0, 0, 11030, 0, 'Eranikus Say 0');
INSERT INTO `creature_text` VALUES (15491, 1, 1, '%s lets loose a sinister laugh.', 16, 0, 100, 0, 0, 0, 0, 11296, 0, 'Eranikus Say 1');
INSERT INTO `creature_text` VALUES (15491, 2, 2, 'You are certainly not your father, insect. Should it interest me, I would crush you with but a swipe of my claws. Turn Shan\'do Stormrage over to me and your pitiful life will be spared along with the lives of your people.', 14, 0, 100, 0, 0, 0, 0, 11294, 0, 'Eranikus Say 2');
INSERT INTO `creature_text` VALUES (15491, 3, 3, 'My redemption? You are bold, little one. My redemption comes by the will of my god.', 14, 0, 100, 0, 0, 0, 0, 11297, 0, 'Eranikus Say 3');
INSERT INTO `creature_text` VALUES (15491, 4, 4, '%s roars furiously.', 16, 0, 100, 0, 0, 0, 0, 11298, 0, 'Eranikus Say 4');
INSERT INTO `creature_text` VALUES (15491, 5, 5, 'Rise, servants of the Nightmare! Rise and destroy this world! Let there be no survivors...', 14, 0, 100, 0, 0, 0, 0, 11299, 0, 'Eranikus Say 5');
INSERT INTO `creature_text` VALUES (15491, 6, 6, 'Where is your savior? How long can you hold out against my attacks? ', 14, 0, 100, 0, 0, 0, 0, 11304, 0, 'Eranikus Say 6');
INSERT INTO `creature_text` VALUES (15491, 7, 7, 'Remulos, look how easy they fall before me? You can stop this, fool. Turn the druid over to me and it will all be over...', 14, 0, 100, 0, 0, 0, 0, 11306, 0, 'Eranikus Say 7');
INSERT INTO `creature_text` VALUES (15491, 8, 8, 'Defeated my minions? Then face me, mortals!', 14, 0, 100, 0, 0, 0, 0, 11305, 0, 'Eranikus Say 8');
