INSERT INTO `creature_text` VALUES (24536, 1, 2, 'And when the brew\'s all missin\'\nTa Shadowforge we\'ll hop,\nA bitter toast ta Ragnaros...\n... but bring him not a drop!', 12, 0, 100, 0, 0, 0, 0, 22445, 0, 'Dark Iron Herald');
