INSERT INTO `creature_text` VALUES (37058, 1, 0, 'It took a coward\'s poison and the fury of Hellscream to bring my father down.', 12, 0, 100, 0, 0, 21161, 0, 36900, 0, 'Baine Bloodhoof');
