INSERT INTO `creature_text` VALUES (37058, 3, 0, 'It is for you.', 12, 0, 100, 0, 0, 21163, 0, 36916, 0, 'Baine Bloodhoof');
INSERT INTO `creature_text` VALUES (37058, 4, 0, 'We\'re done here. Mulgore will no longer suffer the hatred of the Grimtotem.', 12, 0, 100, 0, 0, 0, 0, 42893, 0, 'Baine Bloodhoof');
INSERT INTO `creature_text` VALUES (37058, 5, 0, 'I will meet you again in Thunder Bluff.', 12, 0, 100, 0, 0, 0, 0, 42894, 0, 'Baine Bloodhoof');
INSERT INTO `creature_text` VALUES (37129, 9, 0, 'I\'ll remain and inform Tirion of our progress. You must press on and bring down Sindragosa. Lok\'tar, champions! ', 14, 0, 100, 0, 0, 16830, 0, 37018, 0, '');
INSERT INTO `creature_text` VALUES (37129, 10, 0, 'Our champions have lost much blood in the struggle to activate this Scourge transporter. We must be prepared to return the favor. Follow me, men!', 14, 0, 100, 0, 0, 0, 0, 37391, 0, '');
INSERT INTO `creature_text` VALUES (37570, 0, 0, 'I will attempt to seal the scar, $n. Make sure nothing from the other side makes its way through!', 12, 0, 100, 0, 0, 0, 0, 38492, 0, 'Naralex');
INSERT INTO `creature_text` VALUES (38002, 0, 0, 'Swiftclaw isn\'t stopping!  Steer him back to the raptor pens near Darkspear Hold.', 41, 0, 100, 0, 0, 0, 0, 37757, 0, '');
INSERT INTO `creature_text` VALUES (38015, 0, 0, 'Psssstt - $n! Over here! By the tree.', 15, 0, 100, 3, 0, 0, 0, 37771, 0, 'Karthog');
INSERT INTO `creature_text` VALUES (38015, 1, 0, 'They come! Everyone, get ready...', 12, 0, 100, 5, 0, 0, 0, 37786, 0, 'Karthog');
INSERT INTO `creature_text` VALUES (38015, 2, 0, 'Blood and thunder!', 12, 0, 100, 15, 0, 0, 0, 34164, 0, 'Karthog');
INSERT INTO `creature_text` VALUES (38015, 3, 0, 'He\'s ours! The Butcher of Taurajo is dead!', 14, 0, 100, 0, 0, 0, 0, 37793, 0, 'Karthog');
INSERT INTO `creature_text` VALUES (38015, 4, 0, 'We\'ll string his body from the trees and leave him swinging in the wind for the Alliance to discover. Go back and tell Warlord Bloodhilt the news!', 12, 0, 100, 1, 0, 0, 0, 37794, 0, 'Karthog');
