INSERT INTO `creature_text` VALUES (38225, 4, 0, 'I must be returnin\' ta Darkspear Hold. Please meet me there once Vanira is done with her healin\' of the boy.', 12, 0, 100, 1, 0, 20123, 0, 38834, 0, 'Vol\'jin');
INSERT INTO `creature_text` VALUES (38306, 0, 0, 'You are weak Vol\'jin, like your father was weak. Today I will finish what I started long ago - the Darkspear shall be wiped from existence!', 14, 0, 100, 1, 0, 21810, 0, 38294, 0, 'Zar\'jira');
INSERT INTO `creature_text` VALUES (38423, 0, 0, 'I\'ll get the fires dis time!', 14, 0, 100, 5, 0, 21388, 0, 39043, 0, 'Zuni');
INSERT INTO `creature_text` VALUES (38437, 0, 0, 'Take care of her spirits! We be handlin\' Zar\'jira.', 14, 0, 100, 5, 0, 21944, 0, 38826, 0, 'Vanira');
INSERT INTO `creature_text` VALUES (38306, 1, 0, 'Leave the fire alone, pesky trolls! Just give up and die.', 14, 0, 100, 0, 0, 21812, 0, 38819, 0, 'Zar\'jira');
INSERT INTO `creature_text` VALUES (38306, 2, 0, 'No! I will make your death slow and painful, little trolls.', 14, 0, 100, 0, 0, 21813, 0, 38820, 0, 'Zar\'jira');
INSERT INTO `creature_text` VALUES (38306, 3, 0, 'Not so fast, little troll!', 14, 0, 100, 1, 0, 21815, 0, 39045, 0, 'Zar\'jira');
INSERT INTO `creature_text` VALUES (38437, 1, 0, 'ZUNI! NOOOO!', 14, 0, 100, 5, 0, 21945, 0, 39044, 0, 'Vanira');
INSERT INTO `creature_text` VALUES (38437, 2, 0, 'I\'m afraid there\'s nothin\' I can do for our brother... her power tore away at his soul.', 12, 0, 100, 1, 0, 21946, 0, 38835, 0, 'Vanira');
INSERT INTO `creature_text` VALUES (38437, 3, 0, 'I\'ll send some watchers ta get his body so we can offer a proper farewell... I wish I could do more.', 12, 0, 100, 1, 0, 21947, 0, 38836, 0, 'Vanira');
INSERT INTO `creature_text` VALUES (38437, 4, 0, 'Vol\'jin rushed off in eagerness, but I can take us back to safety. Just give me the word when ya ready, mon.', 12, 0, 100, 275, 0, 21948, 0, 38837, 0, 'Vanira');
INSERT INTO `creature_text` VALUES (38748, 0, 0, 'Sniff for pheromones to find your way into the hive!', 41, 0, 100, 0, 0, 0, 0, 39178, 0, 'Mind-Controlled Swarmer');
INSERT INTO `creature_text` VALUES (38872, 0, 0, '$n - help! The ropes ... please...', 12, 0, 100, 0, 0, 0, 0, 38860, 0, 'Ambassador Gaines');
INSERT INTO `creature_text` VALUES (38872, 1, 0, 'Ah, thank you! Suffice it to say... Negotiations did proceed as planned.', 12, 0, 100, 1, 0, 0, 0, 38862, 0, 'Ambassador Gaines');
INSERT INTO `creature_text` VALUES (38872, 2, 0, 'I should be able to get back to camp on my own.', 12, 0, 100, 3, 0, 0, 0, 38863, 0, 'Ambassador Gaines');
INSERT INTO `creature_text` VALUES (39034, 0, 0, 'Into the Thunderdrome, $n! There\'s no getting out until someone\'s hit the sand. Let\'s start the show!', 12, 0, 100, 1, 0, 0, 0, 39167, 0, 'Dr. Dealwell');
INSERT INTO `creature_text` VALUES (39034, 1, 0, 'Any Ladies and Gentlemen present should LEAVE NOW...for only REAL MEN AND WOMEN will be able to handle the gory spectacle about to unfold! Introducing the first fighter, the warrior of the Wastewander, the Ayatollah of Kaja-Cola...THE GINORMUS!', 14, 0, 100, 15, 0, 0, 0, 39183, 0, 'Dr. Dealwell');
INSERT INTO `creature_text` VALUES (39034, 2, 0, 'The Ginormus has fallen! $n looks like $Ghe\'s:she\'s; ready for more!', 14, 0, 100, 15, 0, 0, 0, 39196, 0, 'Dr. Dealwell');
INSERT INTO `creature_text` VALUES (39034, 3, 0, 'Our next fighter was found in the deepest tombs of Zul\'Farrak! Gorged on cursed blood, raised from death countless times, he now knows only fury and violence! Dare you face the mindless wrath of...ZUMONGA!', 14, 0, 100, 15, 0, 0, 0, 39195, 0, 'Dr. Dealwell');
INSERT INTO `creature_text` VALUES (39034, 4, 0, '$n has done it again! Zumonga dies once more!', 14, 0, 100, 15, 0, 0, 0, 39232, 0, 'Dr. Dealwell');
INSERT INTO `creature_text` VALUES (39034, 5, 0, 'The faint of heart may wish to shield their eyes! Being brought into the ring now is a horror, captured from the dark tunnels of the Centipaar Hive! I give you the skittering death: SARINEXX!', 14, 0, 100, 15, 0, 0, 0, 39233, 0, 'Dr. Dealwell');
INSERT INTO `creature_text` VALUES (39034, 6, 0, 'There is no stopping $n! Sarinexx has been massacred!', 14, 0, 100, 15, 0, 0, 0, 39235, 0, 'Dr. Dealwell');
INSERT INTO `creature_text` VALUES (39034, 7, 0, 'And that\'s that, folks! This conflict\'s been settled the proper way, with VIOLENCE! Let\'s hear it for our contestants!', 14, 0, 100, 15, 0, 0, 0, 40673, 0, 'Dr. Dealwell');
INSERT INTO `creature_text` VALUES (39034, 8, 0, 'All that\'s left of the challenger is a red stain on the floor!', 14, 0, 100, 15, 0, 0, 0, 30676, 0, 'Dr. Dealwell');
INSERT INTO `creature_text` VALUES (39034, 8, 1, 'The challenger has been defeated!', 14, 0, 100, 15, 0, 0, 0, 30674, 0, 'Dr. Dealwell');
INSERT INTO `creature_text` VALUES (39062, 0, 0, 'Get in the pit and show us your stuff, $G boy:girl;.', 12, 0, 100, 0, 0, 0, 0, 37886, 0, 'Darkspear Jailor');
INSERT INTO `creature_text` VALUES (39075, 0, 0, 'There has been too much violence. Too much pain. But I have an honorable compromise. Just walk away.', 14, 0, 100, 1, 0, 0, 0, 39397, 0, 'The Ginormus');
INSERT INTO `creature_text` VALUES (39075, 1, 0, 'I am gravely disappointed. Again you have made me unleash my dogs of war. ', 14, 0, 100, 0, 0, 0, 0, 39398, 0, 'The Ginormus');
INSERT INTO `creature_text` VALUES (39148, 0, 0, 'ZUMONGA...KILL...', 14, 0, 100, 0, 0, 0, 0, 39197, 0, 'Zumonga');
INSERT INTO `creature_text` VALUES (39378, 3, 1, 'I take this life as an offering!', 14, 0, 0, 0, 0, 18914, 0, 44759, 0, 'VO_HO_Rajh_Slay02');
INSERT INTO `creature_text` VALUES (39407, 0, 0, 'Come forth, Taerar! I stand before you!', 14, 0, 100, 0, 0, 0, 0, 39956, 0, 'Ysondre');
INSERT INTO `creature_text` VALUES (39407, 1, 0, 'I\'ve returned only to end this, Taerar. This land will finally have peace.', 12, 0, 100, 0, 0, 0, 0, 39959, 0, 'Ysondre');
INSERT INTO `creature_text` VALUES (39425, 3, 1, 'A product of your own insolence.', 14, 0, 0, 0, 0, 18583, 0, 44783, 0, 'VO_HO_Anhuur_Slay02');
INSERT INTO `creature_text` VALUES (39428, 2, 1, 'The earth TREMBLES!', 14, 0, 0, 0, 0, 18908, 0, 44781, 0, 'VO_HO_Ptah_Slay02');
