INSERT INTO `creature_text` VALUES (39587, 0, 1, 'My luster... wanes.', 14, 0, 0, 0, 0, 18844, 0, 44776, 0, 'VO_HO_Isiset_Event01');
INSERT INTO `creature_text` VALUES (39587, 1, 0, 'Gaze to the heavens! What do you see?', 14, 0, 0, 0, 0, 18843, 0, 44772, 0, 'VO_HO_Isiset_Engage');
INSERT INTO `creature_text` VALUES (39587, 2, 0, 'Bask in my radiance!', 14, 0, 0, 0, 0, 18845, 0, 44777, 0, 'VO_HO_Isiset_Event02');
INSERT INTO `creature_text` VALUES (39587, 3, 0, 'The glimmer of your life, extinguished.', 14, 0, 0, 0, 0, 18846, 0, 44773, 0, 'VO_HO_Isiset_Slay01');
INSERT INTO `creature_text` VALUES (39587, 3, 1, 'Insignificant!', 14, 0, 0, 0, 0, 18847, 0, 44774, 0, 'VO_HO_Isiset_Slay02');
INSERT INTO `creature_text` VALUES (39625, 1, 1, 'Messy...', 14, 0, 100, 0, 0, 18532, 0, 47786, 0, 'VO_GB_Umbriss_Event03');
INSERT INTO `creature_text` VALUES (39644, 0, 0, 'What did they do to me? I\'ve got to get back to camp...', 12, 0, 100, 0, 0, 0, 0, 39735, 0, 'Twilight Servitor');
INSERT INTO `creature_text` VALUES (39644, 0, 1, '...What am I saying? Nothing makes sense anymore. I\'ve got to get out of this place!', 12, 0, 100, 0, 0, 0, 0, 39732, 0, 'Twilight Servitor');
INSERT INTO `creature_text` VALUES (39644, 0, 2, 'Wha - what? Where am I? I\'ve got to get out of here!', 12, 0, 100, 0, 0, 0, 0, 39730, 0, 'Twilight Servitor');
INSERT INTO `creature_text` VALUES (39644, 0, 3, 'What happened to me? I\'m leaving!', 12, 0, 100, 0, 0, 0, 0, 39731, 0, 'Twilight Servitor');
INSERT INTO `creature_text` VALUES (39644, 0, 4, 'Whoa, you made the voices go away. I\'m outta here!', 12, 0, 100, 0, 0, 0, 0, 39734, 0, 'Twilight Servitor');
INSERT INTO `creature_text` VALUES (39644, 0, 5, 'My ... brain ... hurts!', 12, 0, 100, 0, 0, 0, 0, 39733, 0, 'Twilight Servitor');
INSERT INTO `creature_text` VALUES (39696, 0, 0, 'Butcherbot will harvest meat, tra la la la la.', 12, 0, 100, 0, 0, 0, 0, 39698, 0, 'Butcherbot');
INSERT INTO `creature_text` VALUES (39696, 0, 1, 'Chop chop chop!', 12, 0, 100, 0, 0, 0, 0, 39701, 0, 'Butcherbot');
INSERT INTO `creature_text` VALUES (39696, 0, 2, 'I want the arterieeeeeeeeees!', 12, 0, 100, 0, 0, 0, 0, 39699, 0, 'Butcherbot');
INSERT INTO `creature_text` VALUES (39696, 0, 3, 'VWHHHHHRRRRRRRRRRRRRRRRHHHHHHMNNNGHHHGH', 12, 0, 100, 0, 0, 0, 0, 39702, 0, 'Butcherbot');
INSERT INTO `creature_text` VALUES (39732, 0, 0, 'Yes! Harness... your... hatred.', 14, 0, 0, 0, 0, 18552, 0, 44765, 0, 'VO_HO_Setesh_Death');
INSERT INTO `creature_text` VALUES (39732, 1, 0, 'You fear that which you cannot control. But can you control your fear?', 14, 0, 0, 0, 0, 18553, 0, 44762, 0, 'VO_HO_Setesh_Engage');
INSERT INTO `creature_text` VALUES (39732, 2, 0, 'Embrace the end!', 14, 0, 0, 0, 0, 18555, 0, 837, 0, 'VO_HO_Setesh_Slay01');
INSERT INTO `creature_text` VALUES (39732, 2, 1, 'Do you understand now?', 14, 0, 0, 0, 0, 18556, 0, 44764, 0, 'VO_HO_Setesh_Slay02');
INSERT INTO `creature_text` VALUES (39788, 2, 1, 'Purge complete.', 14, 0, 0, 0, 0, 20859, 0, 47922, 0, 'VO_HO_Anraphet_Slay02');
INSERT INTO `creature_text` VALUES (39853, 0, 0, 'I knew you\'d return, Ysondre...It was only a matter of time...', 14, 0, 100, 0, 0, 0, 0, 39957, 0, 'Taerar');
INSERT INTO `creature_text` VALUES (39853, 1, 0, 'Peace? PEACE?', 12, 0, 100, 0, 0, 0, 0, 39958, 0, 'Taerar');
INSERT INTO `creature_text` VALUES (39853, 2, 0, 'Peace is but a fleeting dream! Let the NIGHTMARE reign!', 14, 0, 100, 0, 0, 0, 0, 10886, 0, 'Taerar');
INSERT INTO `creature_text` VALUES (39853, 3, 0, 'My essence...will...persist...', 14, 0, 100, 0, 0, 0, 0, 39963, 0, 'Taerar');
INSERT INTO `creature_text` VALUES (39873, 0, 0, 'Come to my aid, Drake!', 14, 0, 100, 0, 0, 0, 0, 41044, 0, '');
INSERT INTO `creature_text` VALUES (39974, 0, 0, '%s attempts to run away in fear!', 16, 0, 100, 0, 0, 0, 0, 1150, 0, 'Twilight Inferno Lord');
INSERT INTO `creature_text` VALUES (39974, 1, 0, 'All will be ash.', 12, 0, 100, 0, 0, 0, 0, 50466, 0, 'Twilight Inferno Lord');
INSERT INTO `creature_text` VALUES (39974, 1, 1, 'Flames below, obey the call of the Twilight\'s Hammer!', 12, 0, 100, 0, 0, 0, 0, 50469, 0, 'Twilight Inferno Lord');
INSERT INTO `creature_text` VALUES (39974, 1, 2, 'I just want to set the world on fire....', 12, 0, 100, 0, 0, 0, 0, 50468, 0, 'Twilight Inferno Lord');
INSERT INTO `creature_text` VALUES (39974, 1, 3, 'The end of all things is upon us!', 12, 0, 100, 0, 0, 0, 0, 50129, 0, 'Twilight Inferno Lord');
INSERT INTO `creature_text` VALUES (39974, 1, 4, 'The flames... they lick... they bite... always biting...', 12, 0, 100, 0, 0, 0, 0, 50470, 0, 'Twilight Inferno Lord');
INSERT INTO `creature_text` VALUES (39974, 1, 5, 'This world will burn.', 12, 0, 100, 0, 0, 0, 0, 50465, 0, 'Twilight Inferno Lord');
INSERT INTO `creature_text` VALUES (39974, 1, 6, 'You only delay the inevitable.', 12, 0, 100, 0, 0, 0, 0, 50131, 0, 'Twilight Inferno Lord');
