INSERT INTO `creature_text` VALUES (54853, 7, 1, 'If you intend to play the fool, you may as well look the part.', 14, 0, 100, 0, 0, 26025, 0, 56834, 0, 'woe Azshara spell 2');
INSERT INTO `creature_text` VALUES (54853, 8, 0, 'Serve Azshara, puppets, and rejoice.', 14, 0, 100, 0, 0, 26026, 0, 55920, 0, 'woe Azshara spell 3');
INSERT INTO `creature_text` VALUES (54853, 9, 0, 'I have no time for such diversions. Keepers of Eternity, will you stand for your queen?', 14, 0, 100, 0, 0, 26027, 0, 53300, 0, 'woe Azshara spell 4');
INSERT INTO `creature_text` VALUES (54853, 10, 0, 'Still these strangers would oppose your queen\'s will. Who will stop them?', 14, 0, 100, 0, 0, 26028, 0, 53301, 0, 'woe Azshara spell 5');
INSERT INTO `creature_text` VALUES (54853, 11, 0, 'I beseech of you, my beloved subjects: Put an end to these miscreants.', 14, 0, 100, 0, 0, 26029, 0, 53299, 0, 'woe Azshara spell 6');
INSERT INTO `creature_text` VALUES (54130, 0, 0, 'Look out! Its friendses are coming!', 14, 0, 100, 2, 0, 0, 0, 56473, 0, 'Amberleaf Scamp');
INSERT INTO `creature_text` VALUES (54856, 0, 0, 'Master Li Fei\'s voice echoes, \"The way of the Tushui... enlightenment through patience and meditation... the principled life.\"', 16, 0, 100, 6, 0, 0, 0, 53129, 0, 'Master Li Fei');
INSERT INTO `creature_text` VALUES (54856, 1, 0, 'Master Li Fei\'s voice echoes, \"It is good to see you again, Aysa. You\'ve come with respect, and so I shall give you the answers you seek.\"', 16, 0, 100, 1, 0, 0, 0, 53845, 0, 'Master Li Fei');
INSERT INTO `creature_text` VALUES (54856, 2, 0, 'Master Li Fei\'s voice echoes, \"Huo, the spirit of fire, is known for his hunger. He wants for tinder to eat. He needs the caress of the wind to rouse him.\"', 16, 0, 100, 1, 0, 0, 0, 53841, 0, 'Master Li Fei');
INSERT INTO `creature_text` VALUES (54856, 3, 0, 'Master Li Fei\'s voice echoes, \"If you find these things and bring them to his cave, on the far side of Wu-Song Village, you will face a challenge within.\"', 16, 0, 100, 1, 0, 0, 0, 53842, 0, 'Master Li Fei');
INSERT INTO `creature_text` VALUES (54856, 4, 0, 'Master Li Fei\'s voice echoes, \"Overcome that challenge, and you shall be graced by Huo\'s presence. Rekindle his flame, and if your spirit is pure, he shall follow you.\"', 16, 0, 100, 1, 0, 0, 0, 53843, 0, 'Master Li Fei');
INSERT INTO `creature_text` VALUES (54856, 5, 0, 'Master Li Fei\'s voice echoes, \"Go, children. We shall meet again very soon.\"', 16, 0, 100, 2, 0, 0, 0, 53844, 0, 'Master Li Fei');
INSERT INTO `creature_text` VALUES (54856, 6, 0, 'Master Li Fei fades away.', 16, 0, 100, 0, 0, 0, 0, 53130, 0, 'Master Li Fei');
INSERT INTO `creature_text` VALUES (54867, 0, 0, 'There is an unnatural darkness to this place, a perpetual midnight. Take caution, heroes, and do not stray from the light.', 12, 0, 100, 0, 0, 25955, 0, 53122, 0, 'Nozdormu intro 01');
INSERT INTO `creature_text` VALUES (54867, 1, 0, 'The undying flames are all that remain of this sacred place. I sense much anger here...a seething rage, barely held in check. Be on your guard.', 12, 0, 100, 0, 0, 25956, 0, 53958, 0, 'Nozdormu intro 02');
INSERT INTO `creature_text` VALUES (54867, 2, 0, 'This is all that is left of the Blue Dragonshrine. A great battle shattered the timeways leading out of this forsaken place. You must reconstruct the fragments strewn across the ground and defeat the trapped spirit to proceed.', 12, 0, 100, 0, 0, 25957, 0, 53138, 0, 'Nozdormu intro 03');
INSERT INTO `creature_text` VALUES (54867, 3, 0, 'This is where she stood, heroes, and this is where she fell. The time-lost echo of Sylvanas Windrunner will reverberate through the rotting limbs of the Dragonshrine for all eternity.', 12, 0, 100, 0, 0, 25958, 0, 53192, 0, 'Nozdormu intro 04');
INSERT INTO `creature_text` VALUES (54867, 4, 0, 'You must give peace to these lands if you are to face Murozond.', 12, 0, 100, 0, 0, 25959, 0, 53214, 0, 'Nozdormu intro 05');
INSERT INTO `creature_text` VALUES (54882, 0, 0, 'I pray that the Light of a Thousand Moons will grant me this honor.', 14, 0, 100, 0, 0, 26042, 0, 53285, 0, 'woe highborne event 01');
INSERT INTO `creature_text` VALUES (54882, 1, 0, 'Yes, Light of Lights! My life is yours!', 14, 0, 100, 0, 0, 26513, 0, 53286, 0, 'woe highborne event 02');
INSERT INTO `creature_text` VALUES (54882, 2, 0, 'The Flower of Life calls upon me. I WILL NOT fail you, my Queen.', 14, 0, 100, 0, 0, 26047, 0, 53287, 0, 'woe highborne event 03');
