INSERT INTO `creature_text` VALUES (54918, 0, 0, 'Enter the Rumpus Pit, then set off the firework. Be prepared to fight several enemies at once! Anything can happen in the Rumpus.', 12, 0, 100, 22, 0, 0, 0, 53579, 0, '');
INSERT INTO `creature_text` VALUES (54918, 1, 0, 'Now entering the arena: $n!', 14, 0, 100, 22, 0, 0, 0, 53476, 0, '');
INSERT INTO `creature_text` VALUES (54918, 2, 0, 'And now... let the Rumpus start!', 14, 0, 100, 22, 0, 0, 0, 53477, 0, '');
INSERT INTO `creature_text` VALUES (54918, 3, 0, 'Halfway there, $n. Keep it up!', 14, 0, 100, 22, 0, 0, 0, 53478, 0, '');
INSERT INTO `creature_text` VALUES (54918, 4, 0, 'You\'re nearly done, $n!', 14, 0, 100, 22, 0, 0, 0, 53479, 0, '');
INSERT INTO `creature_text` VALUES (54918, 5, 0, 'Congratulations to $n, victor of the Rumpus!', 14, 0, 100, 22, 0, 0, 0, 53480, 0, '');
INSERT INTO `creature_text` VALUES (54925, 0, 0, 'Very well!', 14, 0, 100, 0, 0, 0, 0, 53212, 0, '');
INSERT INTO `creature_text` VALUES (54925, 1, 0, 'I knew I would lose. The rivers told me so.', 12, 0, 100, 0, 0, 0, 0, 53213, 0, '');
INSERT INTO `creature_text` VALUES (54926, 0, 0, 'Fine, then. It\'s time for an open-faced $R sandwich!', 14, 0, 100, 0, 0, 0, 0, 53219, 0, '');
INSERT INTO `creature_text` VALUES (54926, 1, 0, 'You win... this time. I\'d better get back to my size training.', 12, 0, 100, 0, 0, 0, 0, 53220, 0, '');
INSERT INTO `creature_text` VALUES (54927, 0, 0, 'The portal is closing! Hurry!', 14, 0, 100, 0, 0, 26037, 0, 53406, 0, 'woe guardian demon event 01');
INSERT INTO `creature_text` VALUES (54927, 1, 0, 'There! Go! GO!!!', 14, 0, 100, 0, 0, 26038, 0, 53405, 0, 'woe guardian demon event 02');
INSERT INTO `creature_text` VALUES (54927, 2, 0, 'Such fragile creatures. Worthless!', 12, 0, 100, 0, 0, 26039, 0, 53423, 0, 'woe guardian demon event 03');
INSERT INTO `creature_text` VALUES (54927, 3, 0, 'Their fate is sealed.', 12, 0, 100, 0, 0, 26040, 0, 54870, 0, 'woe guardian demon event 04');
INSERT INTO `creature_text` VALUES (54927, 4, 0, 'Leave nothing but smoldering husks.', 12, 0, 100, 0, 0, 26041, 0, 54871, 0, 'woe guardian demon event 05');
INSERT INTO `creature_text` VALUES (54938, 0, 0, 'I looked into the eyes of The Dragon, and despaired...', 14, 0, 100, 0, 0, 25865, 0, 56538, 0, 'benedictus death');
INSERT INTO `creature_text` VALUES (54938, 1, 0, 'Get inside, quickly! I\'ll hold them off!', 12, 0, 100, 0, 0, 25866, 0, 53254, 0, 'benedictus event 01');
INSERT INTO `creature_text` VALUES (54938, 2, 0, 'And now, Shaman, you will give the Dragon Soul to ME.', 12, 0, 100, 0, 0, 25867, 0, 53255, 0, 'benedictus event 02');
INSERT INTO `creature_text` VALUES (54938, 3, 0, 'The Light will consume you!', 14, 0, 100, 0, 0, 25868, 0, 56542, 0, 'benedictus spell 01');
INSERT INTO `creature_text` VALUES (54938, 4, 0, 'Drown in Shadow!', 14, 0, 100, 0, 0, 25869, 0, 56543, 0, 'benedictus spell 02');
INSERT INTO `creature_text` VALUES (54943, 0, 0, 'Shhhh! Come quietly. She\'s practicing.', 12, 0, 100, 396, 0, 0, 0, 59712, 0, 'Raufen - Merchant Lorvo - Talk on approach');
INSERT INTO `creature_text` VALUES (54960, 0, 0, 'Stranger, have you ever seen a stone fall into a calm lake? Every action creates a ripple, even accepting a pledge of friendship. I shall think upon your request.', 12, 0, 100, 0, 0, 0, 0, 58066, 0, 'Elder Lusshan to Player');
INSERT INTO `creature_text` VALUES (54968, 0, 0, 'Let\'s get to work, shall we?', 14, 0, 100, 0, 0, 25816, 0, 53268, 0, 'asira aggro');
INSERT INTO `creature_text` VALUES (54968, 1, 0, 'You\'re much... better... than I thought...', 14, 0, 100, 0, 0, 25817, 0, 54821, 0, 'asira death');
INSERT INTO `creature_text` VALUES (54968, 2, 0, 'Where do you think you\'re going, little lizard?', 14, 0, 100, 0, 0, 25818, 0, 53825, 0, 'asira event 01');
INSERT INTO `creature_text` VALUES (54968, 3, 0, '...and with that out of the way, you and your flock of fumbling friends are next on my list. Mmm, I thought you\'d never get here!', 14, 0, 100, 0, 0, 25819, 0, 53751, 0, 'asira event 02');
INSERT INTO `creature_text` VALUES (54968, 4, 0, 'Ah. Well. That was even easier than I thought!', 14, 0, 100, 0, 0, 25820, 0, 54820, 0, 'asira event 03');
INSERT INTO `creature_text` VALUES (54968, 5, 0, 'Tch!  So soon?', 14, 0, 100, 0, 0, 25821, 0, 54822, 0, 'asira slay 01');
INSERT INTO `creature_text` VALUES (54968, 5, 1, 'I hope your friends can do better.', 14, 0, 100, 0, 0, 25822, 0, 54823, 0, 'asira slay 02');
INSERT INTO `creature_text` VALUES (54968, 5, 2, 'Mmm, too much fun!', 14, 0, 100, 0, 0, 25823, 0, 54824, 0, 'asira slay 03');
INSERT INTO `creature_text` VALUES (54968, 5, 3, 'Good night...', 14, 0, 100, 0, 0, 25824, 0, 54825, 0, 'asira slay 04');
INSERT INTO `creature_text` VALUES (54968, 6, 0, 'Bye-bye.', 14, 0, 100, 0, 0, 25825, 0, 54826, 0, 'asira spell 01');
INSERT INTO `creature_text` VALUES (54968, 6, 1, 'Now you see me...', 14, 0, 100, 0, 0, 25826, 0, 54828, 0, 'asira spell 02');
INSERT INTO `creature_text` VALUES (54968, 6, 2, 'Surprise.', 14, 0, 100, 0, 0, 25827, 0, 54827, 0, 'asira spell 03');
INSERT INTO `creature_text` VALUES (54969, 0, 0, 'Come Stormrage, and I will show you what happens to those that betray the lord of the Legion!', 14, 0, 100, 0, 0, 26478, 0, 54223, 0, 'woe mannoroth aggro');
INSERT INTO `creature_text` VALUES (54969, 1, 0, 'No...no! This victory will not be ripped from my grasp! I will not return to him in failure! I will not be torn from this pitiful world! No...NOOOOOOOO!!!', 14, 0, 100, 0, 0, 26479, 0, 54271, 0, 'woe mannoroth death');
INSERT INTO `creature_text` VALUES (54969, 2, 0, 'Varo\'then, see that I am not disrupted by this rabble!', 14, 0, 100, 0, 0, 26480, 0, 54118, 0, 'woe mannoroth event 01 varothen');
INSERT INTO `creature_text` VALUES (54969, 3, 0, 'Lord Sargeras, I will not fail you! Sweep your molten fist through this world, so that it may be reborn in flames and darkness!', 14, 0, 100, 0, 0, 26481, 0, 54256, 0, 'woe mannoroth event 02 sargeras');
INSERT INTO `creature_text` VALUES (54969, 4, 0, 'Rrraaaghhh!!', 14, 0, 100, 0, 0, 26482, 0, 54555, 0, 'woe mannoroth event 03');
INSERT INTO `creature_text` VALUES (54969, 5, 0, 'Your blood is MINE, elf!', 14, 0, 100, 0, 0, 26483, 0, 54595, 0, 'woe mannoroth event 04');
INSERT INTO `creature_text` VALUES (54969, 6, 0, 'Yes...yes! I can feel his burning eyes upon me, he is close...so close! And then your world will be unmade, your lives as nothing!', 14, 0, 100, 0, 0, 26484, 0, 54259, 0, 'woe mannoroth event 05');
INSERT INTO `creature_text` VALUES (54969, 7, 0, 'Fall and DIE before me!', 14, 0, 100, 0, 0, 26485, 0, 56641, 0, 'woe mannoroth kill 01');
INSERT INTO `creature_text` VALUES (54969, 7, 1, 'SQUIRM! SCREAM!', 14, 0, 100, 0, 0, 26486, 0, 56642, 0, 'woe mannoroth kill 02');
INSERT INTO `creature_text` VALUES (54969, 7, 2, 'Useless!', 14, 0, 100, 0, 0, 26487, 0, 56643, 0, 'woe mannoroth kill 03');
INSERT INTO `creature_text` VALUES (54969, 8, 0, '[Demonic] Amanare maev il azgalada zila ashj ashj zila enkil!', 14, 0, 100, 0, 0, 26488, 0, 56644, 0, 'woe mannoroth spell 01');
INSERT INTO `creature_text` VALUES (54971, 0, 0, 'I will NOT, Archbishop. It will never be yours.', 12, 0, 100, 0, 0, 25893, 0, 53275, 0, 'THRALL_BENEDICTUS_1');
INSERT INTO `creature_text` VALUES (54971, 1, 0, 'You were a figurehead of the Light, Benedictus. How could you betray your own people?', 12, 0, 100, 0, 0, 25894, 0, 53279, 0, 'THRALL_BENEDICTUS_2');
INSERT INTO `creature_text` VALUES (54972, 0, 0, 'Beware. Enemies approach!', 14, 0, 100, 0, 0, 25883, 0, 53553, 0, 'THRALL_ASIRA_1');
INSERT INTO `creature_text` VALUES (54972, 1, 0, 'Let none stand in our way.', 14, 0, 100, 0, 0, 25884, 0, 53821, 0, 'THRALL_ASIRA_2');
INSERT INTO `creature_text` VALUES (54972, 2, 0, 'The Twilight\'s Hammer returns.', 14, 0, 100, 0, 0, 25885, 0, 53822, 0, 'THRALL_ASIRA_3');
INSERT INTO `creature_text` VALUES (54972, 3, 0, 'Let them come!', 14, 0, 100, 0, 0, 25886, 0, 53823, 0, 'THRALL_ASIRA_4');
INSERT INTO `creature_text` VALUES (54972, 4, 0, 'Alexstrasza\'s drakes should meet us here...', 12, 0, 100, 0, 0, 25887, 0, 53857, 0, 'THRALL_ASIRA_5');
INSERT INTO `creature_text` VALUES (54972, 5, 0, 'Up there, above us!', 12, 0, 100, 0, 0, 25888, 0, 53875, 0, 'THRALL_ASIRA_6');
INSERT INTO `creature_text` VALUES (54972, 6, 0, 'I haven\'t come to be stopped by the likes of you.', 14, 0, 100, 0, 0, 25890, 0, 53968, 0, 'THRALL_ASIRA_7');
INSERT INTO `creature_text` VALUES (54972, 7, 0, 'Well done, Let us see to our friend...', 12, 0, 100, 0, 0, 25891, 0, 53986, 0, 'THRALL_ASIRA_8');
INSERT INTO `creature_text` VALUES (54972, 8, 0, 'The rest of the drakes should be here shortly. I\'ll fly on ahead; catch up when you can.', 12, 0, 100, 0, 0, 25892, 0, 53987, 0, 'THRALL_ASIRA_9');
INSERT INTO `creature_text` VALUES (55009, 0, 0, 'Over here, $n!', 12, 0, 100, 0, 0, 0, 0, 40305, 0, '');
INSERT INTO `creature_text` VALUES (55084, 0, 0, 'Mogu ambushers descend from the trees. Look out!', 42, 0, 100, 0, 0, 37666, 0, 53394, 0, '');
INSERT INTO `creature_text` VALUES (55084, 1, 0, 'Look at what we have here! Another straggler from the orchard...', 12, 0, 100, 0, 0, 0, 0, 53395, 0, '');
INSERT INTO `creature_text` VALUES (55085, 0, 0, 'No mortal may stand before me and live!', 12, 0, 100, 0, 0, 26112, 0, 54778, 0, 'woe perotharn aggro');
INSERT INTO `creature_text` VALUES (55085, 0, 1, 'None will reach the palace without besting Peroth\'arn, first of fel-touched!', 12, 0, 100, 0, 0, 26128, 0, 54834, 0, 'woe perotharn intro 2');
INSERT INTO `creature_text` VALUES (55085, 1, 0, 'Nooooo... how can this be?', 12, 0, 100, 0, 0, 26113, 0, 56991, 0, 'woe perotharn');
INSERT INTO `creature_text` VALUES (55085, 2, 0, 'ENOUGH! It is time to end this game!', 12, 0, 100, 0, 0, 26117, 0, 54812, 0, 'woe perotharn event 4');
INSERT INTO `creature_text` VALUES (55085, 3, 0, 'He is near, lurking in the shadows... I can sense it.', 12, 0, 100, 0, 0, 26118, 0, 54683, 0, 'woe perotharn event 5');
INSERT INTO `creature_text` VALUES (55085, 4, 0, 'You, Felguard.  Hold this area.', 12, 0, 100, 0, 0, 26120, 0, 54685, 0, 'woe perotharn event 7');
INSERT INTO `creature_text` VALUES (55085, 5, 0, 'The rest of you, secure the courtyard.', 12, 0, 100, 0, 0, 26121, 0, 54687, 0, 'woe perotharn event 8');
INSERT INTO `creature_text` VALUES (55085, 6, 0, 'I can see you.', 12, 0, 100, 0, 0, 26122, 0, 54790, 0, 'woe perotharn event 9');
INSERT INTO `creature_text` VALUES (55085, 7, 0, 'You hide well, worms.  But how long you can delay your doom?', 12, 0, 100, 0, 0, 26123, 0, 54766, 0, 'woe perotharn event 10');
INSERT INTO `creature_text` VALUES (55085, 8, 0, 'I will find you!', 12, 0, 100, 0, 0, 26124, 0, 54817, 0, 'woe perotharn event 11');
INSERT INTO `creature_text` VALUES (55085, 8, 1, 'The shadows will not save you.', 12, 0, 100, 0, 0, 26125, 0, 54818, 0, 'woe perotharn event 12');
INSERT INTO `creature_text` VALUES (55085, 8, 2, 'Cower in hiding, heh.', 12, 0, 100, 0, 0, 26126, 0, 54819, 0, 'woe perotharn event 13');
INSERT INTO `creature_text` VALUES (55085, 9, 0, 'Who shut down the portals? Clever little worms.', 12, 0, 100, 0, 0, 26127, 0, 54833, 0, 'woe perotharn intro 1');
INSERT INTO `creature_text` VALUES (55085, 10, 0, 'Your essence... is MINE.', 12, 0, 100, 0, 0, 26132, 0, 54546, 0, 'woe perotharn spell 1');
INSERT INTO `creature_text` VALUES (55085, 11, 0, 'The shadows serve ME, now...', 12, 0, 100, 0, 0, 26133, 0, 54816, 0, 'woe perotharn spell 2');
INSERT INTO `creature_text` VALUES (55085, 12, 0, '%s vanishes into the shadows!', 41, 0, 100, 0, 0, 0, 0, 55983, 0, 'woe perotharn announce hide');
INSERT INTO `creature_text` VALUES (55085, 13, 0, 'The Eyes of Peroth\'arn are looking for you.', 41, 0, 100, 0, 0, 0, 0, 55984, 0, 'woe perotharn announce hunting');
INSERT INTO `creature_text` VALUES (55085, 14, 0, '%s ambushes his helpless prey!', 41, 0, 100, 0, 0, 0, 0, 56047, 0, 'woe perotharn announce easy prey');
INSERT INTO `creature_text` VALUES (55107, 0, 0, 'Death to all who oppose us!', 14, 0, 100, 0, 0, 0, 0, 53757, 0, 'TWILIGHT_RANGER');
INSERT INTO `creature_text` VALUES (55265, 0, 0, 'You seek to halt an avalanche. I will bury you.', 14, 0, 100, 0, 0, 26268, 0, 56456, 0, 'morchok aggro');
INSERT INTO `creature_text` VALUES (55265, 1, 0, 'Impossible. This cannot be. The tower... must... fall...', 14, 0, 100, 0, 0, 26269, 0, 56458, 0, 'morchok death');
INSERT INTO `creature_text` VALUES (55265, 2, 0, 'Cowards. Weaklings. Come down and fight, or I will bring you down.', 14, 0, 100, 0, 0, 26270, 0, 56475, 0, 'morchok event 1');
INSERT INTO `creature_text` VALUES (55265, 3, 0, 'You cannot hide in this temple forever, Shaman.', 14, 0, 100, 0, 0, 26271, 0, 56476, 0, 'morchok event 2');
INSERT INTO `creature_text` VALUES (55265, 4, 0, 'I will turn this tower to rubble, and scatter it across the wastes.', 14, 0, 100, 0, 0, 26272, 0, 56477, 0, 'morchok event 3');
INSERT INTO `creature_text` VALUES (55265, 5, 0, 'Wyrmrest will fall. All will be dust.', 14, 0, 100, 0, 0, 26273, 0, 56478, 0, 'morchok event 4');
INSERT INTO `creature_text` VALUES (55265, 6, 0, 'The stone calls...', 14, 0, 100, 0, 0, 26274, 0, 56462, 0, 'morchok ground 1 1');
INSERT INTO `creature_text` VALUES (55265, 6, 1, 'The ground shakes...', 14, 0, 100, 0, 0, 26275, 0, 56463, 0, 'morchok ground 1 2');
INSERT INTO `creature_text` VALUES (55265, 6, 2, 'The rocks tremble...', 14, 0, 100, 0, 0, 26276, 0, 56465, 0, 'morchok ground 1 3');
INSERT INTO `creature_text` VALUES (55265, 6, 3, 'The surface quakes...', 14, 0, 100, 0, 0, 26277, 0, 56466, 0, 'morchok ground 1 4');
INSERT INTO `creature_text` VALUES (55265, 7, 0, '...and the black blood of the earth consumes you.', 14, 0, 100, 0, 0, 26278, 0, 56467, 0, 'morchok ground 2 1');
INSERT INTO `creature_text` VALUES (55265, 7, 1, '...and there is no escape from the Old Gods.', 14, 0, 100, 0, 0, 26279, 0, 56468, 0, 'morchok ground 2 2');
INSERT INTO `creature_text` VALUES (55265, 7, 2, '...and the rage of the true gods follows.', 14, 0, 100, 0, 0, 26280, 0, 56469, 0, 'morchok ground 2 3');
INSERT INTO `creature_text` VALUES (55265, 7, 3, '...and you drown in the hate of The Master.', 14, 0, 100, 0, 0, 26281, 0, 56470, 0, 'morchok ground 2 4');
INSERT INTO `creature_text` VALUES (55265, 8, 0, 'No mortal shall turn me from my task.', 14, 0, 100, 0, 0, 26282, 0, 56455, 0, 'morchok intro');
INSERT INTO `creature_text` VALUES (55265, 9, 0, 'Flee, and die.', 14, 0, 100, 0, 0, 26283, 0, 56471, 0, 'morchok orb 1');
INSERT INTO `creature_text` VALUES (55265, 9, 1, 'Run, and perish.', 14, 0, 100, 0, 0, 26284, 0, 56472, 0, 'morchok orb 2');
INSERT INTO `creature_text` VALUES (55265, 10, 0, 'I am unstoppable.', 14, 0, 100, 0, 0, 26285, 0, 56459, 0, 'morchok slay 1');
INSERT INTO `creature_text` VALUES (55265, 10, 1, 'It was inevitable.', 14, 0, 100, 0, 0, 26286, 0, 56460, 0, 'morchok slay 2');
INSERT INTO `creature_text` VALUES (55265, 10, 2, 'Ground to dust.', 14, 0, 100, 0, 0, 26287, 0, 56461, 0, 'morchok slay 3');
INSERT INTO `creature_text` VALUES (55265, 11, 0, 'You thought to fight me alone? The earth splits to swallow and crush you.', 14, 0, 100, 0, 0, 26288, 0, 56457, 0, 'morchok split');
INSERT INTO `creature_text` VALUES (55265, 12, 0, '%s summons a Resonating Crystal!', 41, 0, 100, 0, 0, 0, 0, 54353, 0, 'Morchok - summon crystall');
INSERT INTO `creature_text` VALUES (55294, 0, 0, 'Now is the hour of twilight!', 14, 0, 100, 0, 0, 26314, 0, 55329, 0, 'Ultraxion aggro');
INSERT INTO `creature_text` VALUES (55294, 1, 0, 'I WILL DRAG YOU WITH ME INTO FLAME AND DARKNESS!', 14, 0, 100, 0, 0, 26315, 0, 55337, 0, 'Ultraxion berserk');
INSERT INTO `creature_text` VALUES (55294, 2, 0, 'But...but...I am...Ul...trax...ionnnnnn...', 14, 0, 100, 0, 0, 26316, 0, 55330, 0, 'Ultraxion death');
INSERT INTO `creature_text` VALUES (55294, 3, 0, 'I am the beginning of the end...the shadow which blots out the sun...the bell which tolls your doom...', 14, 0, 100, 0, 0, 26317, 0, 55320, 0, 'Ultraxion intro 01');
INSERT INTO `creature_text` VALUES (55294, 4, 0, 'For this moment ALONE was I made. Look upon your death, mortals, and despair!', 14, 0, 100, 0, 0, 26318, 0, 55323, 0, 'Ultraxion intro 02');
INSERT INTO `creature_text` VALUES (55294, 5, 0, 'Fall before Ultraxion!', 14, 0, 100, 0, 0, 26319, 0, 55331, 0, 'Ultraxion slay 01');
INSERT INTO `creature_text` VALUES (55294, 5, 1, 'You have no hope!', 14, 0, 100, 0, 0, 26320, 0, 55332, 0, 'Ultraxion slay 02');
INSERT INTO `creature_text` VALUES (55294, 5, 2, 'Hahahahaha!', 14, 0, 100, 0, 0, 26321, 0, 55333, 0, 'Ultraxion slay 03');
INSERT INTO `creature_text` VALUES (55294, 6, 0, 'The final shred of light fades, and with it, your pitiful mortal existence!', 14, 0, 100, 0, 0, 26323, 0, 55334, 0, 'Ultraxion spell 01');
INSERT INTO `creature_text` VALUES (55294, 7, 0, 'Through the pain and fire my hatred burns!', 14, 0, 100, 0, 0, 26325, 0, 55336, 0, 'Ultraxion spell 03');
INSERT INTO `creature_text` VALUES (55294, 8, 0, 'A monstrous force pulls you into the twilight realm!', 41, 0, 100, 0, 0, 0, 0, 55410, 0, 'Ultraxion announce');
INSERT INTO `creature_text` VALUES (55294, 9, 0, '|TInterface\\Icons\\spell_shadow_twilight.blp:20|t Ultraxion begins casting |cFF9900CC|Hspell:106371|h[Hour of Twilight]|h|r!', 41, 0, 100, 0, 0, 0, 0, 56545, 0, 'Ultraxion - announce hour of twilight');
