INSERT INTO `creature_text` VALUES (55308, 0, 0, 'Once more shall the twisted flesh-banners of N\'Zoth chitter and howl above the fly-blown corpse of this world. After millennia, we have returned.', 14, 0, 100, 0, 0, 26335, 0, 56755, 0, 'zonozz aggro');
