INSERT INTO `creature_text` VALUES (55312, 3, 0, 'Sk\'yahf qi\'plahf PH\'MAGG!', 14, 0, 100, 0, 0, 26329, 0, 56809, 0, 'yor\'sahj slay 1');
INSERT INTO `creature_text` VALUES (55312, 3, 1, 'H\'iwn zaix Shuul\'wah, PHQUATHI!', 14, 0, 100, 0, 0, 26330, 0, 56811, 0, 'yor\'sahj slay 2');
INSERT INTO `creature_text` VALUES (55312, 3, 2, 'Shkul an\'zig qvsakf KSSH\'GA, ag\'THYZAK agthu!', 14, 0, 100, 0, 0, 26331, 0, 56814, 0, 'yor\'sahj slay 3');
INSERT INTO `creature_text` VALUES (55312, 4, 0, 'KYTH ag\'xig yyg\'far IIQAATH ONGG!', 14, 0, 100, 0, 0, 26332, 0, 56816, 0, 'yor\'sahj spell 1');
INSERT INTO `creature_text` VALUES (55312, 4, 1, 'UULL lwhuk H\'IWN!', 14, 0, 100, 0, 0, 26333, 0, 56818, 0, 'yor\'sahj spell 2');
INSERT INTO `creature_text` VALUES (55312, 5, 0, 'You will drown in the blood of the Old Gods! ALL OF YOU!', 42, 0, 100, 0, 0, 0, 0, 56805, 0, 'yor\'sahj aggro whisper');
INSERT INTO `creature_text` VALUES (55312, 6, 0, 'O, Deathwing! Your faithful servant has failed you!', 42, 0, 100, 0, 0, 0, 0, 56808, 0, 'yor\'sahj death whisper');
INSERT INTO `creature_text` VALUES (55312, 7, 0, 'Our numbers are endless, our power beyond reckoning! All who oppose the Destroyer will DIE A THOUSAND DEATHS!', 42, 0, 100, 0, 0, 0, 0, 56803, 0, 'yor\'sahj intro whisper');
INSERT INTO `creature_text` VALUES (55312, 8, 0, 'Your soul will know ENDLESS TORMENT!', 42, 0, 100, 0, 0, 0, 0, 56810, 0, 'yor\'sahj slay 1 whisper');
INSERT INTO `creature_text` VALUES (55312, 8, 1, 'All praise Deathwing, THE DESTROYER!', 42, 0, 100, 0, 0, 0, 0, 56812, 0, 'yor\'sahj slay 2 whisper');
INSERT INTO `creature_text` VALUES (55312, 8, 2, 'From its BLEAKEST DEPTHS, we RECLAIM this world!', 42, 0, 100, 0, 0, 0, 0, 56815, 0, 'yor\'sahj slay 3 whisper');
INSERT INTO `creature_text` VALUES (55312, 9, 0, 'SEE how we pour from the CURSED EARTH!', 42, 0, 100, 0, 0, 0, 0, 56817, 0, 'yor\'sahj spell 1 whisper');
INSERT INTO `creature_text` VALUES (55312, 9, 1, 'The DARKNESS devours ALL!', 42, 0, 100, 0, 0, 0, 0, 56819, 0, 'yor\'sahj spell 2 whisper');
