INSERT INTO `creature_text` VALUES (55419, 0, 0, 'For you, Azshara.', 12, 0, 100, 0, 0, 26134, 0, 54225, 0, 'woe varothen aggro');
INSERT INTO `creature_text` VALUES (55419, 1, 0, 'Light of lights...I have failed you. I am sorry...my Azshara...', 12, 0, 100, 0, 0, 26135, 0, 54253, 0, 'woe varothen death');
INSERT INTO `creature_text` VALUES (55419, 2, 0, 'None may cross the Queen!', 14, 0, 100, 0, 0, 26138, 0, 56634, 0, 'woe varothen kill 01');
INSERT INTO `creature_text` VALUES (55419, 2, 1, 'A deserved death!', 14, 0, 100, 0, 0, 26139, 0, 56635, 0, 'woe varothen kill 02');
INSERT INTO `creature_text` VALUES (55419, 2, 2, 'In Azshara\'s name!', 14, 0, 100, 0, 0, 26140, 0, 56636, 0, 'woe varothen kill 03');
INSERT INTO `creature_text` VALUES (55500, 0, 0, 'Return to the shadows!', 12, 0, 100, 0, 0, 26048, 0, 54831, 0, 'woe illidan boss cloak 1');
INSERT INTO `creature_text` VALUES (55500, 1, 0, 'Nothing will stop me. Not even you, demon.', 12, 0, 100, 0, 0, 26049, 0, 54829, 0, 'woe illidan boss enter');
INSERT INTO `creature_text` VALUES (55500, 2, 0, 'Another demon, ready to be slaughtered.', 12, 0, 100, 0, 0, 26050, 0, 54574, 0, 'woe illidan boss precombat 1');
INSERT INTO `creature_text` VALUES (55500, 3, 0, 'The hunter became the prey.', 12, 0, 100, 0, 0, 26051, 0, 54838, 0, 'woe illidan boss victory 01');
INSERT INTO `creature_text` VALUES (55500, 4, 0, 'You did well, but for now I must continue alone.  Good hunting.', 12, 0, 100, 0, 0, 26052, 0, 54839, 0, 'woe illidan boss victory 02');
INSERT INTO `creature_text` VALUES (55500, 5, 0, 'Your magic is pathetic. Let me show you mine.', 12, 0, 100, 0, 0, 26053, 0, 54775, 0, 'woe illidan break 01');
INSERT INTO `creature_text` VALUES (55500, 6, 0, 'We now hide in shadows, hidden from our enemies.', 12, 0, 100, 0, 0, 26054, 0, 54705, 0, 'woe illidan cloak 1');
INSERT INTO `creature_text` VALUES (55500, 7, 0, 'We\'re leaving. Stay close.', 12, 0, 100, 0, 0, 26055, 0, 54511, 0, 'woe illidan combat 1');
INSERT INTO `creature_text` VALUES (55500, 8, 0, 'My blades hunger.', 12, 0, 100, 0, 0, 26057, 0, 54503, 0, 'woe illidan combat 2');
INSERT INTO `creature_text` VALUES (55500, 9, 0, 'This won\'t hurt a bit.', 12, 0, 100, 0, 0, 26058, 0, 54529, 0, 'woe illidan combat 3');
INSERT INTO `creature_text` VALUES (55500, 10, 0, 'Death to the Legion!', 12, 0, 100, 0, 0, 26056, 0, 54444, 0, 'woe illidan combat 4');
INSERT INTO `creature_text` VALUES (55500, 11, 0, 'I will hold them back so we can get past. Be ready.', 12, 0, 100, 0, 0, 26063, 0, 54297, 0, 'woe illidan distract');
INSERT INTO `creature_text` VALUES (55500, 12, 0, 'My magic is fading. I\'m going through!', 12, 0, 100, 0, 0, 26064, 0, 54299, 0, 'woe illidan distract end');
INSERT INTO `creature_text` VALUES (55500, 13, 0, 'Come with me if you\'d like to live long enough to see me save this world!', 12, 0, 100, 0, 0, 26065, 0, 54284, 0, 'woe illidan escort 1');
INSERT INTO `creature_text` VALUES (55500, 14, 0, 'So many demons, not enough time.', 12, 0, 100, 0, 0, 26066, 0, 54288, 0, 'woe illidan escort 2');
INSERT INTO `creature_text` VALUES (55500, 15, 0, 'They will get what they deserve, in due time.', 12, 0, 100, 0, 0, 26067, 0, 54292, 0, 'woe illidan escort 3');
INSERT INTO `creature_text` VALUES (55500, 16, 0, 'I\'ve seen a single Guardian Demon slaughter a hundred elves.  Tread lightly.', 12, 0, 100, 0, 0, 26068, 0, 54296, 0, 'woe illidan escort 4');
INSERT INTO `creature_text` VALUES (55500, 17, 0, 'They come endlessly from the palace.', 12, 0, 100, 0, 0, 26069, 0, 54510, 0, 'woe illidan escort 5');
INSERT INTO `creature_text` VALUES (55500, 18, 0, 'The stench of sulfur and brimstone... These portals are as foul as the demons themselves.', 12, 0, 100, 0, 0, 26070, 0, 54512, 0, 'woe illidan escort 6');
INSERT INTO `creature_text` VALUES (55500, 19, 0, 'Cut this one down from the shadows.', 12, 0, 100, 0, 0, 26071, 0, 54514, 0, 'woe illidan escort 7');
INSERT INTO `creature_text` VALUES (55500, 20, 0, 'Let us shut down this final portal and finish this.', 12, 0, 100, 0, 0, 26072, 0, 54736, 0, 'woe illidan escort 8');
INSERT INTO `creature_text` VALUES (55500, 21, 0, 'The demons should all be leaving. We will be at the palace in no time.', 12, 0, 100, 0, 0, 26073, 0, 54750, 0, 'woe illidan escort 9');
INSERT INTO `creature_text` VALUES (55500, 22, 0, 'The demons are no longer pouring from the palace. We can move ahead.', 12, 0, 100, 0, 0, 26074, 0, 54572, 0, 'woe illidan escort 10');
INSERT INTO `creature_text` VALUES (55500, 23, 0, 'Too easy.', 12, 0, 100, 0, 0, 26075, 0, 6188, 0, 'woe illidan escort 11');
INSERT INTO `creature_text` VALUES (55500, 24, 0, 'Over here, in the shadows.', 12, 0, 100, 0, 0, 26076, 0, 54274, 0, 'woe illidan greeting 1');
INSERT INTO `creature_text` VALUES (55500, 25, 0, 'Explain your presence.', 12, 0, 100, 0, 0, 26080, 0, 54835, 0, 'woe illidan greeting idle 1');
INSERT INTO `creature_text` VALUES (55500, 26, 0, 'Attack. I don\'t like to be kept waiting.', 12, 0, 100, 0, 0, 26081, 0, 54304, 0, 'woe illidan idle 1');
INSERT INTO `creature_text` VALUES (55500, 27, 0, 'I\'ll let you have the first kill.  Don\'t make me regret that.', 12, 0, 100, 0, 0, 26082, 0, 54308, 0, 'woe illidan idle 2');
INSERT INTO `creature_text` VALUES (55500, 28, 0, 'Patience is not one of my virtues.', 12, 0, 100, 0, 0, 26083, 0, 54309, 0, 'woe illidan idle 3');
INSERT INTO `creature_text` VALUES (55500, 29, 0, 'Waiting to attack...', 12, 0, 100, 0, 0, 26100, 0, 54302, 0, 'woe illidan precombat 1');
INSERT INTO `creature_text` VALUES (55500, 30, 0, 'They stand unaware that their deaths are moments away.', 12, 0, 100, 0, 0, 26101, 0, 54575, 0, 'woe illidan precombat 1 1');
INSERT INTO `creature_text` VALUES (55500, 31, 0, 'My strength returns.', 12, 0, 100, 0, 0, 26102, 0, 54832, 0, 'woe illidan recovery 1');
INSERT INTO `creature_text` VALUES (55500, 32, 0, 'Destroy the crystal so we can move on.', 12, 0, 100, 0, 0, 26103, 0, 54468, 0, 'woe illidan waiting 1');
INSERT INTO `creature_text` VALUES (55500, 33, 0, 'Smash the crystal. We need to move.', 12, 0, 100, 0, 0, 26104, 0, 54709, 0, 'woe illidan waiting 2');
INSERT INTO `creature_text` VALUES (55500, 34, 0, 'Destroy the portal energy focus!', 12, 0, 100, 0, 0, 26105, 0, 54710, 0, 'woe illidan waiting 3');
INSERT INTO `creature_text` VALUES (55500, 35, 0, 'I think we stand a better chance fighting alongside one another.', 12, 0, 100, 0, 0, 26525, 0, 54277, 0, 'woe illidan greeting 2');
INSERT INTO `creature_text` VALUES (55500, 36, 0, 'Talk with me now.', 12, 0, 100, 0, 0, 26523, 0, 54836, 0, 'woe illidan greeting idle 2');
INSERT INTO `creature_text` VALUES (55500, 37, 0, 'Are you here to help?', 12, 0, 100, 0, 0, 26524, 0, 54837, 0, 'woe illidan greeting idle 3');
INSERT INTO `creature_text` VALUES (55500, 38, 0, 'Shut down the portal and we will continue.', 12, 0, 100, 0, 0, 26106, 0, 54716, 0, 'woe illidan waiting 4');
INSERT INTO `creature_text` VALUES (55500, 39, 0, 'Curious weapons you wield... such power.', 15, 0, 100, 0, 0, 0, 0, 56857, 0, 'woe illidan azzinoth 1');
INSERT INTO `creature_text` VALUES (55500, 40, 0, 'You seem prepared.', 15, 0, 100, 0, 0, 0, 0, 56869, 0, 'woe illidan azzinoth 2');
INSERT INTO `creature_text` VALUES (55524, 0, 0, 'Malfurion...', 12, 0, 100, 0, 0, 25989, 0, 56620, 0, 'woe tyrande conclusion 1');
INSERT INTO `creature_text` VALUES (55524, 1, 0, 'By the very edge...', 12, 0, 100, 0, 0, 25990, 0, 56621, 0, 'woe tyrande conclusion 2');
INSERT INTO `creature_text` VALUES (55524, 2, 0, 'I do not know who you are, but I thank you. Without your aid, our world would be...I do not wish to think about it. Moon goddess light your path.', 12, 0, 100, 0, 0, 25991, 0, 56622, 0, 'woe tyrande conclusion 3');
INSERT INTO `creature_text` VALUES (55524, 3, 0, 'He knows what we attempt. We have not much time, the forest crawls with his demons.', 12, 0, 100, 0, 0, 25993, 0, 54108, 0, 'woe tyrande mannoroth 1');
INSERT INTO `creature_text` VALUES (55524, 4, 0, 'Mother moon, guide us through this darkness.', 12, 0, 100, 0, 0, 25994, 0, 54110, 0, 'woe tyrande mannoroth 2');
INSERT INTO `creature_text` VALUES (55524, 5, 0, 'Illidan, what is in that vial? What are you doing?', 12, 0, 100, 0, 0, 25995, 0, 54114, 0, 'woe tyrande mannoroth 3');
INSERT INTO `creature_text` VALUES (55524, 6, 0, 'I cannot strike them!  What is this demon magic?', 12, 0, 100, 0, 0, 25996, 0, 54120, 0, 'woe tyrande mannoroth 4');
INSERT INTO `creature_text` VALUES (55524, 7, 0, 'I will handle the demons. Elune, guide my arrows!', 12, 0, 100, 0, 0, 25997, 0, 54227, 0, 'woe tyrande mannoroth 5');
INSERT INTO `creature_text` VALUES (55524, 8, 0, 'Light of Elune, save me!', 14, 0, 100, 0, 0, 25998, 0, 54229, 0, 'woe tyrande mannoroth 6');
INSERT INTO `creature_text` VALUES (55524, 9, 0, 'I will hold them back for now!', 14, 0, 100, 0, 0, 25999, 0, 54231, 0, 'woe tyrande mannoroth 7');
INSERT INTO `creature_text` VALUES (55524, 10, 0, 'Illidan, I am out of arrows! Moon goddess, protect us from the darkness, that we may see your light again another night!', 12, 0, 100, 0, 0, 26000, 0, 54258, 0, 'woe tyrande mannoroth 8');
INSERT INTO `creature_text` VALUES (55524, 11, 0, 'Illidan...you musn\'t!', 12, 0, 100, 0, 0, 26001, 0, 54262, 0, 'woe tyrande mannoroth 9');
INSERT INTO `creature_text` VALUES (55524, 12, 0, 'No! Illidan!', 12, 0, 100, 0, 0, 26002, 0, 54265, 0, 'woe tyrande mannoroth 10');
INSERT INTO `creature_text` VALUES (55524, 13, 0, 'Malfurion, he has done it! The portal is collapsing!', 12, 0, 100, 0, 0, 26003, 0, 54270, 0, 'woe tyrande mannoroth 11');
INSERT INTO `creature_text` VALUES (55524, 14, 0, 'There are too many of them!', 14, 0, 100, 0, 0, 26004, 0, 54609, 0, 'woe tyrande mannoroth 12');
INSERT INTO `creature_text` VALUES (55532, 0, 0, 'The artifact!', 14, 0, 100, 0, 0, 26059, 0, 56630, 0, 'woe illidan conclusion 1');
INSERT INTO `creature_text` VALUES (55532, 1, 0, 'Brother. A timely arrival...', 12, 0, 100, 0, 0, 26060, 0, 56631, 0, 'woe illidan conclusion 2');
INSERT INTO `creature_text` VALUES (55532, 2, 0, 'Aye. It\'s been twisted and turned by too many spells. The fuss we - especially you - made with the portal was too much! The same spell that sent the Burning Legion back into their foul realm now works on the well! It\'s devouring itself and taking its surroundings with it! Fascinating, isn\'t it?', 12, 0, 100, 0, 0, 26061, 0, 56632, 0, 'woe illidan conclusion 3');
INSERT INTO `creature_text` VALUES (55532, 3, 0, 'If you\'ve a way out of here, we should probably use it! I\'ve tried casting myself and Tyrande out of here, but the well is too much in flux!', 12, 0, 100, 0, 0, 26062, 0, 56633, 0, 'woe illidan conclusion 4');
INSERT INTO `creature_text` VALUES (55532, 4, 0, 'Can you close the portal, brother?', 12, 0, 100, 0, 0, 26084, 0, 54103, 0, 'woe illidan mannoroth 1');
INSERT INTO `creature_text` VALUES (55532, 5, 0, 'Very well, we shall break it for you.', 12, 0, 100, 0, 0, 26086, 0, 54104, 0, 'woe illidan mannoroth 2');
INSERT INTO `creature_text` VALUES (55532, 6, 0, 'Let them come.', 12, 0, 100, 0, 0, 26085, 0, 54105, 0, 'woe illidan mannoroth 3');
INSERT INTO `creature_text` VALUES (55532, 7, 0, 'There are so MANY of them...', 12, 0, 100, 0, 0, 26087, 0, 54109, 0, 'woe illidan mannoroth 4');
INSERT INTO `creature_text` VALUES (55532, 8, 0, 'Weak, pitiful creatures. Hardly worthy of being called a legion.', 12, 0, 100, 0, 0, 26088, 0, 54111, 0, 'woe illidan mannoroth 5');
INSERT INTO `creature_text` VALUES (55532, 9, 0, 'Oh this will be fun...', 12, 0, 100, 0, 0, 26089, 0, 54112, 0, 'woe illidan mannoroth 6');
INSERT INTO `creature_text` VALUES (55532, 10, 0, 'Wait, I have an idea.', 12, 0, 100, 0, 0, 26090, 0, 54113, 0, 'woe illidan mannoroth 7');
INSERT INTO `creature_text` VALUES (55532, 11, 0, 'What our people could not.', 12, 0, 100, 0, 0, 26091, 0, 54116, 0, 'woe illidan mannoroth 8');
INSERT INTO `creature_text` VALUES (55532, 12, 0, 'Yes...YES. I can feel the raw power of the Well of Eternity! It is time to end this charade!', 12, 0, 100, 0, 0, 26092, 0, 54117, 0, 'woe illidan mannoroth 9');
INSERT INTO `creature_text` VALUES (55532, 13, 0, 'They are not where they appear to be!  Strike in an area, it is the only way to uncover the real one!', 12, 0, 100, 0, 0, 26093, 0, 54121, 0, 'woe illidan mannoroth 10');
INSERT INTO `creature_text` VALUES (55532, 14, 0, 'Handle Varo\'then. Mannoroth is mine.', 12, 0, 100, 0, 0, 26094, 0, 54184, 0, 'woe illidan mannoroth 11');
INSERT INTO `creature_text` VALUES (55532, 15, 0, 'The sword has pierced his infernal armor! Strike him down!', 14, 0, 100, 0, 0, 26095, 0, 54254, 0, 'woe illidan mannoroth 14');
INSERT INTO `creature_text` VALUES (55532, 16, 0, 'You are not the sole wielder of Sargeras\' power, Mannoroth! Behold!', 14, 0, 100, 0, 0, 26096, 0, 54261, 0, 'woe illidan mannoroth 15');
INSERT INTO `creature_text` VALUES (55532, 17, 0, 'I will be the savior of our people! I WILL FULFILL MY DESTINY!', 14, 0, 100, 0, 0, 26098, 0, 54264, 0, 'woe illidan mannoroth 16');
INSERT INTO `creature_text` VALUES (55532, 18, 0, '[Demonic] Revos ill ok mordanas archim maz naztheros! Archim xi ante maz-re mishun te nagas!', 14, 0, 100, 0, 0, 26097, 0, 54266, 0, 'woe illidan mannoroth 17');
INSERT INTO `creature_text` VALUES (55532, 19, 0, 'He is still connected to the Well somehow! Focus your attacks on Mannoroth, we must disrupt his concentration!', 14, 0, 100, 0, 0, 26099, 0, 54257, 0, 'woe illidan mannoroth 18');
INSERT INTO `creature_text` VALUES (55570, 0, 0, 'Hush, Tyrande. Where is Illidan?', 12, 0, 100, 0, 0, 26490, 0, 56637, 0, 'woe malfurion conclusion 01');
INSERT INTO `creature_text` VALUES (55570, 1, 0, 'Illidan! The well is out of control!', 14, 0, 100, 0, 0, 26491, 0, 56638, 0, 'woe malfurion conclusion 02');
INSERT INTO `creature_text` VALUES (55570, 2, 0, 'Not if we\'re caught up in it! Why weren\'t you running? What have you been doing with your hand in the Well?', 12, 0, 100, 0, 0, 26492, 0, 56639, 0, 'woe malfurion conclusion 03');
INSERT INTO `creature_text` VALUES (55570, 3, 0, 'This way!', 14, 0, 100, 0, 0, 26493, 0, 49296, 0, 'woe malfurion conclusion 04');
INSERT INTO `creature_text` VALUES (55570, 4, 0, 'It is being maintained by the will of a powerful demon...Mannoroth.', 12, 0, 100, 0, 0, 26494, 0, 54106, 0, 'woe malfurion mannoroth 01');
INSERT INTO `creature_text` VALUES (55570, 5, 0, 'I cannot break his will alone...', 12, 0, 100, 0, 0, 26495, 0, 54107, 0, 'woe malfurion mannoroth 02');
INSERT INTO `creature_text` VALUES (55624, 4, 0, 'Good luck, heroes!', 12, 0, 100, 0, 0, 25964, 0, 55306, 0, 'woe Nozdormu intro 4');
INSERT INTO `creature_text` VALUES (55636, 0, 0, 'Death to all who oppose us!', 14, 0, 100, 0, 0, 0, 0, 53757, 0, 'TWILIGHT_DRAKE');
INSERT INTO `creature_text` VALUES (55649, 0, 0, 'I am in pain, but it warms my heart that Liu Lang\'s grandchildren have not forgotten me.', 12, 0, 100, 0, 0, 27822, 0, 55550, 0, 'Shang Xi\'s Hot Air Balloon');
INSERT INTO `creature_text` VALUES (55649, 1, 0, 'There is a thorn in my side.  I cannot remove it.', 12, 0, 100, 0, 0, 27823, 0, 55568, 0, 'Shang Xi\'s Hot Air Balloon');
INSERT INTO `creature_text` VALUES (55649, 2, 0, 'The pain is unbearable, and I can no longer swim straight.', 12, 0, 100, 0, 0, 27824, 0, 55569, 0, 'Shang Xi\'s Hot Air Balloon');
INSERT INTO `creature_text` VALUES (55649, 3, 0, 'Please grandchildren, can you remove this thorn?  I cannot do so on my own.', 12, 0, 100, 0, 0, 27825, 0, 55570, 0, 'Shang Xi\'s Hot Air Balloon');
INSERT INTO `creature_text` VALUES (55649, 4, 0, 'It is in the forest where your feet do not walk.  Continue along the mountains and you will find it.', 12, 0, 100, 0, 0, 27826, 0, 55572, 0, 'Shang Xi\'s Hot Air Balloon');
INSERT INTO `creature_text` VALUES (55649, 5, 0, 'Thank you, grandchildren.', 12, 0, 100, 0, 0, 27827, 0, 63407, 0, 'Shang Xi\'s Hot Air Balloon');
INSERT INTO `creature_text` VALUES (55689, 0, 0, 'Even with the Aspect of Time on your side, you stumble foolishly into a trap?', 14, 0, 100, 0, 0, 26223, 0, 56614, 0, 'hagara adds 1');
INSERT INTO `creature_text` VALUES (55689, 1, 0, 'Don\'t preen just yet, little pups. We\'ll cleanse this world of your kind.', 14, 0, 100, 0, 0, 26224, 0, 56615, 0, 'hagara adds 2');
INSERT INTO `creature_text` VALUES (55689, 2, 0, 'You\'ll not leave this place alive!', 14, 0, 100, 0, 0, 26225, 0, 56616, 0, 'hagara adds 3');
INSERT INTO `creature_text` VALUES (55689, 3, 0, 'Not one of you will live to see the final cataclysm! Finish them!', 14, 0, 100, 0, 0, 26226, 0, 56617, 0, 'hagara adds 4');
INSERT INTO `creature_text` VALUES (55689, 4, 0, 'You cross the Stormbinder! I\'ll slaughter you all.', 14, 0, 100, 0, 0, 26227, 0, 55647, 0, 'hagara aggro');
INSERT INTO `creature_text` VALUES (55689, 5, 0, 'What are you doing?', 14, 0, 100, 0, 0, 26228, 0, 16901, 0, 'hagara circuit 01');
INSERT INTO `creature_text` VALUES (55689, 5, 1, 'You\'re toying with death.', 14, 0, 100, 0, 0, 26229, 0, 55669, 0, 'hagara circuit 02');
INSERT INTO `creature_text` VALUES (55689, 5, 2, 'You think you can play with my lightning?', 14, 0, 100, 0, 0, 26230, 0, 55670, 0, 'hagara circuit 03');
INSERT INTO `creature_text` VALUES (55689, 5, 3, 'Enough of your games! You won\'t live to do it again.', 14, 0, 100, 0, 0, 26232, 0, 55673, 0, 'hagara circuit 05');
INSERT INTO `creature_text` VALUES (55689, 5, 4, 'No! More... lightning...', 14, 0, 100, 0, 0, 26233, 0, 55672, 0, 'hagara circuit 06');
INSERT INTO `creature_text` VALUES (55689, 5, 5, 'I\'ll finish you now, pups!', 14, 0, 100, 0, 0, 26234, 0, 55674, 0, 'hagara circuit 07');
INSERT INTO `creature_text` VALUES (55689, 6, 0, 'Get away from that, mongrel!', 14, 0, 100, 0, 0, 26235, 0, 57084, 0, 'hagara crystal dead 01');
INSERT INTO `creature_text` VALUES (55689, 6, 1, 'You\'ll PAY for that.', 14, 0, 100, 0, 0, 26236, 0, 68368, 0, 'hagara crystal dead 02');
INSERT INTO `creature_text` VALUES (55689, 6, 2, 'Enough!', 14, 0, 100, 0, 0, 26237, 0, 7381, 0, 'hagara crystal dead 03');
INSERT INTO `creature_text` VALUES (55689, 6, 3, 'Again?!', 14, 0, 100, 0, 0, 26239, 0, 65693, 0, 'hagara crystal dead 05');
INSERT INTO `creature_text` VALUES (55689, 6, 4, 'Impudent pup!', 14, 0, 100, 0, 0, 26240, 0, 55666, 0, 'hagara crystal dead 06');
INSERT INTO `creature_text` VALUES (55689, 6, 5, 'The one remaining is still enough to finish you.', 14, 0, 100, 0, 0, 26241, 0, 55667, 0, 'hagara crystal dead 07');
INSERT INTO `creature_text` VALUES (55689, 7, 0, 'Cowards! You pack of weakling... dogs...', 14, 0, 100, 0, 0, 26243, 0, 55715, 0, 'hagara dead');
INSERT INTO `creature_text` VALUES (55689, 8, 0, 'You face more than my axes, this close.', 14, 0, 100, 0, 0, 26244, 0, 55658, 0, 'hagara frostray 01');
INSERT INTO `creature_text` VALUES (55689, 8, 1, 'See what becomes of those who stand before me!', 14, 0, 100, 0, 0, 26245, 0, 55659, 0, 'hagara frostray 02');
INSERT INTO `creature_text` VALUES (55689, 8, 2, 'Feel a chill up your spine...?', 14, 0, 100, 0, 0, 26246, 0, 55660, 0, 'hagara frostray 03');
INSERT INTO `creature_text` VALUES (55689, 9, 0, 'You can\'t outrun the storm.', 14, 0, 100, 0, 0, 26247, 0, 55654, 0, 'hagara glacier 01');
INSERT INTO `creature_text` VALUES (55689, 9, 1, 'Die beneath the ice.', 14, 0, 100, 0, 0, 26248, 0, 55655, 0, 'hagara glacier 02');
INSERT INTO `creature_text` VALUES (55689, 10, 0, 'Stay, pup.', 14, 0, 100, 0, 0, 26249, 0, 55652, 0, 'hagara icetomb 01');
INSERT INTO `creature_text` VALUES (55689, 10, 1, 'Hold still.', 14, 0, 100, 0, 0, 26250, 0, 55653, 0, 'hagara icetomb 02');
INSERT INTO `creature_text` VALUES (55689, 11, 0, 'Swagger all you like; you pups don\'t stand a chance. Flee now, while you can.', 14, 0, 100, 0, 0, 26251, 0, 55646, 0, 'hagara intro 01');
INSERT INTO `creature_text` VALUES (55689, 12, 0, 'Suffer the storm\'s wrath!', 14, 0, 100, 0, 0, 26252, 0, 55656, 0, 'hagara lightning 01');
INSERT INTO `creature_text` VALUES (55689, 12, 1, 'Thunder and lightning dance at my call!', 14, 0, 100, 0, 0, 26253, 0, 55657, 0, 'hagara lightning 02');
INSERT INTO `creature_text` VALUES (55689, 13, 0, 'Feh!', 14, 0, 100, 0, 0, 26254, 0, 55649, 0, 'hagara slay 02');
INSERT INTO `creature_text` VALUES (55689, 13, 1, 'You should have run, dog.', 14, 0, 100, 0, 0, 26255, 0, 55648, 0, 'hagara slay 01');
INSERT INTO `creature_text` VALUES (55689, 13, 2, 'Down, pup.', 14, 0, 100, 0, 0, 26256, 0, 55650, 0, 'hagara slay 03');
INSERT INTO `creature_text` VALUES (55689, 13, 3, 'A waste of my time.', 14, 0, 100, 0, 0, 26257, 0, 55651, 0, 'hagara slay 04');
INSERT INTO `creature_text` VALUES (55779, 0, 0, 'Show yourself!', 12, 0, 100, 1, 0, 25877, 0, 53049, 0, 'THRALL_ARCURION_1');
INSERT INTO `creature_text` VALUES (55779, 1, 0, 'We\'re surrounded - dispatch the ascendant while I keep the ambushers at bay.', 14, 0, 100, 1, 0, 25878, 0, 54177, 0, 'THRALL_ARCURION_2');
INSERT INTO `creature_text` VALUES (55779, 2, 0, 'You\'ve almost got him! Og\'nor ka Lok\'tar - Now we finish this!', 14, 0, 100, 1, 0, 25881, 0, 53963, 0, 'THRALL_ARCURION_3');
INSERT INTO `creature_text` VALUES (55779, 3, 0, 'We\'ve been discovered. I know you\'re tired, but we cannot keep the Aspects waiting!', 12, 0, 100, 1, 0, 25882, 0, 54294, 0, 'THRALL_ARCURION_4');
INSERT INTO `creature_text` VALUES (55779, 4, 0, 'Follow me.', 12, 0, 100, 0, 0, 0, 0, 11279, 0, 'THRALL_ARCURION_5');
INSERT INTO `creature_text` VALUES (55869, 4, 2, 'I hate mercy.', 14, 0, 0, 0, 0, 25783, 0, 56320, 0, 'VO_BH_ALIZABAL_SLAY_03');
INSERT INTO `creature_text` VALUES (55869, 4, 3, 'I didn\'t hate that.', 14, 0, 0, 0, 0, 25784, 0, 56321, 0, 'VO_BH_ALIZABAL_SLAY_04');
INSERT INTO `creature_text` VALUES (55869, 5, 2, 'Feel my hatred!', 14, 0, 0, 0, 0, 25787, 0, 49734, 0, 'VO_BH_ALIZABAL_SPELL_03');
INSERT INTO `creature_text` VALUES (55869, 5, 3, 'My hatred burns!', 14, 0, 0, 0, 0, 25788, 0, 56327, 0, 'VO_BH_ALIZABAL_SPELL_04');
INSERT INTO `creature_text` VALUES (55869, 5, 4, 'My hate will consume you!', 14, 0, 0, 0, 0, 25789, 0, 56328, 0, 'VO_BH_ALIZABAL_SPELL_05');
INSERT INTO `creature_text` VALUES (55870, 0, 0, 'Little hand says it\'s time to rock and roll. Everyone into the hold: We\'ve got a dragon to kill.', 14, 0, 100, 0, 0, 26289, 0, 55137, 0, 'Sky Captain Swayze blackhorn victory');
INSERT INTO `creature_text` VALUES (55870, 1, 0, 'The plates! He\'s coming apart! Tear up the plates and we\'ve got a shot at bringing him down!', 14, 0, 100, 0, 0, 26290, 0, 56618, 0, 'Sky Captain Swayze deathwing back 01');
INSERT INTO `creature_text` VALUES (55870, 2, 0, 'Don\'t worry about me. Stop the Destroyer! Enshu-falah-nah, dorini.', 14, 0, 100, 0, 0, 26291, 0, 56619, 0, 'Sky Captain Swayze');
INSERT INTO `creature_text` VALUES (55870, 3, 0, 'All ahead full. Everything depends on our speed! We can\'t let the Destroyer get away.', 14, 0, 100, 0, 0, 26292, 0, 55430, 0, 'Sky Captain Swayze event 01');
INSERT INTO `creature_text` VALUES (55870, 4, 0, 'All hands to battle stations; get those monsters away from the ship!', 14, 0, 100, 0, 0, 26294, 0, 55431, 0, 'Sky Captain Swayze event 02');
INSERT INTO `creature_text` VALUES (55870, 5, 0, 'Concentrate everything on the armored drake!', 14, 0, 100, 0, 0, 26295, 0, 55433, 0, 'Sky Captain Swayze event 03');
INSERT INTO `creature_text` VALUES (55870, 6, 0, 'An enemy sapper\'s breached the engine room!', 14, 0, 100, 0, 0, 26296, 0, 55434, 0, 'Sky Captain Swayze event 04');
INSERT INTO `creature_text` VALUES (55870, 7, 0, 'The Skyfire can\'t take much more of this!', 14, 0, 100, 0, 0, 26297, 0, 55435, 0, 'Sky Captain Swayze event 05');
INSERT INTO `creature_text` VALUES (55870, 8, 0, 'We\'re going down. Abandon the ship!', 14, 0, 100, 0, 0, 26298, 0, 55436, 0, 'Sky Captain Swayze event 06');
INSERT INTO `creature_text` VALUES (55870, 9, 0, 'Welcome aboard the Skyfire.  You ready to chase down the end of the world?', 14, 0, 100, 0, 0, 26302, 0, 56629, 0, 'Sky Captain Swayze pre deathwing back 01');
INSERT INTO `creature_text` VALUES (55870, 10, 0, 'Our engines are damaged!  We\'re sitting ducks up here!', 14, 0, 100, 0, 0, 26303, 0, 56079, 0, 'Sky Captain Swayze pre deathwing back 02');
INSERT INTO `creature_text` VALUES (55971, 0, 0, 'It is good to see you again, Alexstrasza. I have been busy in my absence.', 14, 0, 100, 0, 0, 26360, 0, 56080, 0, '');
INSERT INTO `creature_text` VALUES (55971, 1, 0, 'Twisting your pitiful whelps into mindless abominations, bent only to my will. It was a very... painful process.', 14, 0, 100, 0, 0, 26361, 0, 56646, 0, '');
INSERT INTO `creature_text` VALUES (55971, 2, 0, 'Mere whelps, experiments, a means to a greater end. You will see what the research of my clutch has yielded.', 14, 0, 100, 0, 0, 26362, 0, 55322, 0, '');
INSERT INTO `creature_text` VALUES (55971, 3, 0, 'Nefarian, Onyxia, Sinestra... they were nothing. Now you face my ultimate creation.', 14, 0, 100, 0, 0, 26363, 0, 56647, 0, '');
INSERT INTO `creature_text` VALUES (55971, 4, 0, 'The Hour of Twilight is nigh; the sun sets on your pitiful mortal existence.', 14, 0, 100, 0, 0, 26364, 0, 56648, 0, '');
INSERT INTO `creature_text` VALUES (55999, 0, 0, 'I can... I can barely breathe.  I\'m alive thanks to you.', 12, 0, 100, 18, 0, 0, 0, 64337, 0, 'Raufen - Injured Soldier - Talk');
INSERT INTO `creature_text` VALUES (55999, 0, 1, 'I owe you my life.', 12, 0, 100, 2, 0, 0, 0, 37286, 0, 'Raufen - Injured Soldier - Talk');
INSERT INTO `creature_text` VALUES (55999, 0, 2, 'I thought that was it... I thought I was going to die.  Thank you, stranger.', 12, 0, 100, 18, 0, 0, 0, 64344, 0, 'Raufen - Injured Soldier - Talk');
INSERT INTO `creature_text` VALUES (55999, 0, 3, 'I\'m alive thanks only to you... I won\'t forget this.', 12, 0, 100, 18, 0, 0, 0, 64342, 0, 'Raufen - Injured Soldier - Talk');
INSERT INTO `creature_text` VALUES (55999, 0, 4, 'I\'m... alive?  Thank you... thank you so much!', 12, 0, 100, 18, 0, 0, 0, 63840, 0, 'Raufen - Injured Soldier - Talk');
INSERT INTO `creature_text` VALUES (55999, 0, 5, 'I... I am in your debt.', 12, 0, 100, 0, 0, 0, 0, 55139, 0, 'Raufen - Injured Soldier - Talk');
INSERT INTO `creature_text` VALUES (55999, 0, 6, 'Thank you for the timely rescue, hero.', 12, 0, 100, 2, 0, 0, 0, 64355, 0, 'Raufen - Injured Soldier - Talk');
INSERT INTO `creature_text` VALUES (56099, 4, 0, 'He\'s completely mad!', 14, 0, 100, 0, 0, 26499, 0, 56774, 0, 'Alexstrasza - second phase');
INSERT INTO `creature_text` VALUES (56099, 5, 0, 'Excellent work. The fire of my heart glows with a brilliant purity unmatched; every spark of it I will channel into the Dragon Soul.', 14, 0, 100, 0, 0, 26497, 0, 56773, 0, 'Alexstrasza - outro');
INSERT INTO `creature_text` VALUES (56102, 4, 0, 'It is time. I will expend everything to bind every thread here, now, around the Dragon Soul. What comes to pass will NEVER be undone.', 14, 0, 100, 0, 0, 25948, 0, 56769, 0, 'Nozdormu - outro');
INSERT INTO `creature_text` VALUES (56103, 1, 1, 'Avenge the fallen! Continue the fight!', 14, 0, 100, 0, 0, 25903, 0, 56738, 0, 'Thrall - second phase 2');
INSERT INTO `creature_text` VALUES (56103, 1, 2, 'These champions are our only hope for exposing Deathwing\'s weakness. Together, we can prevail.', 14, 0, 100, 0, 0, 25896, 0, 55845, 0, 'Thrall - second phase 3');
INSERT INTO `creature_text` VALUES (56103, 1, 3, 'Don\'t give up! We are counting on you to push Deathwing back.', 14, 0, 100, 0, 0, 25897, 0, 55846, 0, 'Thrall - second phase 4');
INSERT INTO `creature_text` VALUES (56103, 1, 4, 'Only you can give us our opening!', 14, 0, 100, 0, 0, 25902, 0, 55844, 0, 'Thrall - second phase 5');
INSERT INTO `creature_text` VALUES (56159, 0, 0, 'Come child. We have one final journey to take together before your training is complete.', 12, 0, 100, 1, 0, 27797, 0, 55520, 0, '');
INSERT INTO `creature_text` VALUES (56159, 1, 0, 'Beyond the Elders\' Path lies the Wood of Staves, a sacred place that only the worthy may enter.', 12, 0, 100, 1, 0, 27800, 0, 55521, 0, '');
INSERT INTO `creature_text` VALUES (56159, 2, 0, 'Of the many ways to prove your worth, I require the simplest of you now. I must know that you will fight for our people. I must know that you can keep them safe.', 12, 0, 100, 1, 0, 27801, 0, 55522, 0, '');
INSERT INTO `creature_text` VALUES (56159, 3, 0, 'Defeat the Guardian of the Elders, and we may pass.', 12, 0, 100, 1, 0, 27802, 0, 55523, 0, '');
INSERT INTO `creature_text` VALUES (56159, 4, 0, 'You\'ve become strong indeed, child. This is good. You will need that strength soon.', 12, 0, 100, 1, 0, 27803, 0, 56484, 0, '');
INSERT INTO `creature_text` VALUES (55672, 0, 0, 'And here we are. Help me with a couple small tasks while I prepare, if you would.', 12, 0, 100, 1, 0, 27805, 0, 56485, 0, '');
INSERT INTO `creature_text` VALUES (56165, 0, 0, 'A conducting crystal overloads!', 41, 0, 100, 0, 0, 0, 0, 54780, 0, 'Crystal Conductor - announce overload');
INSERT INTO `creature_text` VALUES (56167, 10, 0, '|TInterface\\Icons\\spell_fire_moltenblood.blp:20|t%s begins to |cFFFF0000|Hspell:105863|h[Hemorrhage]|h|r!', 41, 0, 100, 0, 0, 0, 0, 55023, 0, 'Madness of Deathwing - Hemorrhage');
INSERT INTO `creature_text` VALUES (56168, 10, 0, '|TInterface\\Icons\\spell_fire_moltenblood.blp:20|t%s begins to |cFFFF0000|Hspell:105863|h[Hemorrhage]|h|r!', 41, 0, 100, 0, 0, 0, 0, 55023, 0, 'Madness of Deathwing - Hemorrhage');
