INSERT INTO `creature_text` VALUES (56541, 1, 0, 'If you truly wish to undo what your kind have wrought upon our land you must first prove yourselves here in our school.', 14, 0, 100, 0, 0, 27627, 0, 55308, 0, 'Master Snowdrift');
