INSERT INTO `creature_text` VALUES (56742, 0, 0, 'We\'ve got to light that signal fire. Without reinforcements, they\'ll destroy the gate!', 12, 0, 0, 5, 0, 29068, 0, 55755, 0, 'Serpent\'s Spine Defender');
INSERT INTO `creature_text` VALUES (56743, 0, 0, 'But the mantid have overrun the area. There\'s no clear path!', 12, 0, 0, 1, 0, 29071, 0, 55756, 0, 'Serpent\'s Spine Defender');
INSERT INTO `creature_text` VALUES (56745, 0, 0, 'Don\'t worry! I can make it!', 12, 0, 0, 0, 0, 27692, 0, 55757, 0, 'Serpent\'s Spine Trainee');
INSERT INTO `creature_text` VALUES (56747, 0, 0, 'Let me show you my power.', 12, 0, 100, 0, 0, 28317, 0, 59297, 0, 'Gu Cloudstrike');
INSERT INTO `creature_text` VALUES (56747, 1, 0, 'Come to me, serpent!  Together we will destroy these intruders.', 12, 0, 100, 0, 0, 28318, 0, 59298, 0, 'Gu Cloudstrike');
INSERT INTO `creature_text` VALUES (56747, 2, 0, 'Even together... we failed...', 14, 0, 100, 0, 0, 28319, 0, 66226, 0, 'Gu Cloudstrike');
INSERT INTO `creature_text` VALUES (56747, 3, 0, 'Their energy is strong!', 14, 0, 100, 0, 0, 28320, 0, 58683, 0, 'Gu Cloudstrike');
INSERT INTO `creature_text` VALUES (56747, 4, 0, 'No, no! Do not fail me!', 14, 0, 100, 0, 0, 28321, 0, 58684, 0, 'Gu Cloudstrike');
INSERT INTO `creature_text` VALUES (56747, 5, 0, 'Weaklings... let me make room for our guests.', 14, 0, 100, 0, 0, 28322, 0, 59289, 0, 'Gu Cloudstrike');
INSERT INTO `creature_text` VALUES (56747, 6, 0, 'Approach... your death awaits.', 14, 0, 100, 0, 0, 28323, 0, 59290, 0, 'Gu Cloudstrike');
INSERT INTO `creature_text` VALUES (56747, 7, 0, 'Serpent, charge me with your power!', 12, 0, 100, 0, 0, 28324, 0, 58682, 0, 'Gu Cloudstrike');
INSERT INTO `creature_text` VALUES (56747, 8, 0, 'Weakling!', 14, 0, 100, 0, 0, 28325, 0, 35694, 0, 'Gu Cloudstrike');
INSERT INTO `creature_text` VALUES (56747, 8, 1, 'Lightning has no mercy!', 14, 0, 100, 0, 0, 28326, 0, 66228, 0, 'Gu Cloudstrike');
INSERT INTO `creature_text` VALUES (56762, 0, 0, 'Be free of doubt, priestess...', 14, 0, 100, 0, 0, 28559, 0, 55909, 0, 'Yu\'lon');
INSERT INTO `creature_text` VALUES (56781, 0, 0, '|TInterface\\Icons\\spell_fire_twilightflamebreath.blp:20|t%s prepares to unleash a |cFF9900CC|Hspell:106401|h[Twilight Onslaught]|h|r!', 41, 0, 100, 0, 0, 0, 0, 56596, 0, 'goriona - twilight onslaught');
INSERT INTO `creature_text` VALUES (56781, 1, 0, '|Tinterface\\icons\\spell_fire_twilightrainoffire.blp:0|t%s fires a |cFF9900CC|Hspell:110153|h[Broadside]|h|r at the Skyfire!', 41, 0, 100, 0, 0, 0, 0, 57050, 0, 'goriona - broadside');
INSERT INTO `creature_text` VALUES (56781, 2, 0, '%s screeches in pain and retreats into the swirling clouds.', 41, 0, 100, 0, 0, 0, 0, 56156, 0, 'goriona - low hp');
INSERT INTO `creature_text` VALUES (56843, 0, 0, 'You there! In the rafters! My sacred library has become possessed by the Sha!', 14, 0, 100, 0, 0, 30887, 0, 57464, 0, 'Lorewalker Stonestep');
INSERT INTO `creature_text` VALUES (56843, 1, 0, 'See how they pour from every letter of these sacred scrolls... corrupting them! Defiling them!', 14, 0, 100, 0, 0, 30888, 0, 57465, 0, 'Lorewalker Stonestep');
INSERT INTO `creature_text` VALUES (56843, 2, 0, 'I beseech you, kind strangers. Cleanse this place of their foul presence! Lay our stories to rest!', 14, 0, 100, 0, 0, 30889, 0, 57604, 0, 'Lorewalker Stonestep');
INSERT INTO `creature_text` VALUES (56843, 3, 0, 'You must destroy the source of the corruption! Quickly, before our cherished history is erased forever!', 14, 0, 100, 0, 0, 30890, 0, 57467, 0, 'Lorewalker Stonestep');
INSERT INTO `creature_text` VALUES (56843, 4, 0, 'Oh, my. If I am not mistaken, it appears that the tale of Zao Sunseeker has come to life before us.', 14, 0, 100, 0, 0, 30901, 0, 57605, 0, 'Lorewalker Stonestep');
INSERT INTO `creature_text` VALUES (56843, 5, 0, 'According to the legend, long ago, there were five suns that burned brightly in the sky. When the winds blew, their intense heat caused great wildfires.', 14, 0, 100, 0, 0, 30902, 0, 57607, 0, 'Lorewalker Stonestep');
INSERT INTO `creature_text` VALUES (56843, 6, 0, 'Crops wilted, villages burned, and families wandered endlessly to seek refuge from the flames...', 14, 0, 100, 0, 0, 30903, 0, 57608, 0, 'Lorewalker Stonestep');
INSERT INTO `creature_text` VALUES (56843, 7, 0, 'The fires, they are burning the library! Please, heroes! Purge this evil before the temple crumbles to ash!', 14, 0, 100, 0, 0, 30904, 0, 57610, 0, 'Lorewalker Stonestep');
INSERT INTO `creature_text` VALUES (56843, 8, 0, 'Zao has become corrupted too! Please, fight him with dignity...', 14, 0, 100, 0, 0, 30906, 0, 57746, 0, 'Lorewalker Stonestep');
INSERT INTO `creature_text` VALUES (56843, 9, 0, 'Ah, it is not yet over. From what I see, we face the trial of the yaungol. Let me shed some light...', 14, 0, 100, 0, 0, 30896, 0, 57614, 0, 'Lorewalker Stonestep');
INSERT INTO `creature_text` VALUES (56843, 10, 0, 'As the tale goes, the yaungol was traveling across the Kun\'lai plains when suddenly he was ambushed by two strange creatures!', 14, 0, 100, 0, 0, 30897, 0, 57986, 0, 'Lorewalker Stonestep');
INSERT INTO `creature_text` VALUES (56843, 11, 0, 'The yaungol attacked the beasts with great determination and ferocity. Yet, the more he attacked, the more fearsome the beasts grew!', 14, 0, 100, 0, 0, 30898, 0, 57988, 0, 'Lorewalker Stonestep');
INSERT INTO `creature_text` VALUES (56843, 12, 0, 'However, when he let it alone, the beast shrank to near nothingness.', 14, 0, 100, 0, 0, 30899, 0, 57989, 0, 'Lorewalker Stonestep');
INSERT INTO `creature_text` VALUES (56843, 13, 0, 'For you see, strife feeds on conflict. When you have peace in your heart, strife will find somewhere else to roam.', 14, 0, 100, 0, 0, 30900, 0, 57990, 0, 'Lorewalker Stonestep');
INSERT INTO `creature_text` VALUES (56846, 10, 0, '|TInterface\\Icons\\spell_fire_moltenblood.blp:20|t%s begins to |cFFFF0000|Hspell:105863|h[Hemorrhage]|h|r!', 41, 0, 100, 0, 0, 0, 0, 55023, 0, 'Madness of Deathwing - Hemorrhage');
INSERT INTO `creature_text` VALUES (56877, 0, 0, '%s\'s |cFFFF0000Weak Spot|r is exposed! Attack it to break his Impervious Carapace!', 41, 0, 100, 0, 0, 0, 0, 57628, 0, 'boss_raigonn');
INSERT INTO `creature_text` VALUES (56877, 1, 0, 'The |cFFFE9A2EKrik\'thik Swarm Bringer|r reinforcements join the battle!', 41, 0, 100, 0, 0, 0, 0, 57603, 0, 'boss_raigonn');
INSERT INTO `creature_text` VALUES (56877, 2, 0, 'The |cFFFE9A2EKrik\'thik Engulfer|r reinforcements appear above!', 41, 0, 100, 0, 0, 0, 0, 57602, 0, 'boss_raigonn');
INSERT INTO `creature_text` VALUES (56877, 3, 0, 'The |cFFFE9A2EKrik\'thik Protectorate|r reinforcements appear!', 41, 0, 100, 0, 0, 0, 0, 57601, 0, 'boss_raigonn');
INSERT INTO `creature_text` VALUES (56884, 0, 0, 'Taran Zhu and the Shado-Pan are MINE. Come forward and die by my hand, as all who oppose me shall in the days to come!', 14, 0, 100, 0, 0, 28334, 0, 63341, 0, 'Corrupted Taran Zhu');
INSERT INTO `creature_text` VALUES (56884, 1, 0, 'Hatred will consume and conquer all!', 14, 0, 100, 0, 0, 28332, 0, 63342, 0, 'Corrupted Taran Zhu');
INSERT INTO `creature_text` VALUES (56884, 2, 0, 'No... NO!', 14, 0, 100, 0, 0, 28333, 0, 60276, 0, 'Corrupted Taran Zhu');
INSERT INTO `creature_text` VALUES (56884, 3, 0, 'Rage at your defeat.', 14, 0, 100, 0, 0, 28335, 0, 63344, 0, 'Corrupted Taran Zhu');
INSERT INTO `creature_text` VALUES (56884, 4, 0, 'The Sha of Hatred has fled my body... and the monastery, as well. I must thank you, strangers. The Shado-Pan are in your debt. Now, there is much work to be done...', 14, 0, 100, 0, 0, 28423, 0, 63340, 0, 'Corrupted Taran Zhu');
INSERT INTO `creature_text` VALUES (56906, 0, 0, 'Gate?! I\'m going to bring this whole wall down!', 14, 0, 0, 0, 0, 29341, 0, 55896, 0, 'boss_saboteur_kiptilak - SAY_INTRO');
INSERT INTO `creature_text` VALUES (56906, 1, 0, 'I brought enough bombs... for all of you!', 14, 0, 0, 0, 0, 29339, 0, 63323, 0, 'boss_saboteur_kiptilak - SAY_AGGRO');
