INSERT INTO `creature_text` VALUES (56906, 2, 2, 'You blew it!', 14, 0, 0, 0, 0, 29344, 0, 63330, 0, 'boss_saboteur_kiptilak - SAY_SLAY');
INSERT INTO `creature_text` VALUES (56906, 3, 0, 'I... blew it...', 14, 0, 0, 0, 0, 29340, 0, 63325, 0, 'boss_saboteur_kiptilak - SAY_DEATH');
INSERT INTO `creature_text` VALUES (56906, 4, 0, 'You\'re the bomb!', 14, 0, 0, 0, 0, 29345, 0, 55899, 0, 'boss_saboteur_kiptilak - SAY_SPECIAL');
INSERT INTO `creature_text` VALUES (56906, 4, 1, 'This will be the bomb.', 14, 0, 0, 0, 0, 29346, 0, 55897, 0, 'boss_saboteur_kiptilak - SAY_SPECIAL');
INSERT INTO `creature_text` VALUES (56906, 4, 2, 'Things that make you go... BOOM!', 14, 0, 0, 0, 0, 29347, 0, 55900, 0, 'boss_saboteur_kiptilak - SAY_SPECIAL');
INSERT INTO `creature_text` VALUES (56906, 4, 3, 'BOOM! Explosion! Overpowering!', 14, 0, 0, 0, 0, 29348, 0, 55901, 0, 'boss_saboteur_kiptilak - SAY_SPECIAL');
