INSERT INTO `creature_text` VALUES (57107, 0, 0, 'It is them!', 14, 0, 100, 0, 0, 26030, 0, 55913, 0, 'woe bronze dragon1 event 1');
INSERT INTO `creature_text` VALUES (57107, 1, 0, 'Sister, look! The artifact is surrounded by an aura of darkness!', 14, 0, 100, 0, 0, 26031, 0, 55915, 0, 'woe bronze dragon1 event 2');
INSERT INTO `creature_text` VALUES (57107, 2, 0, 'Heroes! We have been sent by Nozdormu! Quickly, on our backs, we must get you to the Dragon Soul!', 14, 0, 100, 0, 0, 26032, 0, 55914, 0, 'woe bronze dragon1 event 3');
INSERT INTO `creature_text` VALUES (57107, 3, 0, 'The aspects!', 14, 0, 100, 0, 0, 26033, 0, 55916, 0, 'woe bronze dragon1 event 4');
INSERT INTO `creature_text` VALUES (57107, 4, 0, 'The Old Gods protect the Soul!', 14, 0, 100, 0, 0, 26035, 0, 55917, 0, 'woe bronze dragon1 event 5');
INSERT INTO `creature_text` VALUES (57107, 5, 0, 'The link to the portal must be broken! Quickly, to Stormrage!', 14, 0, 100, 0, 0, 26036, 0, 55918, 0, 'woe bronze dragon1 event 6');
INSERT INTO `creature_text` VALUES (57120, 0, 0, 'Oh, I don\'t know. I am old, and I have always...', 12, 0, 100, 0, 0, 0, 0, 55924, 0, '');
INSERT INTO `creature_text` VALUES (57120, 1, 0, 'Look out! Mantid!', 14, 0, 100, 0, 0, 0, 0, 55925, 0, '');
INSERT INTO `creature_text` VALUES (57120, 2, 0, 'I\'ve reconsidered... it\'s too dangerous to stay here. No piece of land is worth losing your life over. Let me see those evacuation orders.', 12, 0, 100, 0, 0, 0, 0, 55952, 0, '');
INSERT INTO `creature_text` VALUES (57121, 0, 0, 'That\'s right. Now please, $r, leave and let us defend our property.', 12, 0, 100, 0, 0, 0, 0, 55931, 0, '');
INSERT INTO `creature_text` VALUES (57122, 0, 0, 'Pshh... outsiders. You know nothing about the Stoneplow way.', 12, 0, 100, 0, 0, 0, 0, 55929, 0, '');
INSERT INTO `creature_text` VALUES (57122, 1, 0, 'We\'re proud. We\'re strong. We are descended from the blood of the Tiller himself... and we don\'t run from trouble.', 12, 0, 100, 0, 0, 0, 0, 55930, 0, '');
INSERT INTO `creature_text` VALUES (57155, 0, 0, 'I feared that this day would come. I will pack up my things.', 12, 0, 100, 0, 0, 0, 0, 55926, 0, '');
INSERT INTO `creature_text` VALUES (57161, 0, 0, 'I see. I\'d hoped that we could wait out this invasion, but if the authorities want us to leave, we will leave.', 12, 0, 100, 0, 0, 0, 0, 55934, 0, '');
INSERT INTO `creature_text` VALUES (57161, 1, 0, 'Ying, Huizhong... gather your things. We\'re leaving the farm until it\'s safe.', 12, 0, 100, 0, 0, 0, 0, 55935, 0, '');
INSERT INTO `creature_text` VALUES (57161, 2, 0, 'I don\'t know, dear.', 12, 0, 100, 0, 0, 0, 0, 55938, 0, '');
INSERT INTO `creature_text` VALUES (57161, 3, 0, 'Are you?', 12, 0, 100, 0, 0, 0, 0, 55939, 0, '');
INSERT INTO `creature_text` VALUES (57162, 0, 0, 'Oh, honey...', 12, 0, 100, 0, 0, 0, 0, 55936, 0, '');
INSERT INTO `creature_text` VALUES (57163, 0, 0, 'Daddy, is this nice person going to kill all the bad mantid soldiers?', 12, 0, 100, 0, 0, 0, 0, 55937, 0, '');
INSERT INTO `creature_text` VALUES (57183, 0, 0, '%s sighs.', 16, 0, 100, 0, 0, 0, 0, 9464, 0, '');
INSERT INTO `creature_text` VALUES (57183, 1, 0, 'I am a farmer, $c. It\'s all I know.', 12, 0, 100, 0, 0, 0, 0, 55943, 0, '');
INSERT INTO `creature_text` VALUES (57183, 2, 0, 'All we have is our land. And that commander wants us to give it up to the mantid?', 12, 0, 100, 0, 0, 0, 0, 55944, 0, '');
INSERT INTO `creature_text` VALUES (57183, 3, 0, 'I will not do it. My grandfather knew what he was getting into when he first planted seed in the shadow of the Serpent\'s Spine.', 12, 0, 100, 0, 0, 0, 0, 55945, 0, '');
INSERT INTO `creature_text` VALUES (57183, 4, 0, 'I have my wife, two strong daughters, and two strong sons. Thank you, but we can protect this farm ourselves.', 12, 0, 100, 0, 0, 0, 0, 55946, 0, '');
INSERT INTO `creature_text` VALUES (57319, 0, 0, 'I keep hearing voices in my head. Should I be worried?', 15, 0, 100, 0, 0, 0, 0, 56762, 0, 'Emulates player');
INSERT INTO `creature_text` VALUES (57319, 0, 1, 'Why do I try so hard? It\'s all meaningless in the end...', 15, 0, 100, 0, 0, 0, 0, 56766, 0, 'Emulates player');
INSERT INTO `creature_text` VALUES (57319, 0, 2, 'Does this armor look a little tight on me?', 15, 0, 100, 0, 0, 0, 0, 56759, 0, 'Emulates player');
INSERT INTO `creature_text` VALUES (57319, 0, 3, 'Everyone around me only pretends to like me.', 15, 0, 100, 0, 0, 0, 0, 56758, 0, 'Emulates player');
INSERT INTO `creature_text` VALUES (57319, 0, 4, 'Nothing is ever going to change.', 15, 0, 100, 0, 0, 0, 0, 56763, 0, 'Emulates player');
INSERT INTO `creature_text` VALUES (57319, 0, 5, 'Of course my worst fears are going to come true.', 15, 0, 100, 0, 0, 0, 0, 56760, 0, 'Emulates player');
INSERT INTO `creature_text` VALUES (57319, 0, 6, 'What are people saying about me?', 15, 0, 100, 0, 0, 0, 0, 56761, 0, 'Emulates player');
INSERT INTO `creature_text` VALUES (57319, 0, 7, 'Why do I always feel so alone? Is it because...I\'m alone?', 15, 0, 100, 0, 0, 0, 0, 56767, 0, 'Emulates player');
INSERT INTO `creature_text` VALUES (57802, 0, 0, 'Assassin, eh? Who sent you? Speak, or I will carve the answer from your skull!', 12, 0, 100, 0, 0, 0, 0, 56520, 0, '');
INSERT INTO `creature_text` VALUES (57802, 1, 0, 'I tire of this disguise. Now we finish this!', 12, 0, 100, 0, 0, 0, 0, 56534, 0, '');
INSERT INTO `creature_text` VALUES (57802, 2, 0, 'Who... ordered... this?', 12, 0, 100, 0, 0, 0, 0, 56536, 0, '');
INSERT INTO `creature_text` VALUES (58202, 0, 0, 'Help!', 14, 0, 100, 0, 0, 0, 0, 485, 0, 'Serpent\'s Spine Defender');
INSERT INTO `creature_text` VALUES (58202, 0, 1, 'Mmmphhh', 14, 0, 100, 0, 0, 0, 0, 66519, 0, 'Serpent\'s Spine Defender');
INSERT INTO `creature_text` VALUES (58202, 0, 2, 'The mantid are coming over the wall!', 14, 0, 100, 0, 0, 0, 0, 66521, 0, 'Serpent\'s Spine Defender');
INSERT INTO `creature_text` VALUES (58632, 0, 0, 'Ah-hah! Another chance to test my might.', 14, 0, 100, 0, 0, 29428, 0, 65557, 0, 'Harlan Intro');
INSERT INTO `creature_text` VALUES (58632, 1, 0, 'Bested... by the likes of...', 14, 0, 100, 0, 0, 29427, 0, 65560, 0, '');
INSERT INTO `creature_text` VALUES (58632, 2, 0, 'Hah-HAH!', 14, 0, 100, 0, 0, 29429, 0, 65561, 0, '');
INSERT INTO `creature_text` VALUES (58632, 3, 0, 'Tsk! You need more training.', 14, 0, 100, 0, 0, 29430, 0, 65562, 0, '');
INSERT INTO `creature_text` VALUES (58632, 4, 0, 'On your guard!', 14, 0, 100, 0, 0, 29426, 0, 33752, 0, '');
INSERT INTO `creature_text` VALUES (58633, 0, 0, 'Class is now in session.', 14, 0, 100, 0, 0, 29460, 0, 61513, 0, 'CHILLHEART_INTRO_1');
INSERT INTO `creature_text` VALUES (58633, 1, 0, 'Class dismissed.', 14, 0, 100, 0, 0, 29455, 0, 58415, 0, 'CHILLHEART_AGGRO');
INSERT INTO `creature_text` VALUES (58633, 2, 0, 'Your soul cannot withstand my power!', 14, 0, 100, 0, 0, 29457, 0, 61514, 0, 'CHILLHEART_WRACK_SOUL');
INSERT INTO `creature_text` VALUES (58633, 3, 0, 'I can never die!', 14, 0, 100, 0, 0, 29458, 0, 61515, 0, 'CHILLHEART_FAKE_DEATH');
INSERT INTO `creature_text` VALUES (58633, 4, 0, 'THIS CANNOT BE!', 14, 0, 100, 0, 0, 29456, 0, 36154, 0, 'CHILLHEART_DEATH');
INSERT INTO `creature_text` VALUES (58633, 5, 0, 'A deadly wave of ice begins to sweep across the room!', 14, 0, 100, 0, 0, 0, 0, 59295, 0, 'TALK_WAVE_OF_ICE');
INSERT INTO `creature_text` VALUES (58633, 6, 0, 'Instructor Chillheart\'s soul escapes to her phylactery. Destroy it!', 14, 0, 100, 0, 0, 0, 0, 59296, 0, 'TALK_ESCAPE');
INSERT INTO `creature_text` VALUES (58740, 0, 0, 'The storm is starting! Get ready.', 14, 0, 100, 5, 0, 0, 0, 58142, 0, 'Blacnhe - Intro');
INSERT INTO `creature_text` VALUES (58740, 1, 0, 'If the brewkegs catch fire, I need you to help put them out.', 14, 0, 100, 1, 0, 0, 0, 58143, 0, 'Blanche - About kegs');
INSERT INTO `creature_text` VALUES (58740, 2, 0, 'Watch out for lightning strikes!', 14, 0, 100, 5, 0, 0, 0, 58144, 0, 'Blanche - About lightning');
INSERT INTO `creature_text` VALUES (58740, 3, 0, '...Saurok? What are they doing here? Keep them away!', 14, 0, 100, 5, 0, 0, 0, 58145, 0, 'Blanche - About Sauroks');
INSERT INTO `creature_text` VALUES (58740, 4, 0, 'Yow, the storm is more intense than I thought! Be careful!', 14, 0, 100, 5, 0, 0, 0, 58146, 0, 'Blanche - On 50 pct progress bar');
INSERT INTO `creature_text` VALUES (58740, 5, 0, 'Stand in Blanche\'s Elixir of Replenishment to restore health and mana!', 41, 0, 100, 0, 0, 0, 0, 61946, 0, '');
INSERT INTO `creature_text` VALUES (58740, 6, 0, 'Stay on your toes!', 14, 0, 100, 0, 0, 0, 0, 59117, 0, 'Blanche - Hit lightning on player');
INSERT INTO `creature_text` VALUES (58740, 7, 0, 'You did it! Let\'s get this brew to the Monastery...', 14, 0, 100, 4, 0, 0, 0, 57811, 0, 'Blanche - First Chapter Done');
INSERT INTO `creature_text` VALUES (58740, 8, 0, 'Rest up for a second while I prep the keg.', 14, 0, 100, 1, 0, 0, 0, 58238, 0, 'Blanche - First Chapter Outro');
INSERT INTO `creature_text` VALUES (58740, 9, 0, 'Everyone ready? Let\'s roll!', 14, 0, 100, 1, 0, 0, 0, 58239, 0, 'Blanche - Second Chapter Begun');
INSERT INTO `creature_text` VALUES (58740, 10, 0, 'Ho boy, more saurok. They always seek high ground when the rains hit!', 14, 0, 100, 5, 0, 0, 0, 58240, 0, 'Blanche - At down of cliff');
INSERT INTO `creature_text` VALUES (58740, 11, 0, 'Don\'t you die on me!', 14, 0, 100, 0, 0, 0, 0, 59119, 0, 'Blanche - At low hp players');
INSERT INTO `creature_text` VALUES (58740, 12, 0, 'They\'re scaling the cliff walls! This is going to get dirty.', 14, 0, 100, 1, 0, 0, 0, 58250, 0, 'Blacnhe - At cliff');
INSERT INTO `creature_text` VALUES (58740, 13, 0, 'Watch your footing! The stones are still slick from rain.', 14, 0, 100, 1, 0, 0, 0, 58252, 0, 'Blanche - About wall cliff');
INSERT INTO `creature_text` VALUES (58740, 14, 0, 'Keep an eye on the rear! No - not MY rear. Behind us!', 14, 0, 100, 5, 0, 0, 0, 58253, 0, 'Blanche - Joke');
INSERT INTO `creature_text` VALUES (58740, 15, 0, 'Phew! I need a breather. Let\'s rest here a momen- wha?', 14, 0, 100, 1, 0, 0, 0, 59312, 0, 'Blanche - Breathing');
INSERT INTO `creature_text` VALUES (58740, 16, 0, 'No time to catch our breath. Quickly! We\'ve got to help them!', 14, 0, 100, 25, 0, 0, 0, 59314, 0, 'Blanche - Ready to go');
INSERT INTO `creature_text` VALUES (58740, 17, 0, 'Let me know when you\'re ready to go inside...', 14, 0, 100, 1, 0, 0, 0, 59352, 0, 'Blanche - Chapter Three Intro');
INSERT INTO `creature_text` VALUES (58740, 18, 0, 'Let\'s go be heroes!', 14, 0, 100, 4, 0, 0, 0, 59353, 0, 'Blanche - Chapter Three Begun');
INSERT INTO `creature_text` VALUES (58740, 19, 0, 'Use the Boomer Brew to zap saurok minions!', 41, 0, 100, 0, 0, 0, 0, 59400, 0, '');
INSERT INTO `creature_text` VALUES (58740, 20, 0, 'Last call, you fork-tongued dip-slithers!', 14, 0, 100, 15, 0, 0, 0, 59354, 0, 'Blanche - On Borokhala Intro');
INSERT INTO `creature_text` VALUES (58740, 21, 0, 'You don\'t have to go home, but you can\'t stay here.', 14, 0, 100, 25, 0, 0, 0, 59355, 0, 'Blanche - On 70% of Borokhala');
INSERT INTO `creature_text` VALUES (58740, 22, 0, 'You did it! Thunderpaw Refuge is saved!', 14, 0, 100, 4, 0, 0, 0, 59402, 0, 'Blanche - On Scenario End');
INSERT INTO `creature_text` VALUES (58777, 0, 0, 'The ships are aflame! Troops, to the harbor!', 14, 0, 100, 0, 0, 30565, 0, 64859, 0, '');
INSERT INTO `creature_text` VALUES (58777, 1, 0, 'Avoid or destroy Baldruc\'s Storm Totems.', 41, 0, 100, 0, 0, 0, 0, 58811, 0, '');
INSERT INTO `creature_text` VALUES (58787, 0, 0, 'Don\'t get caught in Big Bessa\'s sights.', 41, 0, 100, 0, 0, 0, 0, 58810, 0, '');
INSERT INTO `creature_text` VALUES (58816, 0, 0, 'Ah, it\'s about time. Will you free me, or did you have... other ideas?', 12, 0, 100, 1, 0, 31474, 0, 57576, 0, '');
INSERT INTO `creature_text` VALUES (58816, 1, 0, 'The Captain of the Guard upstairs, Hedric Evencane, should posses a key. Please, make haste. We do not have all night, you know!', 12, 0, 100, 1, 0, 31475, 0, 57581, 0, '');
INSERT INTO `creature_text` VALUES (58816, 2, 0, 'My thanks! Shall we make our way off this miserable little island?', 12, 0, 100, 0, 0, 31477, 0, 57597, 0, '');
INSERT INTO `creature_text` VALUES (58816, 3, 0, 'The \"failed\" siege was merely a feint, of course. You didn\'t realize?', 12, 0, 100, 0, 0, 31478, 0, 57658, 0, '');
INSERT INTO `creature_text` VALUES (58816, 4, 0, 'Even now an airship draws near; its arcane payload will flatten Theramore Isle and everyone still standing upon it.', 12, 0, 100, 0, 0, 31479, 0, 64887, 0, '');
INSERT INTO `creature_text` VALUES (58816, 5, 0, 'There we go. Should you pass through Silvermoon City, come and say hello, hmmm? Al diel shala...', 12, 0, 100, 0, 0, 31480, 0, 64890, 0, '');
INSERT INTO `creature_text` VALUES (58826, 0, 0, 'I can allow this no longer!', 14, 0, 100, 0, 0, 27747, 0, 57748, 0, 'Zao Sunseeker');
INSERT INTO `creature_text` VALUES (58826, 1, 0, 'The blistering heat must have dulled my senses... mirages! All of you!', 14, 0, 100, 0, 0, 27748, 0, 57623, 0, 'Zao Sunseeker');
INSERT INTO `creature_text` VALUES (58840, 0, 0, '%s draws his sword and shield.', 41, 0, 100, 0, 0, 0, 0, 58795, 0, '');
INSERT INTO `creature_text` VALUES (58840, 1, 0, '%s drops his shield and draws a dagger.', 41, 0, 100, 0, 0, 0, 0, 58797, 0, '');
INSERT INTO `creature_text` VALUES (58870, 0, 0, 'The unthinkable has happened: Our siege on Theramore has failed.', 12, 0, 100, 5, 0, 30642, 0, 64678, 0, '');
INSERT INTO `creature_text` VALUES (58870, 1, 0, 'You\'re the contingency plan. Your mission is to infiltrate Theramore Isle.', 12, 0, 100, 1, 0, 30643, 0, 64684, 0, '');
INSERT INTO `creature_text` VALUES (58870, 2, 0, 'Burn their ships. Butcher their flight master. Sabotage their war machines. And rescue our spy!', 12, 0, 100, 25, 0, 30644, 0, 64826, 0, '');
INSERT INTO `creature_text` VALUES (58875, 0, 0, 'My dear, it appears we have visitors!  Friends of yours, Voss?', 14, 0, 100, 0, 0, 27479, 0, 58193, 0, 'GANDLING_TALK_1');
INSERT INTO `creature_text` VALUES (58875, 1, 0, 'Their deaths only serve to empower my summoners further! Behold!', 14, 0, 100, 0, 0, 27480, 0, 58194, 0, 'GANDLING_TALK_2');
INSERT INTO `creature_text` VALUES (58875, 2, 0, 'What\'s the matter, Voss, getting tired?', 14, 0, 100, 0, 0, 27481, 0, 58195, 0, 'GANDLING_TALK_3');
INSERT INTO `creature_text` VALUES (58875, 3, 0, 'Isn\'t it beautiful? The weaving of flesh and bone and soul.', 14, 0, 100, 0, 0, 27482, 0, 58196, 0, 'GANDLING_TALK_4');
INSERT INTO `creature_text` VALUES (58875, 4, 0, 'You, too, are beautiful, Voss. Have you looked in a mirror recently?', 14, 0, 100, 0, 0, 27483, 0, 58197, 0, 'GANDLING_TALK_5');
INSERT INTO `creature_text` VALUES (58875, 5, 0, 'My dear, you should embrace this gift that has been bestowed upon you.', 14, 0, 100, 0, 0, 27484, 0, 58198, 0, 'GANDLING_TALK_6');
INSERT INTO `creature_text` VALUES (58875, 6, 0, 'Come, Voss, your fate awaits you.', 14, 0, 100, 0, 0, 27485, 0, 58199, 0, 'GANDLING_TALK_7');
INSERT INTO `creature_text` VALUES (59074, 0, 0, 'Yes yes yes! So close! Just a pinch of...oops...', 14, 0, 100, 0, 0, 28290, 0, 59302, 0, 'Uncle Gao to Player');
INSERT INTO `creature_text` VALUES (59074, 1, 0, 'Too bitter! Just a drop of honey, and a cane of sugar, and maybe some corn? Yes, more corn!', 14, 0, 100, 0, 0, 28291, 0, 59303, 0, 'Uncle Gao');
INSERT INTO `creature_text` VALUES (59074, 2, 0, 'Yes, yes, yes...no, no, no no no! Yes! No! Peppers!', 14, 0, 100, 0, 0, 28292, 0, 59304, 0, 'Uncle Gao');
INSERT INTO `creature_text` VALUES (59074, 3, 0, 'Ahh! Help! What is that thing!? It doesn\'t matter, nothing will stop me now, not when I\'m so close! Maybe if I don\'t look at it...?', 14, 0, 100, 0, 0, 28289, 0, 59301, 0, 'Uncle Gao');
INSERT INTO `creature_text` VALUES (59074, 4, 0, 'Is it...can it be???', 12, 0, 100, 0, 0, 28293, 0, 59305, 0, 'Uncle Gao to Player');
INSERT INTO `creature_text` VALUES (59074, 5, 0, 'Such harmony of flavor, such heady aroma! It is...the perfect brew!', 12, 0, 100, 0, 0, 28294, 0, 59306, 0, 'Uncle Gao to Player');
INSERT INTO `creature_text` VALUES (59074, 6, 0, 'The name of Stormstout will be sung once again throughout the hills!', 14, 0, 100, 0, 0, 28295, 0, 59307, 0, 'Uncle Gao to Player');
INSERT INTO `creature_text` VALUES (59074, 7, 0, 'You again! Don\'t you see? I have made a name for myself at last: I have brewed perfection!', 14, 0, 100, 0, 0, 31999, 0, 59308, 0, 'Uncle Gao to Player');
INSERT INTO `creature_text` VALUES (59074, 8, 0, 'Details, details.', 12, 0, 100, 0, 0, 32000, 0, 61472, 0, 'Uncle Gao to Player');
INSERT INTO `creature_text` VALUES (59074, 9, 0, 'Look, \"Chen Stormstout,\" we all can\'t be heroes, running from our responsibilities, tromping around the Dread Wastes, saving the world. Some of us are \"Artists.\"', 12, 0, 100, 0, 0, 32001, 0, 61473, 0, 'Uncle Gao to Player');
INSERT INTO `creature_text` VALUES (59074, 10, 0, 'Yes, yes. That happens.', 12, 0, 100, 0, 0, 32002, 0, 61474, 0, 'Uncle Gao to Player');
INSERT INTO `creature_text` VALUES (59080, 0, 0, 'School is in session!', 14, 0, 100, 0, 0, 27477, 0, 7145, 0, 'grandmaster gandling - TALK_AGGRO');
INSERT INTO `creature_text` VALUES (59080, 1, 0, 'Dunce.', 14, 0, 100, 0, 0, 27497, 0, 58100, 0, 'grandmaster gandling - TALK_SLAY');
INSERT INTO `creature_text` VALUES (59080, 2, 0, 'Class... dismissed.', 14, 0, 100, 0, 0, 27478, 0, 58102, 0, 'grandmaster gandling - TALK_DEATH');
INSERT INTO `creature_text` VALUES (59080, 3, 0, '|TInterface\\Icons\\inv_misc_book_01.blp:20|t%s assigns $n a |cFFFF0000|Hspell:113395|h[Harsh Lesson]|h|r!', 41, 0, 100, 0, 0, 0, 0, 58103, 0, 'grandmaster gandling - TALK_HARSH_LESSON');
INSERT INTO `creature_text` VALUES (59150, 0, 0, 'Everything must burn. None shall know of the Scarlet Crusade\'s shame!', 14, 0, 100, 0, 0, 29524, 0, 63259, 0, 'KOEGLER_INTO');
INSERT INTO `creature_text` VALUES (59150, 1, 0, 'You, too, shall be charred to ash!', 14, 0, 100, 0, 0, 29522, 0, 63260, 0, 'KOEGLER_AGGRO');
INSERT INTO `creature_text` VALUES (59150, 2, 0, 'Purged by fire!', 14, 0, 100, 0, 0, 29528, 0, 66472, 0, 'KOEGLER_DRAGON_BREATH');
INSERT INTO `creature_text` VALUES (59150, 4, 0, '|TInterface\\Icons\\inv_misc_head_dragon_01.blp:20|t%s begins to cast |cFFFF0000|Hspell:113653|h[Greater Dragon\'s Breath]|h|r!', 41, 0, 100, 0, 0, 0, 0, 58168, 0, 'KOEGLER_DRAGON_BREATH_ANN');
INSERT INTO `creature_text` VALUES (59150, 3, 0, '|TInterface\\Icons\\ability_warlock_inferno.blp:20|t%s begins to cast |cFFFF0000|Hspell:113364|h[Book Burner]|h|r!', 41, 0, 100, 0, 0, 0, 0, 58133, 0, 'KOEGLER_BOOK_BURNER_ANN');
INSERT INTO `creature_text` VALUES (59150, 5, 0, 'My fire... has gone out..', 14, 0, 100, 0, 0, 29523, 0, 63261, 0, 'KOEGLER_DEATH');
INSERT INTO `creature_text` VALUES (59153, 0, 0, 'RATTLEGORE!', 14, 0, 100, 0, 0, 0, 0, 58323, 0, 'rattlegore - TALK_SUMMONED');
INSERT INTO `creature_text` VALUES (59153, 1, 0, 'Click on a Bone Pile to protect yourself from Bone Spike!', 41, 0, 100, 0, 0, 0, 0, 59449, 0, 'rattlegore - TALK_BONE_PILE_ANN');
INSERT INTO `creature_text` VALUES (59163, 1, 0, 'Follow and use the shield as cover!', 41, 0, 100, 0, 0, 0, 0, 58228, 0, 'INSTANCE_REINFORCEMENT_ANN');
INSERT INTO `creature_text` VALUES (59184, 0, 0, 'Ooh, it takes some real stones to challenge the Mistress of Illusion. Well? Show me what you\'ve got!', 14, 0, 100, 0, 0, 30287, 0, 61034, 0, 'JANDICE_AGGRO');
INSERT INTO `creature_text` VALUES (59184, 1, 0, 'Ugh... death... hurts? Unreal...', 14, 0, 100, 0, 0, 30288, 0, 61039, 0, 'JANDICE_DEATH');
INSERT INTO `creature_text` VALUES (59184, 2, 0, 'Come, try your luck! Ha ha haaa...', 14, 0, 100, 0, 0, 30292, 0, 61041, 0, 'JANDICE_RAPIDITY');
INSERT INTO `creature_text` VALUES (59184, 3, 0, 'Feeling a bit... dizzy?', 14, 0, 100, 0, 0, 30293, 0, 61046, 0, 'JANDICE_ILLUSION');
INSERT INTO `creature_text` VALUES (59191, 1, 0, 'Keep firing! Let none escape!', 12, 0, 100, 0, 0, 29437, 0, 58174, 0, 'LINDON_RANGE');
INSERT INTO `creature_text` VALUES (59191, 2, 0, 'Another down.  Do not relent!', 12, 0, 100, 0, 0, 29438, 0, 58175, 0, 'LINDON_FIRE_ARROWS');
INSERT INTO `creature_text` VALUES (59191, 3, 0, 'Blades at the ready men!', 12, 0, 100, 0, 0, 0, 0, 58181, 0, 'LINDON_MELEE');
INSERT INTO `creature_text` VALUES (59200, 0, 0, 'I...won\'t let you...', 14, 0, 100, 0, 0, 29464, 0, 58423, 0, 'LILIAN_TALK_1');
INSERT INTO `creature_text` VALUES (59200, 1, 0, 'I can\'t...fight him...', 14, 0, 100, 0, 0, 29465, 0, 58424, 0, 'LILIAN_TALK_2');
INSERT INTO `creature_text` VALUES (59200, 2, 0, 'It burns...my soul...it burns!!!!', 14, 0, 100, 0, 0, 29468, 0, 58492, 0, 'LILIAN_TALK_3');
INSERT INTO `creature_text` VALUES (59200, 3, 0, 'END IT! NOW! Before he can...', 14, 0, 100, 0, 0, 29463, 0, 58425, 0, 'LILIAN_TALK_4');
INSERT INTO `creature_text` VALUES (59200, 4, 0, 'DIE, NECROMANCER!', 14, 0, 100, 0, 0, 29466, 0, 58527, 0, 'LILIAN_TALK_5');
INSERT INTO `creature_text` VALUES (59200, 5, 0, 'Leave me...to die...alone...please...', 14, 0, 100, 0, 0, 29467, 0, 58528, 0, 'LILIAN_TALK_6');
INSERT INTO `creature_text` VALUES (59200, 6, 0, 'You there!  Make yourselves useful and deal with these summoners!', 12, 0, 100, 0, 0, 29469, 0, 58148, 0, 'LILIAN_TALK_1');
INSERT INTO `creature_text` VALUES (59200, 7, 0, 'You feed their souls to their brethren?  You are sick.', 12, 0, 100, 0, 0, 29470, 0, 58200, 0, 'LILIAN_TALK_2');
INSERT INTO `creature_text` VALUES (59200, 8, 0, 'I\'ll never stop hunting your kind, necromancer.  NEVER.', 12, 0, 100, 0, 0, 29471, 0, 58201, 0, 'LILIAN_TALK_3');
INSERT INTO `creature_text` VALUES (59200, 9, 0, 'We\'ll see if you revel so much in death once I\'ve ripped out your throat!', 12, 0, 100, 0, 0, 29472, 0, 58202, 0, 'LILIAN_TALK_4');
INSERT INTO `creature_text` VALUES (59200, 10, 0, 'You\'ll pay for your tongue.', 12, 0, 100, 0, 0, 29473, 0, 58211, 0, 'LILIAN_TALK_6');
INSERT INTO `creature_text` VALUES (59200, 11, 0, 'Run...necromancer...your life ends once I catch my breath.', 12, 0, 100, 0, 0, 29475, 0, 58213, 0, 'LILIAN_TALK_7');
INSERT INTO `creature_text` VALUES (59223, 0, 0, 'I will break you.', 14, 0, 100, 0, 0, 29496, 0, 63297, 0, 'Korloff_AGGRO');
INSERT INTO `creature_text` VALUES (59223, 1, 0, 'You are like... piece of iron...', 14, 0, 100, 0, 0, 29497, 0, 66586, 0, 'Korloff_DEATH');
INSERT INTO `creature_text` VALUES (59223, 2, 0, 'If you die, you die.', 14, 0, 100, 0, 0, 29500, 0, 63300, 0, 'Korloff_firestom');
INSERT INTO `creature_text` VALUES (59223, 3, 0, 'My fists are scarlet... with your blood!', 14, 0, 100, 0, 0, 29499, 0, 63299, 0, 'Korloff_Fists');
INSERT INTO `creature_text` VALUES (59303, 0, 0, 'Ah, fresh meat for the dogs.', 12, 0, 100, 0, 0, 27550, 0, 58608, 0, '');
INSERT INTO `creature_text` VALUES (59303, 1, 0, 'I bleed... haha... but I won\'t die!', 12, 0, 100, 0, 0, 27544, 0, 58592, 0, '');
INSERT INTO `creature_text` VALUES (59303, 2, 0, 'What? No, no! Stop!  Obey your master!', 12, 0, 100, 0, 0, 27545, 0, 59135, 0, '');
INSERT INTO `creature_text` VALUES (59303, 3, 0, 'I let the dogs out!', 12, 0, 100, 0, 0, 27551, 0, 66219, 0, '');
INSERT INTO `creature_text` VALUES (59303, 4, 0, 'Stupid animals!  I\'ll put you down after I kill these weaklings.', 12, 0, 100, 0, 0, 27543, 0, 58491, 0, '');
INSERT INTO `creature_text` VALUES (59368, 0, 0, 'Blood blood blood BLOOD BLOOD!!', 12, 0, 100, 0, 0, 27752, 0, 58362, 0, '');
INSERT INTO `creature_text` VALUES (59368, 0, 1, 'More blood!', 12, 0, 100, 0, 0, 27754, 0, 58364, 0, '');
INSERT INTO `creature_text` VALUES (59368, 0, 2, 'Yes! Yes! More blood!', 12, 0, 100, 0, 0, 27751, 0, 58361, 0, '');
INSERT INTO `creature_text` VALUES (59368, 1, 0, 'The scent of blood...so delicious!', 12, 0, 100, 0, 0, 27755, 0, 58365, 0, '');
INSERT INTO `creature_text` VALUES (59483, 0, 0, 'Fool! I will crush you!', 14, 0, 100, 0, 0, 0, 0, 58631, 0, '');
INSERT INTO `creature_text` VALUES (59483, 7, 0, 'You have been affected by |cFFFF0404|Hspell:116784|h[Wildfire Spark]|h|r !', 41, 0, 100, 0, 0, 0, 0, 61728, 0, '');
INSERT INTO `creature_text` VALUES (59483, 8, 0, '%s begins to channel a violent |cFFFF0404|Hspell:116040|h[Epicenter]|h|r!', 41, 0, 100, 0, 0, 0, 0, 59714, 0, '');
INSERT INTO `creature_text` VALUES (59483, 9, 0, '%s begins to |cFFFF0404|Hspell:116711|h[Draw Flame]|h|r to his weapon!', 41, 0, 100, 0, 0, 0, 0, 59886, 0, '');
INSERT INTO `creature_text` VALUES (59642, 0, 0, 'Keep those creatures at bay while I meditate. We\'ll soon have the answers we seek.', 12, 0, 100, 0, 0, 27398, 0, 59161, 0, '');
INSERT INTO `creature_text` VALUES (59642, 1, 0, 'And so our path lays before us. Speak to Master Shang Xi, he will tell you what comes next.', 12, 0, 100, 0, 0, 27399, 0, 60046, 0, '');
INSERT INTO `creature_text` VALUES (56660, 6, 0, 'And those aren\'t pandaren down there.  They\'ve got no fur.', 12, 0, 100, 5, 0, 27303, 0, 55579, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (56660, 5, 0, 'Are you seeing what I\'m seeing?!  Is that a boat?!', 12, 0, 100, 5, 0, 27302, 0, 55577, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (56660, 3, 0, 'A thorn?  And I left my tweezers at home.', 12, 0, 100, 6, 0, 27300, 0, 55574, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (56660, 4, 0, 'How could such a thing cause pain to something so large?', 12, 0, 100, 6, 0, 27301, 0, 55575, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (56660, 2, 0, 'When am I not respectful?  You hurt me, Aysa.', 12, 0, 100, 6, 0, 27299, 0, 55561, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (56660, 1, 0, 'Bah, let a pandaren hope, would you?  I\'m going to miss the old man.', 12, 0, 100, 1, 0, 27298, 0, 55556, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (56660, 0, 0, '$p, where\'s Master Shang?', 12, 0, 100, 6, 0, 27297, 0, 55554, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (56663, 0, 0, 'Or if you want to wander the woods a while, Aysa and I can hang out here.  You know... just the two of us.', 12, 0, 100, 1, 0, 27296, 0, 55552, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (56662, 1, 0, 'Hop in.', 12, 0, 100, 1, 0, 27429, 0, 55551, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (56662, 2, 0, 'Don\'t listen to him.  We\'ve got work to do.', 12, 0, 100, 1, 0, 27430, 0, 55553, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (56662, 0, 0, 'We\'re ready to go whenever you are, $n.', 12, 0, 100, 1, 0, 27428, 0, 55549, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (59704, 0, 0, 'I am not Zan - I am Chen Stormstout!', 12, 0, 100, 0, 0, 29766, 0, 59246, 0, 'Chen Stormstout to Player');
INSERT INTO `creature_text` VALUES (59704, 1, 0, 'Tell me, what has happened here?', 12, 0, 100, 0, 0, 29767, 0, 59247, 0, 'Chen Stormstout to Player');
INSERT INTO `creature_text` VALUES (59704, 2, 0, 'Where are the other Stormstouts? Why are hozen all over the brewery?', 12, 0, 100, 0, 0, 29768, 0, 59248, 0, 'Chen #4');
INSERT INTO `creature_text` VALUES (59704, 3, 0, 'Auntie Stormstout...why is the brewery abandoned?', 12, 0, 100, 0, 0, 29769, 0, 59249, 0, 'Chen #5');
INSERT INTO `creature_text` VALUES (59704, 4, 0, 'I see - and where is Uncle Gao?', 12, 0, 100, 0, 0, 29770, 0, 61475, 0, 'Chen #6');
INSERT INTO `creature_text` VALUES (59704, 5, 0, 'There is no time for cookies! Well, one cookie. Just one.', 12, 0, 100, 0, 0, 29771, 0, 61476, 0, 'Chen #7');
INSERT INTO `creature_text` VALUES (59704, 6, 0, 'Wait - these are ghost cookies. These aren\'t filling at all!', 12, 0, 100, 0, 0, 29772, 0, 61477, 0, 'Chen #8');
INSERT INTO `creature_text` VALUES (59751, 0, 0, 'Brothers! Dispose of these insolent outsiders!', 14, 0, 100, 0, 0, 27746, 0, 59329, 0, 'Shado-Pan Warden');
INSERT INTO `creature_text` VALUES (59751, 0, 1, 'The Shado-Pan need no outside aid! Remove these interlopers!', 14, 0, 100, 0, 0, 27745, 0, 66236, 0, 'Shado-Pan Warden');
INSERT INTO `creature_text` VALUES (59789, 0, 0, 'My endless agony shall be yours, as well!', 14, 0, 100, 396, 0, 27832, 0, 63265, 0, 'Thalnos_INTRO');
INSERT INTO `creature_text` VALUES (59789, 1, 0, 'No rest... for the angry dead!', 14, 0, 100, 396, 0, 27831, 0, 6202, 0, 'Thanlos_SUMMON');
INSERT INTO `creature_text` VALUES (59789, 2, 0, 'Seek out a vessel... and return!', 14, 0, 100, 396, 0, 27834, 0, 63269, 0, 'Thanlos_EVIC');
INSERT INTO `creature_text` VALUES (59789, 3, 0, 'Claim a body, and wreak terrible vengeance!', 14, 0, 100, 396, 0, 27835, 0, 63270, 0, 'Thalnos_spirits');
INSERT INTO `creature_text` VALUES (59789, 4, 0, 'Can this be... the end, at last...?', 14, 0, 100, 396, 0, 27830, 0, 63267, 0, 'Thalnos_dead');
INSERT INTO `creature_text` VALUES (59789, 6, 0, '|TInterface\\Icons\\ability_warlock_jinx.blp:20|t%s begins to cast |cFFFF0000|Hspell:115147|h[Empowering Spirits]|h|r!', 41, 0, 100, 0, 0, 0, 0, 59491, 0, 'THALNOS_EMPOWERED_SPIRIT_ANN');
INSERT INTO `creature_text` VALUES (59789, 5, 0, '|TInterface\\Icons\\spell_warlock_demonsoul.blp:20|t%s casts |cFFFF0000|Hspell:115297|h[Evict Soul]|h|r on |cFF006EFD$n|r!', 41, 0, 100, 0, 0, 0, 0, 59541, 0, 'THALNOS_EVICTED_SOUL_ANN');
INSERT INTO `creature_text` VALUES (59822, 0, 0, 'Oh, hello Zan, it is good of you to visit ol\' Auntie Stormstout.', 12, 0, 100, 0, 0, 28528, 0, 61480, 0, 'Auntie Stormstout to Player');
INSERT INTO `creature_text` VALUES (59822, 1, 0, 'Oh Zan! You remind me so much of your father.', 12, 0, 100, 0, 0, 28529, 0, 61481, 0, 'Auntie Stormstout to Player');
INSERT INTO `creature_text` VALUES (59822, 2, 0, 'It is certainly a nice day outside!', 12, 0, 100, 0, 0, 28530, 0, 61482, 0, 'Auntie Stormstout to Player');
INSERT INTO `creature_text` VALUES (59822, 3, 0, 'Have you seen the size of Esme\'s turnips?', 12, 0, 100, 0, 0, 28531, 0, 61483, 0, 'Auntie #4');
INSERT INTO `creature_text` VALUES (59822, 4, 0, 'Abandoned? Oh heavens no! Uncle Gao is in charge while the others are beyond the wall. Isn\'t that exciting?', 12, 0, 100, 0, 0, 28532, 0, 61484, 0, 'Auntie #5');
INSERT INTO `creature_text` VALUES (59822, 5, 0, 'I have some cookies for you!', 12, 0, 100, 0, 0, 28533, 0, 61485, 0, 'Auntie #6');
INSERT INTO `creature_text` VALUES (59915, 0, 0, '|TInterface\\Icons\\inv_misc_qirajicrystal_02.blp:20|t |c00F19C88%s|r begins to petrify all players!', 41, 0, 100, 0, 0, 0, 0, 59700, 0, '');
INSERT INTO `creature_text` VALUES (59986, 0, 0, '$n\'s here. Let\'s go.', 12, 0, 100, 432, 0, 27420, 0, 59481, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (59986, 1, 0, 'Wha...', 12, 0, 100, 432, 0, 27421, 0, 59495, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (59986, 2, 0, 'The gate is jammed.', 12, 0, 100, 274, 0, 27422, 0, 59511, 0, 'Aysa Cloudsinger');
INSERT INTO `creature_text` VALUES (59988, 0, 0, 'Let me try.', 12, 0, 100, 5, 0, 27339, 0, 59513, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (59988, 1, 0, 'Did they prop this door up against a boulder?  It won\'t budge!', 12, 0, 100, 5, 0, 27340, 0, 59515, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (59988, 2, 0, 'Well done, Jojo!', 12, 0, 100, 0, 0, 27341, 0, 59521, 0, 'Ji Firepaw');
INSERT INTO `creature_text` VALUES (60009, 0, 0, 'Tender your souls, mortals! These are the halls of the dead!', 14, 0, 100, 0, 0, 27814, 0, 61068, 0, 'FENG_AGGRO');
INSERT INTO `creature_text` VALUES (60009, 1, 0, 'Oh great spirit! Grant me the power of the earth!', 14, 0, 100, 0, 0, 27816, 0, 61096, 0, 'FENG_PHASE_1');
