INSERT INTO `creature_text` VALUES (60999, 4, 0, 'You think it is so easy to vanquish your fears?', 14, 0, 100, 0, 0, 28400, 0, 63311, 0, 'Sha of Fear - SAY_PHASE_2');
INSERT INTO `creature_text` VALUES (60999, 5, 0, 'Dread rises! It seeks the light. It hungers.', 14, 0, 100, 0, 0, 28401, 0, 63312, 0, 'Sha of Fear - SAY_SUBMERGE');
INSERT INTO `creature_text` VALUES (60999, 6, 0, 'Huddle in terror!', 14, 0, 100, 0, 0, 28405, 0, 63310, 0, 'Sha of Fear - SAY_HUDDLE');
INSERT INTO `creature_text` VALUES (61055, 0, 0, 'Your pitiful rituals are meaningless! I will not be saved!', 14, 0, 100, 0, 0, 31309, 0, 60644, 0, '');
INSERT INTO `creature_text` VALUES (61055, 1, 0, 'Has Ban sent you to kill me, then? Just like he killed my husband?', 12, 0, 100, 0, 0, 31308, 0, 60643, 0, '');
INSERT INTO `creature_text` VALUES (61055, 2, 0, 'I see now... my hatred... consumed me. Tell Ban... all is... forgiven.', 12, 0, 100, 0, 0, 31311, 0, 60646, 0, '');
INSERT INTO `creature_text` VALUES (61140, 0, 0, 'Bah! Bested by de likes of you! What a shame for a Zandalari Priest ta endure.', 14, 0, 100, 0, 0, 29082, 0, 60653, 0, 'GARAJAL_DEATH_1');
INSERT INTO `creature_text` VALUES (61140, 0, 1, 'But it ain\'t no ting. Take your trinkets; you\'ll be needin\' dem.', 14, 0, 100, 0, 0, 29083, 0, 60654, 0, 'GARAJAL_DEATH_2');
INSERT INTO `creature_text` VALUES (61140, 0, 2, 'I be goin\' now; time ta find a new home for me soul.', 14, 0, 100, 0, 0, 29084, 0, 60655, 0, 'GARAJAL_DEATH_3');
INSERT INTO `creature_text` VALUES (61140, 0, 3, 'Death be a doorway, an\' time a window; I\'ll be back! AH-HAHAHAHAHAH!', 14, 0, 100, 0, 0, 29085, 0, 60656, 0, 'GARAJAL_DEATH_4');
INSERT INTO `creature_text` VALUES (61156, 0, 0, 'Flee, craven villagers! This land belongs to nature alone once more!', 14, 0, 100, 0, 0, 0, 0, 60741, 0, '');
INSERT INTO `creature_text` VALUES (61156, 1, 0, 'Vengeful Hui has appeared in the Mason\'s Quarter, north of Greenstone Village. Challenge him!', 41, 0, 100, 0, 0, 0, 0, 60742, 0, '');
INSERT INTO `creature_text` VALUES (61203, 0, 0, 'Many thanks... I couldn\'t have held out much longer.', 12, 0, 100, 0, 0, 0, 0, 60930, 0, '');
INSERT INTO `creature_text` VALUES (61203, 1, 0, 'Alas, I\'m in no condition to go on. If only our entire supply of Greenstone Brew hadn\'t been stolen!', 12, 0, 100, 0, 0, 0, 0, 62401, 0, '');
INSERT INTO `creature_text` VALUES (61203, 2, 0, 'If you can venture into the surrounding forest and return any stolen barrels you find, I\'ll concoct a concentrated draft of marvelous power for you...', 12, 0, 100, 0, 0, 0, 0, 66255, 0, '');
INSERT INTO `creature_text` VALUES (61203, 3, 0, 'An adventurer saves the village, but a HERO returns the brew!', 14, 0, 100, 0, 0, 0, 0, 66253, 0, '');
INSERT INTO `creature_text` VALUES (61203, 3, 1, 'That\'ll teach those pests to pilfer our dojo\'s prized product!', 14, 0, 100, 0, 0, 0, 0, 66251, 0, '');
INSERT INTO `creature_text` VALUES (61203, 3, 2, 'Ah, back where it belongs. Thank you!', 14, 0, 100, 0, 0, 0, 0, 66252, 0, '');
INSERT INTO `creature_text` VALUES (61203, 3, 3, 'A precious barrel of Greenstone brew! Hurrah!', 14, 0, 100, 0, 0, 0, 0, 66250, 0, '');
INSERT INTO `creature_text` VALUES (61203, 4, 0, 'Ah, you have returned! One thousand thanks. Please, take this, our most potent creation: Volatile Greenstone Brew.', 12, 0, 100, 0, 0, 0, 0, 60931, 0, '');
INSERT INTO `creature_text` VALUES (61203, 5, 0, 'You\'ve received Volatile Greenstone Brew! Use it in combat to help defeat multiple enemies.', 41, 0, 100, 0, 0, 0, 0, 60915, 0, '');
INSERT INTO `creature_text` VALUES (61243, 0, 0, 'I hear something...', 14, 0, 0, 0, 0, 32495, 0, 63254, 0, 'Gekkan intro');
INSERT INTO `creature_text` VALUES (61243, 1, 0, 'Search his corpse!', 14, 0, 0, 0, 0, 32496, 0, 63257, 0, 'Gekkan kill target');
INSERT INTO `creature_text` VALUES (61243, 2, 0, 'Slay them!', 14, 0, 0, 0, 0, 32497, 0, 63258, 0, 'Gekkan spell cast');
INSERT INTO `creature_text` VALUES (61243, 3, 0, 'Stop them!', 14, 0, 0, 0, 0, 32488, 0, 13822, 0, 'Gekkan aggro');
INSERT INTO `creature_text` VALUES (61243, 4, 0, 'Such a waste...', 14, 0, 0, 0, 0, 32494, 0, 63256, 0, 'Gekkan death');
INSERT INTO `creature_text` VALUES (61342, 0, 0, 'Away, you fiends! Help!', 14, 0, 100, 20, 0, 0, 0, 60979, 0, '');
INSERT INTO `creature_text` VALUES (61342, 1, 0, 'There\'s hope for Greenstone! Thank you!', 12, 0, 100, 0, 0, 0, 0, 60965, 0, '');
INSERT INTO `creature_text` VALUES (61346, 0, 0, 'Away, you fiends! Help!', 14, 0, 100, 20, 0, 0, 0, 60979, 0, '');
INSERT INTO `creature_text` VALUES (61346, 1, 0, 'Many thanks!', 12, 0, 100, 0, 0, 0, 0, 60684, 0, '');
INSERT INTO `creature_text` VALUES (61348, 0, 0, 'The ancient vaults of the mogu, untouched by time for thousands of years, who knows what secrets await?', 12, 0, 100, 0, 0, 30198, 0, 65168, 0, 'LOREWALKER_CHO_INTRO_1');
INSERT INTO `creature_text` VALUES (61348, 1, 0, 'Many young Pandaren warriors have vanished into this...tomb. Have your wits about you.', 12, 0, 100, 0, 0, 30199, 0, 65170, 0, 'LOREWALKER_CHO_INTRO_2');
INSERT INTO `creature_text` VALUES (61348, 2, 0, 'The large Quilen seem to be guarding that door. Tread carefully, lest you wake them.', 12, 0, 100, 0, 0, 30179, 0, 65172, 0, 'LOREWALKER_CHO_DOGS');
INSERT INTO `creature_text` VALUES (61348, 3, 0, 'That was close!', 14, 0, 100, 0, 0, 30180, 0, 46780, 0, 'LOREWALKER_CHO_DOGS_DIE');
INSERT INTO `creature_text` VALUES (61348, 4, 0, 'The door mechanism seems to have activated with the demise of the guardians. I fear what we may find within.', 12, 0, 100, 0, 0, 30181, 0, 65174, 0, 'LOREWALKER_CHO_DOGS_DOOR_EVENT');
INSERT INTO `creature_text` VALUES (61348, 5, 0, 'We seem to have awakened a spirit of some kind.', 12, 0, 100, 0, 0, 30193, 0, 65175, 0, 'LOREWALKER_CHO_FENG_EVENT_1');
INSERT INTO `creature_text` VALUES (61348, 6, 0, 'So the stories were true...', 12, 0, 100, 0, 0, 30194, 0, 65176, 0, 'LOREWALKER_CHO_FENG_EVENT_2');
INSERT INTO `creature_text` VALUES (61348, 7, 0, 'We have disturbed what should not have been disturbed. He cannot be free to escape this prison! You must destroy him!', 12, 0, 100, 0, 0, 30195, 0, 65177, 0, 'LOREWALKER_CHO_FENG_EVENT_3');
INSERT INTO `creature_text` VALUES (61348, 8, 0, 'Feng the Accursed was betrayed by four emperors of the mogu, his body destroyed each time, only to rise again.', 12, 0, 100, 0, 0, 30196, 0, 65178, 0, 'LOREWALKER_CHO_FENG_PAST_EVENT_1');
INSERT INTO `creature_text` VALUES (61348, 9, 0, 'This is not the last we have seen of Feng...', 12, 0, 100, 0, 0, 30197, 0, 65180, 0, 'LOREWALKER_CHO_FENG_PAST_EVENT_2');
INSERT INTO `creature_text` VALUES (61348, 10, 0, 'The Zandalari, ancient allies of the mogu! They must seek the legendary weapon within the depths of the Vaults!', 12, 0, 100, 0, 0, 30209, 0, 65181, 0, 'LOREWALKER_CHO_GARAJAL_EVENT_1');
INSERT INTO `creature_text` VALUES (61348, 11, 0, 'I am sorry I did not tell you earlier! These vaults house a weapon of unimaginable power! Stop them!', 12, 0, 100, 0, 0, 30208, 0, 65188, 0, 'LOREWALKER_CHO_GARAJAL_EVENT_2');
INSERT INTO `creature_text` VALUES (61348, 12, 0, 'I will stay behind and investigate this dais further...', 12, 0, 100, 0, 0, 30210, 0, 65189, 0, 'LOREWALKER_CHO_GARAJAL_EVENT_3');
INSERT INTO `creature_text` VALUES (61348, 13, 0, 'Ah, the ancient Vaults of the Kings of Mogu\'shan. What might this button do...?', 12, 0, 100, 0, 0, 30176, 0, 60833, 0, 'LOREWALKER_CHO_SPIRIT_KINGS_EVENT_1');
INSERT INTO `creature_text` VALUES (61348, 14, 0, 'Look here! It\'s the history of Zian of the Endless Shadow, a powerful sorcerer who kept the kingdoms in check through terror and darkness.', 12, 0, 100, 0, 0, 30207, 0, 60728, 0, 'LOREWALKER_CHO_SPIRIT_KINGS_EVENT_ZIAN');
INSERT INTO `creature_text` VALUES (61348, 15, 0, 'This looks to be the record of Qiang the Merciless, a great conqueror who ruled his vassals with an iron fist!', 12, 0, 100, 0, 0, 31529, 0, 60857, 0, 'LOREWALKER_CHO_SPIRIT_KINGS_EVENT_QUIANG');
INSERT INTO `creature_text` VALUES (61348, 16, 0, 'I\'ve heard tales of this one: Subetai the Swift, well known as a masterful archer and genius thief. He stole from the rich, and kept everything for himself.', 12, 0, 100, 0, 0, 30177, 0, 60861, 0, 'LOREWALKER_CHO_SPIRIT_KINGS_EVENT_SUBETAI');
INSERT INTO `creature_text` VALUES (61348, 17, 0, 'What is this? I\'ve heard nothing of this emperor. Meng the Demented? According to these texts, Meng was the most foul and maniacal ruler to ever abuse the imperial throne.', 12, 0, 100, 0, 0, 31528, 0, 60865, 0, 'LOREWALKER_CHO_SPIRIT_KINGS_EVENT_MENG');
INSERT INTO `creature_text` VALUES (61350, 0, 0, 'Help! Help!', 14, 0, 100, 20, 0, 0, 0, 26342, 0, '');
INSERT INTO `creature_text` VALUES (61350, 1, 0, 'I thought I was doomed. Thank you so much!', 12, 0, 100, 0, 0, 0, 0, 60966, 0, '');
INSERT INTO `creature_text` VALUES (61398, 0, 0, 'What is this, more have come to claim my crown? They are small and weak.', 14, 0, 100, 0, 0, 28355, 0, 61048, 0, 'Xin the Weaponmaster - TALK_INTRO');
INSERT INTO `creature_text` VALUES (61398, 1, 0, 'You are not the first to challenge me, peons. You will not be the last.', 14, 0, 100, 0, 0, 28349, 0, 61047, 0, 'Xin the Weaponmaster - TALK_AGGRO');
INSERT INTO `creature_text` VALUES (61398, 2, 0, 'Face the might of my arsenal, weaklings!', 14, 0, 100, 0, 0, 28350, 0, 61055, 0, 'Xin the Weaponmaster - TALK_AXES');
INSERT INTO `creature_text` VALUES (61398, 3, 0, '%s activates his |cFFFF0404|Hspell:119374|h[Whirlwinding Axe]|h|r trap!', 41, 0, 100, 0, 0, 28350, 0, 63555, 0, 'Xin the Weaponmaster - EMOTE_AXES');
INSERT INTO `creature_text` VALUES (61398, 4, 0, '%s activates his |cFFFF0404|Hspell:119311|h[Stream of Blades]|h|r trap!', 41, 0, 100, 0, 0, 0, 0, 63551, 0, 'Xin the Weaponmaster - EMOTE_BLADES');
INSERT INTO `creature_text` VALUES (61398, 5, 0, 'MY MIGHT IS ENDLESS!', 14, 0, 100, 0, 0, 28351, 0, 61052, 0, 'Xin the Weaponmaster - TALK_BLADES');
INSERT INTO `creature_text` VALUES (61398, 6, 0, 'ENOUGH OF THIS FARCE! You will feel PAIN, cowards!', 14, 0, 100, 0, 0, 28360, 0, 61058, 0, 'Xin the Weaponmaster - TALK_CROSSBOW');
INSERT INTO `creature_text` VALUES (61398, 7, 0, '%s activates his |cFFFF0404|Hspell:120142|h[Crossbow]|h|r trap!', 41, 0, 100, 0, 0, 28360, 0, 63554, 0, 'Xin the Weaponmaster - EMOTE_CROSSBOW');
INSERT INTO `creature_text` VALUES (61398, 8, 0, 'You call that... a fight...?', 14, 0, 100, 0, 0, 28352, 0, 61065, 0, 'Xin the Weaponmaster to Player - TALK_DEATH');
INSERT INTO `creature_text` VALUES (61398, 9, 0, '%s spits blood in your face.', 16, 0, 100, 0, 0, 0, 0, 61059, 0, 'Xin the Weaponmaster to Player - EMOTE_DEATH');
INSERT INTO `creature_text` VALUES (61398, 10, 0, 'See how effortlessly I CRUSH your bones!', 14, 0, 50, 0, 0, 28357, 0, 61069, 0, 'Xin slay 1 - TALK_SLAY');
INSERT INTO `creature_text` VALUES (61398, 10, 1, 'Fall to your knees, coward!', 14, 0, 50, 0, 0, 28358, 0, 61070, 0, 'Xin slay 2 - TALK_SLAY');
INSERT INTO `creature_text` VALUES (61398, 11, 0, 'I. Am. UNDEFEATED!', 14, 0, 100, 0, 0, 28356, 0, 61178, 0, 'Xin reset - TALK_RESET');
INSERT INTO `creature_text` VALUES (61442, 0, 0, 'Clan Gurthan will show our King and the rest of you power-hungry imposters why we are the rightful ones at his side!', 14, 0, 0, 0, 0, 27932, 0, 60932, 0, 'Kuai the Brute intro');
INSERT INTO `creature_text` VALUES (61442, 1, 0, 'We will never surrender our right to rule the destiny of our people!', 14, 0, 0, 0, 0, 27933, 0, 60943, 0, 'Kuai retire');
INSERT INTO `creature_text` VALUES (61442, 2, 0, 'The glory of Gurthan!', 14, 0, 0, 0, 0, 27942, 0, 63314, 0, 'Kuai kill target');
INSERT INTO `creature_text` VALUES (61442, 3, 0, 'Who allowed these outsiders inside our halls? Only Clan Harthak or Clan Kargesh would stoop to such treachery!', 14, 0, 0, 0, 0, 27940, 0, 61067, 0, 'Kuai postcombat #1');
INSERT INTO `creature_text` VALUES (61442, 4, 0, 'A well-scripted exchange; but we are not fooled. Your clans are clearly in league with one another. For the glory of our king, our empire, we will put you all down!', 14, 0, 0, 0, 0, 27941, 0, 61077, 0, 'Kuai postcombat #2');
INSERT INTO `creature_text` VALUES (61442, 5, 0, 'We are the greatest!', 14, 0, 50, 0, 0, 27934, 0, 61559, 0, 'Kuai idle #1');
INSERT INTO `creature_text` VALUES (61442, 5, 1, 'These other clans are nothing!', 14, 0, 50, 0, 0, 27935, 0, 61560, 0, 'Kuai idle #2');
INSERT INTO `creature_text` VALUES (61444, 0, 0, 'Clan Harthak will show all why they are the truest of Mogu!', 14, 0, 0, 0, 0, 27947, 0, 60934, 0, 'Ming the Cunning intro');
INSERT INTO `creature_text` VALUES (61444, 1, 0, 'Our clan is the true clan! No interloper can change that!', 14, 0, 0, 0, 0, 27948, 0, 60944, 0, 'Ming retire');
INSERT INTO `creature_text` VALUES (61444, 2, 0, 'This is the might of Harthak!', 14, 0, 0, 0, 0, 27957, 0, 63315, 0, 'Ming kill target');
INSERT INTO `creature_text` VALUES (61444, 3, 0, 'Only the dogs of Clan Gurthan would point fingers at others for their own failures! Your clan is the most desperate. It must\'ve been you!', 14, 0, 0, 0, 0, 27955, 0, 61072, 0, 'Ming postcombat #1');
INSERT INTO `creature_text` VALUES (61444, 4, 0, 'Our clan holds true to the ways set forth by the ancients. We deserve the king\'s favor above all others!', 14, 0, 50, 0, 0, 27950, 0, 61562, 0, 'Ming idle #1');
INSERT INTO `creature_text` VALUES (61444, 4, 1, 'These other clans aren\'t worthy of your time, great king!', 14, 0, 50, 0, 0, 27949, 0, 61561, 0, 'Ming idle #2');
INSERT INTO `creature_text` VALUES (61445, 0, 0, 'Clan Kargesh will demonstrate why only the strong deserve to stand at our King\'s side!', 14, 0, 0, 0, 0, 28246, 0, 60933, 0, 'Haiyan the Unstoppable intro');
INSERT INTO `creature_text` VALUES (61445, 1, 0, 'Impossible! Our might is the greatest in all the empire!', 14, 0, 0, 0, 0, 28247, 0, 60945, 0, 'Haiyan retire');
INSERT INTO `creature_text` VALUES (61445, 2, 0, 'For Kargesh!', 14, 0, 0, 0, 0, 28256, 0, 63316, 0, 'Haiyan kill target');
INSERT INTO `creature_text` VALUES (61445, 3, 0, 'Clan Gurthan is right! Only the honorless Clan Harthak could bring in a lesser race to do their bidding. You must be traitors!', 14, 0, 0, 0, 0, 28254, 0, 61074, 0, 'Haiyan postcombat #1');
INSERT INTO `creature_text` VALUES (61445, 4, 0, 'The Mogu cannot reclaim our rightful glory without our clan\'s strength!', 14, 0, 50, 0, 0, 28248, 0, 61563, 0, 'Haiyan idle #1');
INSERT INTO `creature_text` VALUES (61445, 4, 1, 'These pathetic clans are beneath you, my king. They sully the great legacy of our people!', 14, 0, 50, 0, 0, 28249, 0, 61564, 0, 'Haiyan idle #2');
INSERT INTO `creature_text` VALUES (61461, 0, 0, 'They\'ve overrun the village! HELP!', 14, 0, 100, 20, 0, 0, 0, 60980, 0, '');
INSERT INTO `creature_text` VALUES (61461, 1, 0, 'I thought I was doomed. Thank you so much!', 12, 0, 100, 0, 0, 0, 0, 60966, 0, '');
INSERT INTO `creature_text` VALUES (61462, 0, 0, 'Away, you fiends! Help!', 14, 0, 100, 20, 0, 0, 0, 60979, 0, '');
INSERT INTO `creature_text` VALUES (61462, 1, 0, 'I thought I was doomed. Thank you so much!', 12, 0, 100, 0, 0, 0, 0, 60966, 0, '');
INSERT INTO `creature_text` VALUES (61485, 0, 0, 'Niuzao\'s forces attack from the rear!', 14, 0, 100, 0, 0, 30279, 0, 61499, 0, 'Geleral Pavalak - SAY_INTRO');
INSERT INTO `creature_text` VALUES (61485, 1, 0, 'The temple will fall! You cannot stop my forces!', 14, 0, 100, 0, 0, 30277, 0, 61505, 0, 'Geleral Pavalak - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (61485, 2, 0, 'My army will crush you!', 14, 0, 100, 0, 0, 30278, 0, 61506, 0, 'Geleral Pavalak - SAY_BULWARK');
INSERT INTO `creature_text` VALUES (61485, 2, 1, 'Stop them! Mantid attack!', 14, 0, 100, 0, 0, 30280, 0, 61507, 0, 'Geleral Pavalak - SAY_BULWARK');
INSERT INTO `creature_text` VALUES (61485, 2, 2, 'Put down your weapons or die!', 14, 0, 100, 0, 0, 30283, 0, 61509, 0, 'Geleral Pavalak - SAY_BULWARK');
INSERT INTO `creature_text` VALUES (61485, 3, 0, 'The empress demands it!', 14, 0, 100, 0, 0, 30285, 0, 61511, 0, 'Geleral Pavalak - SAY_SLAY');
INSERT INTO `creature_text` VALUES (61485, 3, 1, 'The forces of Niuzao are weak!', 14, 0, 100, 0, 0, 30286, 0, 61512, 0, 'Geleral Pavalak - SAY_SLAY');
INSERT INTO `creature_text` VALUES (61485, 4, 0, 'I have failed you, empress...', 14, 0, 100, 0, 0, 30284, 0, 61510, 0, 'Geleral Pavalak - SAY_');
INSERT INTO `creature_text` VALUES (61492, 0, 0, 'Thank you, friend. Don\'t worry, it was just a scratch. Now I feel good as new!', 12, 0, 100, 0, 0, 0, 0, 61006, 0, '');
INSERT INTO `creature_text` VALUES (61492, 0, 1, 'I thought I was a goner. I owe you my life.', 12, 0, 100, 0, 0, 0, 0, 61003, 0, '');
INSERT INTO `creature_text` VALUES (61492, 0, 2, 'Thanks for saving me. It was rough up there.', 12, 0, 100, 0, 0, 0, 0, 53196, 0, '');
INSERT INTO `creature_text` VALUES (61492, 0, 3, 'I\'m alive? Ha! That\'s more than I can say for those Horde at Garrosh\'ar Point. We tore them up good.', 12, 0, 100, 0, 0, 0, 0, 53197, 0, '');
INSERT INTO `creature_text` VALUES (61492, 0, 4, 'Thank you. Time to lick my wounds and get back to the fight!', 12, 0, 100, 0, 0, 0, 0, 53195, 0, '');
INSERT INTO `creature_text` VALUES (61567, 0, 0, 'I hope they make it this far. Then my strength and skill will be clear to all!', 14, 0, 100, 0, 0, 29392, 0, 66443, 0, 'Vizier Jin\'bak - SAY_INTRO');
INSERT INTO `creature_text` VALUES (61567, 1, 0, 'Ah-hah! The lot of you will soon wish you hadn\'t come this way...', 14, 0, 100, 0, 0, 29390, 0, 66444, 0, 'Vizier Jin\'bak - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (61567, 2, 0, 'But... I was... who will...?!', 14, 0, 100, 0, 0, 29391, 0, 66445, 0, 'Vizier Jin\'bak - SAY_SAY_DEATH');
INSERT INTO `creature_text` VALUES (61567, 3, 0, 'Did you think you stood a chance?!', 14, 0, 100, 0, 0, 29394, 0, 66447, 0, 'Vizier Jin\'bak - SAY_SLAY');
INSERT INTO `creature_text` VALUES (61567, 4, 0, 'Allow me to show you the power of amber alchemy...', 14, 0, 100, 0, 0, 29395, 0, 66448, 0, 'Vizier Jin\'bak - SAY_DETONATE');
INSERT INTO `creature_text` VALUES (61620, 0, 0, 'Thanks for the save, but we\'re still stuck in the middle of the entire mantid army.', 12, 0, 100, 0, 0, 31505, 0, 61584, 0, 'Yang Ironclaw - SAY_INTRO_1');
INSERT INTO `creature_text` VALUES (61620, 1, 0, 'Look out there. Too many for us to take head on.', 12, 0, 100, 0, 0, 31506, 0, 61591, 0, 'Yang Ironclaw - SAY_INTRO_2');
INSERT INTO `creature_text` VALUES (61620, 2, 0, 'Hmmm... I have a crazy idea that just might work, but we\'ll need your help heroes!', 12, 0, 100, 0, 0, 31507, 0, 62580, 0, 'Yang Ironclaw - SAY_INTRO_3');
INSERT INTO `creature_text` VALUES (61620, 3, 0, 'Let\'s give these mantid a taste of their own medicine!', 12, 0, 100, 0, 0, 31508, 0, 62581, 0, 'Yang Ironclaw - SAY_INTRO_4');
INSERT INTO `creature_text` VALUES (61620, 4, 0, 'Li and Lo, get the sap into position!', 12, 0, 100, 0, 0, 31509, 0, 62582, 0, 'Yang Ironclaw - SAY_AGGRO_1');
INSERT INTO `creature_text` VALUES (61620, 5, 0, 'Let\'s get this party started, shall we?', 12, 0, 100, 0, 0, 31510, 0, 62583, 0, 'Yang Ironclaw - SAY_AGGRO_2');
INSERT INTO `creature_text` VALUES (61620, 6, 0, 'Bring it, uglies!', 14, 0, 100, 0, 0, 31513, 0, 62584, 0, 'Yang Ironclaw - SAY_AGGRO_3');
INSERT INTO `creature_text` VALUES (61620, 7, 0, 'We\'ll squish you like bugs!', 14, 0, 100, 0, 0, 31514, 0, 66224, 0, 'Yang Ironclaw - SAY_AGGRO_4');
INSERT INTO `creature_text` VALUES (61620, 8, 0, 'We\'ve got your sap -- come and get it!', 14, 0, 100, 0, 0, 31515, 0, 66225, 0, 'Yang Ironclaw - SAY_AGGRO_5');
INSERT INTO `creature_text` VALUES (61620, 10, 0, 'Swarmers are coming up the ramp -- use the sap to slow them down!', 14, 0, 100, 0, 0, 31511, 0, 62586, 0, 'Yang Ironclaw - SAY_WAVE_ONE');
INSERT INTO `creature_text` VALUES (61620, 12, 0, 'Demolishers are carrying explosives! Any direct hit will blow them up!', 14, 0, 100, 0, 0, 31504, 0, 62025, 0, 'Yang Ironclaw - SAY_WAVE_TWO');
INSERT INTO `creature_text` VALUES (61620, 14, 0, 'Warrior coming in!  Focus your fire!', 14, 0, 100, 0, 0, 31512, 0, 62168, 0, 'Yang Ironclaw - SAY_WAVE_THREE');
INSERT INTO `creature_text` VALUES (61620, 17, 0, 'The Amberwing is flying in, watch out!', 14, 0, 100, 0, 0, 31503, 0, 62023, 0, 'Yang Ironclaw - SAY_AMBERWING');
INSERT INTO `creature_text` VALUES (61620, 18, 0, 'Well, that was fun, wasn\'t it?', 12, 0, 100, 11, 0, 31497, 0, 62551, 0, 'Yang Ironclaw - SAY_OUTRO_1');
INSERT INTO `creature_text` VALUES (61620, 19, 0, 'Li and Lo, go look after our friends down below.', 12, 0, 100, 25, 0, 31498, 0, 62734, 0, 'Yang Ironclaw - SAY_OUTRO_2');
INSERT INTO `creature_text` VALUES (61620, 20, 0, 'Ah, this gate is locked, but I think I have something that\'ll open it.', 12, 0, 100, 0, 0, 31499, 0, 62735, 0, 'Yang Ironclaw - SAY_GATE_1');
