INSERT INTO `creature_text` VALUES (61634, 0, 0, 'Fools! Attacking the might of the mantid head on?  Your deaths will be swift.', 14, 0, 100, 0, 0, 30264, 0, 61296, 0, 'Commander Vo\'jak - SAY_AGGRO');
