INSERT INTO `creature_text` VALUES (61634, 2, 0, 'Demolishers, get in there!  Leave nothing but a pile of ash!', 14, 0, 100, 0, 0, 30266, 0, 62587, 0, 'Commander Vo\'jak - SAY_WAVE_TWO');
