INSERT INTO `creature_text` VALUES (61634, 5, 0, 'Where... where is it? Worthless, useless... damn you!', 14, 0, 100, 0, 0, 30272, 0, 66200, 0, 'Commander Vo\'jak - SAY_AIR_SUPPORT');
INSERT INTO `creature_text` VALUES (61634, 6, 0, 'Pah! Those who failed deserved to die; you have merely culled the weak!', 14, 0, 100, 0, 0, 30262, 0, 66205, 0, 'Commander Vo\'jak - SAY_ENGAGE_1');
INSERT INTO `creature_text` VALUES (61634, 7, 0, 'I must finish this myself, then. So be it!', 14, 0, 100, 0, 0, 30263, 0, 62167, 0, 'Commander Vo\'jak - SAY_ENGAGE_2');
