INSERT INTO `creature_text` VALUES (61634, 8, 2, 'Amberwing, get in there! Lay siege to the holdouts!', 14, 0, 100, 0, 0, 30271, 0, 62627, 0, 'Commander Vo\'jak - SAY_AMBERWING');
