INSERT INTO `creature_text` VALUES (62205, 0, 0, 'You may have come this far. You may have carved a path through my army, but I... will kill you, and I will build the bridge.', 14, 0, 100, 0, 0, 30359, 0, 63348, 0, 'Wing Leader Neronok - SAY_AGGRO');
