INSERT INTO `creature_text` VALUES (62465, 0, 0, 'YOU BETTER BE READY TO GET SOME OOK IN YER DOOKER!', 14, 0, 100, 0, 0, 0, 0, 61877, 0, '');
INSERT INTO `creature_text` VALUES (62465, 1, 0, 'Captain Ook is comin\' down with the scurvy! Keep the orange away from him!', 41, 0, 100, 0, 0, 0, 0, 61859, 0, '');
INSERT INTO `creature_text` VALUES (62467, 0, 0, 'Ah! Good! You made it. It is time to teach these \"pirate\" hozen why they should not raid our villages.', 12, 0, 100, 0, 0, 0, 0, 63624, 0, '');
INSERT INTO `creature_text` VALUES (62467, 1, 0, 'The trick to dealing with pirate hozen is to have the element of surprise.', 12, 0, 100, 1, 0, 0, 0, 61850, 0, '');
INSERT INTO `creature_text` VALUES (62467, 2, 0, 'Watch out!', 12, 0, 100, 0, 0, 0, 0, 14828, 0, '');
INSERT INTO `creature_text` VALUES (62467, 3, 0, 'Okay... so... the new trick to dealing with pirate hozen is to do what you just did right there.', 12, 0, 100, 0, 0, 0, 0, 61851, 0, '');
INSERT INTO `creature_text` VALUES (62467, 4, 0, 'Birds too?! How\'d they even learn to tame the birds?!', 12, 0, 100, 0, 0, 0, 0, 61862, 0, '');
INSERT INTO `creature_text` VALUES (62467, 5, 0, 'Well look what we have here. Our first keg of recovered brew! I\'ll take this one, folks.', 12, 0, 100, 0, 0, 0, 0, 67373, 0, '');
INSERT INTO `creature_text` VALUES (62467, 6, 0, 'We\'d better keep moving before this whole island finds out we\'re here.', 12, 0, 100, 0, 0, 0, 0, 61854, 0, '');
INSERT INTO `creature_text` VALUES (62467, 7, 0, 'Hozen gladiators? Where did they get all of these weapons?', 12, 0, 100, 0, 0, 0, 0, 61855, 0, '');
INSERT INTO `creature_text` VALUES (62467, 8, 0, 'Well! This looks like as good a place to brew as any.', 12, 0, 100, 0, 0, 0, 0, 61856, 0, '');
INSERT INTO `creature_text` VALUES (62468, 0, 0, 'You are what\'s on the menu!', 12, 0, 100, 0, 0, 0, 0, 64784, 0, '');
INSERT INTO `creature_text` VALUES (62468, 1, 0, 'You look a little pale!', 12, 0, 100, 0, 0, 0, 0, 61867, 0, '');
INSERT INTO `creature_text` VALUES (62468, 2, 0, 'Wikkets... gonna... pay.', 14, 0, 100, 0, 0, 0, 0, 64034, 0, '');
INSERT INTO `creature_text` VALUES (62491, 0, 0, 'This cauldron will be perfect for brewing our... brew.', 12, 0, 100, 1, 0, 0, 0, 61872, 0, '');
INSERT INTO `creature_text` VALUES (62491, 1, 0, 'Err... What was that?', 12, 0, 100, 5, 0, 0, 0, 65670, 0, '');
INSERT INTO `creature_text` VALUES (62491, 2, 0, 'Phew! That was close. Ok, here\'s the deal...', 12, 0, 100, 5, 0, 0, 0, 65674, 0, '');
INSERT INTO `creature_text` VALUES (62491, 3, 0, 'One of you, stay with me to keep the cauldron safe. The others, go find some Unga Jungle Brew and bring it back!', 12, 0, 100, 1, 0, 0, 0, 61898, 0, '');
INSERT INTO `creature_text` VALUES (62491, 4, 0, 'Search the island to collect brew for Bo\'s cauldron! But be sure to leave one person nearby to defend Bo!', 41, 0, 100, 1, 0, 0, 0, 65966, 0, '');
INSERT INTO `creature_text` VALUES (62491, 5, 0, 'You can use the bombs and cannons nearby to blast the hozen away!', 41, 0, 100, 5, 0, 0, 0, 65666, 0, '');
INSERT INTO `creature_text` VALUES (62491, 6, 0, 'We need more brew. I think I saw brew guzzling hozen on the mountain peak to the southeast.', 14, 0, 100, 0, 0, 0, 0, 64025, 0, '');
INSERT INTO `creature_text` VALUES (62491, 7, 0, 'DELICIOUS!', 12, 0, 100, 7, 0, 0, 0, 12139, 0, '');
INSERT INTO `creature_text` VALUES (62491, 8, 0, 'Oh... my. That brew is a bit stronger than I had expected... I...', 12, 0, 100, 64, 0, 0, 0, 66242, 0, '');
INSERT INTO `creature_text` VALUES (62491, 9, 0, 'Just need to rest my eyes a bit...', 12, 0, 100, 12, 0, 0, 0, 66243, 0, '');
INSERT INTO `creature_text` VALUES (62492, 0, 0, 'Heyarrrr!', 12, 0, 100, 0, 0, 0, 0, 65184, 0, '');
