INSERT INTO `creature_text` VALUES (62508, 0, 0, 'WIKKETS!!!', 14, 0, 100, 4, 0, 0, 0, 65684, 0, '');
INSERT INTO `creature_text` VALUES (62511, 0, 0, 'Ah, I see you have found a few of my experiments.  Step closer, and you may witness the full glory of my work here.', 14, 0, 100, 0, 0, 31559, 0, 62227, 0, 'UNSOK_INTRO');
INSERT INTO `creature_text` VALUES (62511, 1, 0, 'You must be powerful, to have come this far. Yes, yes... you will make worthy test subjects.', 14, 0, 100, 0, 0, 31555, 0, 62226, 0, 'UNSOK_AGGRO');
INSERT INTO `creature_text` VALUES (62511, 2, 0, 'Watch your step!', 14, 0, 100, 0, 0, 31563, 0, 45474, 0, 'UNSOK_SPELL_AMBER_SCALPEL');
INSERT INTO `creature_text` VALUES (62511, 3, 0, 'Feel fortunate to receive this gift.', 14, 0, 100, 0, 0, 31562, 0, 62233, 0, 'UNSOK_SPELL_PARASITIC');
INSERT INTO `creature_text` VALUES (62511, 4, 0, 'From death comes life, and from life, death....', 14, 0, 100, 0, 0, 31564, 0, 62234, 0, 'UNSOK_SPELL_ABOMINATION');
INSERT INTO `creature_text` VALUES (62511, 5, 0, 'You must think yourselves mighty. How little you know. Behold now, my greatest creation!', 14, 0, 100, 0, 0, 31557, 0, 62229, 0, 'UNSOK_PHASE_2');
INSERT INTO `creature_text` VALUES (62511, 6, 0, 'What have you done? No, this will not be how my work ends. I will not allow it!', 14, 0, 100, 0, 0, 31558, 0, 62230, 0, 'UNSOK_PHASE_3');
INSERT INTO `creature_text` VALUES (62511, 7, 0, 'Excellent!', 14, 0, 100, 0, 0, 31561, 0, 20857, 0, 'UNSOK_SLAY');
INSERT INTO `creature_text` VALUES (62511, 8, 0, 'Forgive me, Empress....', 14, 0, 100, 0, 0, 31556, 0, 62228, 0, 'UNSOK_DEATH');
INSERT INTO `creature_text` VALUES (62543, 0, 0, 'They approach? Good. Now, if only my students were here to see and learn from the coming display of martial prowess...', 14, 0, 100, 0, 0, 29258, 0, 64306, 0, 'Tayak Intro');
INSERT INTO `creature_text` VALUES (62543, 1, 0, 'On your guard, invaders. I, Ta\'yak, Lord of Blades, will be your opponent.', 14, 0, 100, 0, 0, 29252, 0, 62941, 0, 'Tayak Intro');
INSERT INTO `creature_text` VALUES (62543, 2, 0, 'I\'d always hoped to cut someone like that someday, to hear that sound. But to have it happen to my own prothorax is ridiculous.', 14, 0, 100, 0, 0, 29253, 0, 62944, 0, 'Tayak Death');
INSERT INTO `creature_text` VALUES (62543, 3, 0, 'A perfect cut.', 14, 0, 100, 0, 0, 29259, 0, 62942, 0, 'Tayak Kill');
INSERT INTO `creature_text` VALUES (62543, 3, 1, 'This is the technique of a Blade Lord.', 14, 0, 100, 0, 0, 29260, 0, 62943, 0, 'Tayak Kill 2');
INSERT INTO `creature_text` VALUES (62543, 4, 0, 'Mere initiates.', 14, 0, 100, 0, 0, 29254, 0, 64302, 0, 'Tayak - 1');
INSERT INTO `creature_text` VALUES (62543, 4, 1, 'They should have studied harder.', 14, 0, 100, 0, 0, 29255, 0, 64303, 0, 'Tayak - 2');
INSERT INTO `creature_text` VALUES (62543, 4, 2, 'One technique alone is not enough.', 14, 0, 100, 0, 0, 29256, 0, 64304, 0, 'Tayak - 3');
INSERT INTO `creature_text` VALUES (62543, 4, 3, 'They failed their test. Will you?', 14, 0, 100, 0, 0, 29257, 0, 64305, 0, 'Tayak - 4');
INSERT INTO `creature_text` VALUES (62543, 5, 0, 'Can you follow my blade?', 14, 0, 100, 0, 0, 29261, 0, 66887, 0, 'Tayak - Heroic Spell');
INSERT INTO `creature_text` VALUES (62543, 6, 0, 'Now go, impart my techniques to the initiates.', 14, 0, 100, 0, 0, 0, 0, 64272, 0, 'Tayak - Intro');
