INSERT INTO `creature_text` VALUES (62837, 5, 0, 'Such is the fate of those who question authority.', 14, 0, 100, 0, 0, 29273, 0, 62897, 0, 'SHEKZEER_RESET');
INSERT INTO `creature_text` VALUES (62837, 6, 0, 'I will dispose of the outsiders.', 14, 0, 100, 0, 0, 29271, 0, 62900, 0, 'SHEKZEER_EVENT_RECHARGE');
INSERT INTO `creature_text` VALUES (62837, 7, 0, 'Augh... ah ... I...  have failed.', 14, 0, 100, 0, 0, 29268, 0, 62892, 0, 'SHEKZEER_DEATH');
INSERT INTO `creature_text` VALUES (62980, 1, 0, 'The Divine chose us to give mortal voice to Her divine will. We are but the vessel that enacts Her will.', 14, 0, 100, 0, 0, 29301, 0, 62465, 0, 'Imperial Vizier Zor\'lok - TALK_AGGRO');
INSERT INTO `creature_text` VALUES (62980, 2, 0, 'We will not give in to the despair of the dark void. If Her will for us is to perish, then it shall be so.', 14, 0, 100, 0, 0, 29302, 0, 62475, 0, 'Imperial Vizier Zor\'lok - TALK_DEATH');
INSERT INTO `creature_text` VALUES (62980, 3, 0, 'We are the extension of our Empress\'s will.', 14, 0, 100, 0, 0, 29303, 0, 62452, 0, 'Imperial Vizier Zor\'lok - TALK_EVENT_IDLE_1A');
INSERT INTO `creature_text` VALUES (62980, 4, 0, 'Ours is but to serve in Her divine name.', 14, 0, 100, 0, 0, 29304, 0, 62453, 0, 'Imperial Vizier Zor\'lok - TALK_EVENT_IDLE_1B');
INSERT INTO `creature_text` VALUES (62980, 5, 0, 'Never to question, nor to contemplate; we simply act.', 14, 0, 100, 0, 0, 29305, 0, 62454, 0, 'Imperial Vizier Zor\'lok - TALK_EVENT_IDLE_1C');
INSERT INTO `creature_text` VALUES (62980, 6, 0, 'We fight, toil, and serve so that Her vision for us is made reality.', 14, 0, 100, 0, 0, 29306, 0, 62455, 0, 'Imperial Vizier Zor\'lok - TALK_EVENT_IDLE_2');
INSERT INTO `creature_text` VALUES (62980, 7, 0, 'Her happiness is our reward, her sorrow our failure.', 14, 0, 100, 0, 0, 29307, 0, 62456, 0, 'Imperial Vizier Zor\'lok - TALK_EVENT_IDLE_3');
INSERT INTO `creature_text` VALUES (62980, 8, 0, 'We will give our lives for the Empress without hesitation. She is the light, and without Her our lives will be lost to darkness.', 14, 0, 100, 0, 0, 29308, 0, 62457, 0, 'Imperial Vizier Zor\'lok - TALK_EVENT_IDLE_4');
INSERT INTO `creature_text` VALUES (62980, 9, 0, 'Her gifts are many, our gratitude immeasurable.', 14, 0, 100, 0, 0, 29309, 0, 62472, 0, 'Imperial Vizier Zor\'lok - TALK_EVENT_PHASE_1');
INSERT INTO `creature_text` VALUES (62980, 10, 0, 'She tests us. We will not disappoint Her.', 14, 0, 100, 0, 0, 29310, 0, 62473, 0, 'Imperial Vizier Zor\'lok - TALK_EVENT_PHASE_2');
INSERT INTO `creature_text` VALUES (62980, 11, 0, 'We are as unwavering as the amber that shapes our halls. With Her might we will vanquish all who dare intrude upon Her divine home.', 14, 0, 100, 0, 0, 29311, 0, 62474, 0, 'Imperial Vizier Zor\'lok - TALK_EVENT_PHASE_3');
INSERT INTO `creature_text` VALUES (62980, 12, 0, 'The chaff of the world tumbles across our doorstep, driven by fear; Her royal storm will whisk them away.', 14, 0, 100, 0, 0, 29312, 0, 62458, 0, 'Imperial Vizier Zor\'lok - TALK_EVENT_TRASH_A_COMBAT');
INSERT INTO `creature_text` VALUES (62980, 13, 0, 'They were clearly unworthy of Her divine embrace.', 14, 0, 100, 0, 0, 29313, 0, 62461, 0, 'Imperial Vizier Zor\'lok - TALK_EVENT_TRASH_A_DIES');
INSERT INTO `creature_text` VALUES (62980, 14, 0, 'They are but the waves crashing upon the mountain of Her divine will. They may rise again and again; but will accomplish nothing.', 14, 0, 100, 0, 0, 29314, 0, 62459, 0, 'Imperial Vizier Zor\'lok - TALK_EVENT_TRASH_B_COMBAT');
INSERT INTO `creature_text` VALUES (62980, 15, 0, 'We are unfazed. We will stand firm.', 14, 0, 100, 0, 0, 29315, 0, 62462, 0, 'Imperial Vizier Zor\'lok - TALK_EVENT_TRASH_B_DIES');
INSERT INTO `creature_text` VALUES (62980, 16, 0, 'The Divine challenges us to face these intruders.', 14, 0, 100, 0, 0, 29316, 0, 62460, 0, 'Imperial Vizier Zor\'lok - TALK_EVENT_TRASH_C_COMBAT');
INSERT INTO `creature_text` VALUES (62980, 17, 0, 'And so it falls to us, Her chosen voice.', 14, 0, 100, 0, 0, 29317, 0, 62463, 0, 'Imperial Vizier Zor\'lok - TALK_EVENT_TRASH_C_DIES');
INSERT INTO `creature_text` VALUES (62980, 18, 0, 'The Divine will not suffer your taint here, outsider.', 14, 0, 100, 0, 0, 29318, 0, 62466, 0, 'Imperial Vizier Zor\'lok - TALK_SLAY_01');
INSERT INTO `creature_text` VALUES (62980, 19, 0, 'Ours is but to serve; yours is but to die.', 14, 0, 100, 0, 0, 29319, 0, 62467, 0, 'Imperial Vizier Zor\'lok - TALK_SLAY_02');
INSERT INTO `creature_text` VALUES (62980, 20, 0, 'Behold the voice of Her divine wrath.', 14, 0, 100, 0, 0, 29320, 0, 62469, 0, 'Imperial Vizier Zor\'lok - TALK_EXHALE');
INSERT INTO `creature_text` VALUES (62980, 21, 0, 'Through the air we breathe, She strengthens our voice.', 14, 0, 100, 0, 0, 29321, 0, 62468, 0, 'Imperial Vizier Zor\'lok - TALK_INHALE');
INSERT INTO `creature_text` VALUES (62980, 22, 0, 'Her will is greater than us all. You will join us or you will perish.', 14, 0, 100, 0, 0, 29322, 0, 62471, 0, 'Imperial Vizier Zor\'lok - TALK_CONVERT');
INSERT INTO `creature_text` VALUES (62980, 23, 0, 'Our voice gives form to Her will.', 14, 0, 100, 0, 0, 29323, 0, 62470, 0, 'Imperial Vizier Zor\'lok - TALK_PITCH');
INSERT INTO `creature_text` VALUES (62980, 24, 0, 'Imperial Vizier Zor\'lok flies to one of his platforms!', 41, 0, 100, 0, 0, 0, 0, 63770, 0, 'Imperial Vizier Zor\'lok - TALK_FLY_ANN');
INSERT INTO `creature_text` VALUES (62980, 25, 0, 'Imperial Vizier Zor\'lok inhales the Pheromones of Zeal!', 41, 0, 100, 0, 0, 0, 0, 63394, 0, 'Imperial Vizier Zor\'lok - TALK_INHALE_ZEAL_ANN');
INSERT INTO `creature_text` VALUES (62980, 28, 0, '|TInterface\\Icons\\warrior_disruptingshout.blp:20|tImperial Vizier Zor\'lok shouts with |cFFFF0000|Hspell:122713|h[Force and Verve]|h|r!', 41, 0, 100, 0, 0, 0, 0, 63774, 0, 'Imperial Vizier Zor\'lok - TALK_FORCE_AND_VERVE_ANN');
INSERT INTO `creature_text` VALUES (62980, 27, 0, '|TInterface\\Icons\\spell_arcane_mindmastery.blp:20|tImperial Vizier Zor\'lok uses his voice to |cFFFF0000|Hspell:122740|h[Convert]|h|r members of the raid to fight on his side!', 41, 0, 100, 0, 0, 0, 0, 63776, 0, 'Imperial Vizier Zor\'lok - TALK_CONVERT_ANN');
INSERT INTO `creature_text` VALUES (62980, 26, 0, '|TInterface\\Icons\\spell_druid_stamedingroar.blp:20|tImperial Vizier Zor\'lok |cFFFF0000|Hspell:122852|h[Inhales]|h|r a large breath!', 41, 0, 100, 0, 0, 0, 0, 63772, 0, 'Imperial Vizier Zor\'lok - TALK_INHALE_ANN');
INSERT INTO `creature_text` VALUES (62983, 1, 0, 'Wh-what are you doing here?! G-go away!', 14, 0, 100, 0, 0, 29328, 0, 63176, 0, 'Lei Shi - AGGRO');
INSERT INTO `creature_text` VALUES (62983, 2, 0, 'I\'m hiding until you leave!', 41, 0, 100, 0, 0, 29333, 0, 63198, 0, 'Lei Shi - HIDE');
INSERT INTO `creature_text` VALUES (62983, 2, 1, 'I don\'t want to see you anymore!', 41, 0, 100, 0, 0, 29334, 0, 63199, 0, 'Lei Shi - HIDE');
INSERT INTO `creature_text` VALUES (62983, 3, 0, 'Get AWAY!', 14, 0, 100, 0, 0, 29331, 0, 63196, 0, 'Lei Shi - GET AWAY');
INSERT INTO `creature_text` VALUES (62983, 3, 1, 'Stay away from me!', 14, 0, 100, 0, 0, 29332, 0, 63197, 0, 'Lei Shi - GET AWAY');
INSERT INTO `creature_text` VALUES (62983, 4, 0, 'S-sorry!', 14, 0, 100, 0, 0, 29329, 0, 63194, 0, 'Lei Shi - SLAY');
INSERT INTO `creature_text` VALUES (62983, 4, 1, 'It\'s your fault it happened!', 14, 0, 100, 0, 0, 29330, 0, 63195, 0, 'Lei Shi - SLAY');
INSERT INTO `creature_text` VALUES (62983, 5, 0, 'Wh-what are you doing here?! G-go away!', 14, 0, 100, 0, 0, 29328, 0, 63176, 0, 'Lei Shi - INTRO');
INSERT INTO `creature_text` VALUES (62983, 6, 0, 'I... ah... oh! Did I...? Was I...? It was... so... cloudy.', 14, 0, 100, 0, 0, 29325, 0, 62834, 0, 'Lei Shi - DEFEAT');
INSERT INTO `creature_text` VALUES (62983, 7, 0, 'I have to go now.', 14, 0, 100, 0, 0, 29326, 0, 62835, 0, 'Lei Shi - OUTRO_1');
INSERT INTO `creature_text` VALUES (62983, 8, 0, 'I have to make the water not cloudy anymore. Will... will you help?', 14, 0, 100, 0, 0, 29327, 0, 62836, 0, 'Lei Shi - OUTRO_2');
INSERT INTO `creature_text` VALUES (62988, 0, 0, 'You have my gratitude!', 12, 0, 100, 0, 0, 0, 0, 60964, 0, '');
INSERT INTO `creature_text` VALUES (62989, 0, 0, 'You have my gratitude!', 12, 0, 100, 0, 0, 0, 0, 60964, 0, '');
INSERT INTO `creature_text` VALUES (62990, 0, 0, 'You have my gratitude!', 12, 0, 100, 0, 0, 0, 0, 60964, 0, '');
INSERT INTO `creature_text` VALUES (63179, 0, 0, 'This amber is ours, trespasser! Amber not for the mantid!', 12, 0, 100, 0, 0, 0, 0, 62655, 0, '');
INSERT INTO `creature_text` VALUES (63256, 0, 0, 'Heyarrrrrrrr!', 12, 0, 100, 0, 0, 0, 0, 65167, 0, '');
INSERT INTO `creature_text` VALUES (63315, 0, 0, 'Welcome to our first officially-sanctioned combat event, the Arena of Annihilation!', 14, 0, 100, 0, 0, 0, 0, 62842, 0, '');
INSERT INTO `creature_text` VALUES (63315, 1, 0, 'Are you ready to display your martial prowess here, in the eye of The Tiger?', 14, 0, 100, 0, 0, 0, 0, 63395, 0, '');
INSERT INTO `creature_text` VALUES (63315, 2, 0, 'Then HIT THAT GONG, and show these monks what you\'re made of! ...one way or another, heh.', 14, 0, 100, 0, 0, 0, 0, 62847, 0, '');
INSERT INTO `creature_text` VALUES (63315, 3, 0, 'For our first show of skill and power we bring you SCAR-SHELL, mightiest of dragon turtles!', 14, 0, 100, 0, 0, 0, 0, 62915, 0, '');
INSERT INTO `creature_text` VALUES (63315, 4, 0, 'Whew, what a mess! Someone inform the monastery kitchens: We\'re having turtle soup, tonight!', 14, 0, 100, 0, 0, 0, 0, 62916, 0, '');
INSERT INTO `creature_text` VALUES (63315, 5, 0, 'Ready for the next fight? Ring that gong and GET! IT! ON!', 14, 0, 100, 0, 0, 0, 0, 63396, 0, '');
INSERT INTO `creature_text` VALUES (63315, 6, 0, 'It\'s time for the grummle-grubbing goliath, fifteen feet of furry fury, Kun-Lai Summit\'s own snow-stalking horror, JOL\'GRUM!', 14, 0, 100, 0, 0, 0, 0, 62917, 0, '');
INSERT INTO `creature_text` VALUES (63315, 7, 0, 'OUCH! Safe to say that Jol\'Grum wasn\'t quite yet-i ready for his battle with these three killing machines! Speaking of ready...', 14, 0, 100, 0, 0, 0, 0, 62918, 0, '');
INSERT INTO `creature_text` VALUES (63315, 8, 0, 'She\'s here to make new friends... and then set them on fire! It\'s Pandaria\'s prettiest little playful pyromaniac, Liuyang!', 14, 0, 100, 0, 0, 0, 0, 62919, 0, '');
INSERT INTO `creature_text` VALUES (63315, 9, 0, 'Snuffed out like a candle! Is there anyone these three can\'t beat?!', 14, 0, 100, 0, 0, 0, 0, 62920, 0, '');
INSERT INTO `creature_text` VALUES (63315, 10, 0, 'Did someone say Yak Attack?! It\'s ax-master Chagan Firehoof, wandering warrior of the Townlong Steppes!', 14, 0, 100, 0, 0, 0, 0, 62921, 0, '');
INSERT INTO `creature_text` VALUES (63315, 11, 0, 'What a battle! Our fine combatants served up Chagan a heaping helping of Yaungolian barbeque...', 14, 0, 100, 0, 0, 0, 0, 62922, 0, '');
INSERT INTO `creature_text` VALUES (63315, 12, 0, 'Flitting through the mists with death upon his fists comes the sinister and serene jinyu assassin: Satay Byu!', 14, 0, 100, 0, 0, 0, 0, 65632, 0, '');
INSERT INTO `creature_text` VALUES (63315, 13, 0, 'Ooo, that\'s one killer carp whose slaying days have come to a fishy end.', 14, 0, 100, 0, 0, 0, 0, 65633, 0, '');
INSERT INTO `creature_text` VALUES (63315, 14, 0, 'Everyone, please cheer for the NEW CHAMPIONS of the Arena of Annihilation!', 14, 0, 100, 0, 0, 0, 0, 65636, 0, '');
INSERT INTO `creature_text` VALUES (63520, 0, 0, '|TInterface\\Icons\\creatureportrait_bubble.blp:20|t%s casts |cFFFF0000|Hspell:124653|h[Water Shell]|h|r! Use the Water Spouts to pop it!', 41, 0, 100, 0, 0, 0, 0, 63642, 0, 'lite shield');
INSERT INTO `creature_text` VALUES (63528, 0, 0, 'Warbring Qobi dropped his axes. Grab one!', 14, 0, 100, 0, 0, 0, 0, 64112, 0, 'qobi waraxe');
INSERT INTO `creature_text` VALUES (63811, 0, 0, 'Thanks, $p! I thought I was done for.', 14, 0, 100, 0, 0, 0, 0, 63621, 0, 'intro');
INSERT INTO `creature_text` VALUES (63811, 1, 0, 'I panicked when I saw all those saurok and wrecked my boat.', 14, 0, 100, 0, 0, 0, 0, 63622, 0, 'brewing');
INSERT INTO `creature_text` VALUES (63812, 0, 0, 'It looks like he\'s just unconscious. I should be able to wake him up.', 14, 0, 100, 0, 0, 0, 0, 63628, 0, 'aph intro');
INSERT INTO `creature_text` VALUES (63818, 0, 0, 'Brewmaster! Bataari scouting parties have been sighted outside the village!', 14, 0, 100, 0, 0, 0, 0, 63630, 0, 'hiseu intro');
INSERT INTO `creature_text` VALUES (63818, 1, 0, 'A Warbringer approaches! Fall back!', 14, 0, 100, 0, 0, 0, 0, 67649, 0, 'hiseu qobi intro');
INSERT INTO `creature_text` VALUES (63942, 0, 0, 'No more excuses, Empress! Eliminate these cretins or I will kill you myself!', 14, 0, 100, 0, 0, 32647, 0, 64111, 0, 'SHA_AGGRO');
INSERT INTO `creature_text` VALUES (63942, 1, 0, 'You must destroy these outsiders, empress! They have come to conquer your people!', 14, 0, 100, 0, 0, 32648, 0, 62905, 0, 'SHA_INTRO');
INSERT INTO `creature_text` VALUES (63942, 2, 0, 'USELESS! Fall limp and die before me, wretched insect. Your kind are worthless to me!', 14, 0, 100, 0, 0, 32645, 0, 62906, 0, 'SHA_SHEKZEER_DEFEAT_1');
INSERT INTO `creature_text` VALUES (63942, 3, 0, 'As for the rest of you, seek me if you dare. Like the others, you will all die screaming.', 14, 0, 100, 0, 0, 32646, 0, 62907, 0, 'SHA_SHEKZEER_DEFEAT_2');
INSERT INTO `creature_text` VALUES (63955, 0, 0, '%s swims excitedly through the vents of warm water.', 16, 0, 100, 0, 0, 0, 0, 63810, 0, '');
INSERT INTO `creature_text` VALUES (63955, 1, 0, '%s rubs against the prow of the Mist-Hopper and whimpers.', 16, 0, 100, 0, 0, 0, 0, 63811, 0, '');
INSERT INTO `creature_text` VALUES (63955, 2, 0, '%s looks around cautiously before leaving his mark on the whale corpse.', 16, 0, 100, 0, 0, 0, 0, 63812, 0, '');
INSERT INTO `creature_text` VALUES (64185, 0, 0, 'A Bataari War Yeti approaches from the Ancient Passage!', 14, 0, 100, 0, 0, 0, 0, 63945, 0, 'yeti passage');
INSERT INTO `creature_text` VALUES (64185, 1, 0, 'A Bataari War Yeti approaches from the Bridge!', 14, 0, 100, 0, 0, 0, 0, 63946, 0, 'yeti bridge');
INSERT INTO `creature_text` VALUES (64185, 2, 0, 'A Bataari War Yeti approaches from the west!', 14, 0, 100, 0, 0, 0, 0, 63947, 0, 'yeti west');
INSERT INTO `creature_text` VALUES (64361, 0, 0, 'Uncle Gao, this brewery was left in your care! What have you done?', 12, 0, 100, 0, 0, 31998, 0, 61478, 0, 'Chen Stormstout to Player');
INSERT INTO `creature_text` VALUES (64361, 1, 0, 'At what cost? The Brewery is trashed! Infested!', 12, 0, 100, 0, 0, 29773, 0, 61479, 0, 'Chen Stormstout to Player');
INSERT INTO `creature_text` VALUES (64361, 2, 0, 'And there are Virmen in the main store!', 12, 0, 100, 0, 0, 29774, 0, 63022, 0, 'Chen Stormstout to Player');
INSERT INTO `creature_text` VALUES (64361, 3, 0, 'I think the brewery might be on fire.', 12, 0, 100, 0, 0, 29775, 0, 63023, 0, 'Chen Stormstout to Player');
INSERT INTO `creature_text` VALUES (64562, 0, 0, 'Here we go. Do try to keep yourselves alive until the end.', 12, 0, 100, 0, 0, 0, 0, 65072, 0, '');
INSERT INTO `creature_text` VALUES (64562, 1, 0, 'We\'re coming for you, Chillheart! Time for payback!', 14, 0, 100, 0, 0, 0, 0, 65074, 0, '');
INSERT INTO `creature_text` VALUES (64562, 2, 0, 'The Talking Skull cackles gleefully as the instructor\'s soul is destroyed.', 16, 0, 100, 0, 0, 0, 0, 65077, 0, '');
INSERT INTO `creature_text` VALUES (64562, 3, 0, 'The Talking Skull seems hesitant to enter the hall, but proceeds with you nonetheless.', 16, 0, 100, 0, 0, 0, 0, 65078, 0, '');
INSERT INTO `creature_text` VALUES (64562, 4, 0, 'I can\'t bear to watch.', 12, 0, 100, 0, 0, 0, 0, 65085, 0, '');
INSERT INTO `creature_text` VALUES (64562, 5, 0, 'The Talking Skull proceeds into the next chamber without saying a word.', 16, 0, 100, 0, 0, 0, 0, 65086, 0, '');
INSERT INTO `creature_text` VALUES (64562, 6, 0, 'Die, Gandling! Kill him!', 14, 0, 100, 0, 0, 0, 0, 65149, 0, '');
INSERT INTO `creature_text` VALUES (64562, 7, 0, 'NO! Get back here coward!', 14, 0, 100, 0, 0, 0, 0, 65203, 0, '');
INSERT INTO `creature_text` VALUES (64562, 8, 0, 'Let\'s go!', 12, 0, 100, 0, 0, 0, 0, 21649, 0, '');
INSERT INTO `creature_text` VALUES (64562, 9, 0, 'There he is! Kill him now, $p!', 14, 0, 100, 0, 0, 0, 0, 65213, 0, '');
INSERT INTO `creature_text` VALUES (64562, 10, 0, 'Stop him! Forget about her!', 14, 0, 100, 0, 0, 0, 0, 65221, 0, '');
INSERT INTO `creature_text` VALUES (64562, 11, 0, 'PREPARE TO DIE, VILLAIN!', 14, 0, 100, 0, 0, 0, 0, 65253, 0, '');
INSERT INTO `creature_text` VALUES (64727, 0, 0, 'The Horde cannot be allowed to recover the Focusing Iris.  Eliminate what remains of their forces, and I will secure the artifact.', 12, 0, 100, 0, 0, 0, 0, 64956, 0, 'Jaina - Intro');
INSERT INTO `creature_text` VALUES (64727, 1, 0, 'I must attune to the Focusing Iris before transport. Protect me!', 12, 0, 100, 0, 0, 0, 0, 64957, 0, 'Jaina - 5td chapter Done');
INSERT INTO `creature_text` VALUES (64727, 2, 0, 'Your people are despicable cowards, orc. You are nothing more than rabid dogs, and you will be put down!', 14, 0, 100, 0, 0, 0, 0, 64958, 0, 'Jaina - Slain');
INSERT INTO `creature_text` VALUES (64727, 3, 0, 'You spit on mercy?  Then you will have NONE.  You want carnage?!  Garrosh will get more blood than EVER he bargained for!', 14, 0, 100, 0, 0, 0, 0, 64959, 0, 'Jaina - Aggro');
INSERT INTO `creature_text` VALUES (64738, 0, 0, 'There are a lot of corpses out there.  Nicely done, $p.', 12, 0, 100, 0, 0, 0, 0, 65384, 0, 'HOODED_CRUSADER_INTRO');
INSERT INTO `creature_text` VALUES (64738, 1, 0, 'Did you get the codex?', 12, 0, 100, 0, 0, 0, 0, 65385, 0, 'HOODED_CRUSADER_CODEX');
INSERT INTO `creature_text` VALUES (64812, 0, 0, 'Very good. Let\'s talk.', 12, 0, 100, 0, 0, 29136, 0, 65088, 0, 'WRATHION_INTRO');
INSERT INTO `creature_text` VALUES (64812, 1, 0, 'Tong! Drinks, please.', 12, 0, 100, 0, 0, 29137, 0, 65089, 0, 'TONG_DRINKS');
INSERT INTO `creature_text` VALUES (64812, 2, 0, 'My father, Deathwing, tried to destroy the whole of Azeroth.', 12, 0, 100, 0, 0, 29138, 0, 65090, 0, 'ABOUT_DEATHWING_1');
INSERT INTO `creature_text` VALUES (64812, 3, 0, 'He was misguided, of course, but he was right about one thing: Our world is... so fragile.', 12, 0, 100, 0, 0, 29139, 0, 65091, 0, 'ABOUT_DEATHWING_2');
INSERT INTO `creature_text` VALUES (64812, 4, 1, 'We are a point of light in a universe of shadow. A candle in a tempest.', 12, 0, 100, 0, 0, 29140, 0, 65092, 0, 'AZEROTH_SCENE_1');
INSERT INTO `creature_text` VALUES (64812, 5, 0, 'Sometimes I think it was the very precariousness of our world that drove my father to madness.', 12, 0, 100, 0, 0, 29141, 0, 65093, 0, 'AZEROTH_SCENE_2');
INSERT INTO `creature_text` VALUES (64812, 6, 0, 'Ah, thank you Tong.', 12, 0, 100, 0, 0, 29142, 0, 65094, 0, 'TONG_HOLDING_CUP');
INSERT INTO `creature_text` VALUES (64812, 7, 0, 'Now, to my point: I believe we are headed towards a reckoning. And no, I am not talking about the current conflict between the Alliance and the Horde.', 12, 0, 100, 0, 0, 29143, 0, 65095, 0, 'WRATHION_OPINION');
INSERT INTO `creature_text` VALUES (64812, 8, 0, 'Believe me - what Garrosh Hellscream achieved in Theramore is nothing compared to the horrors that are even now bearing down on our fragile home.', 12, 0, 100, 0, 0, 29144, 0, 65096, 0, 'ABOUT_GARROSH');
INSERT INTO `creature_text` VALUES (64812, 9, 0, 'But the war deeply troubles me. Do you see my concern?', 12, 0, 100, 0, 0, 29145, 0, 65097, 0, 'AZEROTH_SCENE_3');
INSERT INTO `creature_text` VALUES (64812, 10, 0, 'A divided Azeroth cannot possibly stand against the darkness.', 12, 0, 100, 0, 0, 29146, 0, 65098, 0, 'AZEROTH_SCENE_4');
INSERT INTO `creature_text` VALUES (64812, 11, 0, 'This war has to end. Soon. Before it consumes our strength!', 12, 0, 100, 0, 0, 29147, 0, 65099, 0, 'ABOUT_WAR');
INSERT INTO `creature_text` VALUES (64812, 12, 0, 'You can rest assured that my loyalties lie with your Horde.', 12, 0, 100, 0, 0, 29149, 0, 65101, 0, 'FACTION_H');
INSERT INTO `creature_text` VALUES (64812, 13, 0, 'I want you to know that your Alliance has my full and unwavering support in this campaign.', 12, 0, 100, 0, 0, 29148, 0, 65100, 0, 'FACTION_A');
INSERT INTO `creature_text` VALUES (64812, 14, 0, 'How do we bring a swift and decisive end to the conflict? I believe the answer lies with heroes like you.', 12, 0, 100, 0, 0, 29150, 0, 65102, 0, 'ABOUT_CONFLICT');
INSERT INTO `creature_text` VALUES (64812, 15, 0, 'We must ensure that you are up to the task... and then equip you accordingly.', 12, 0, 100, 0, 0, 29151, 0, 65103, 0, 'ABOUT_GEARED');
INSERT INTO `creature_text` VALUES (64822, 0, 0, 'I often venture out here to think. The view is breathtaking, would you agree?', 12, 0, 100, 0, 0, 29124, 0, 65109, 0, 'WRATHION_INTRO_VENTURE');
INSERT INTO `creature_text` VALUES (64822, 1, 0, 'Pandaria is indeed a jewel. Such beauty. It makes you want to protect her, yes?', 12, 0, 100, 0, 0, 29125, 0, 65110, 0, 'WRATHION_ABOUT_PANDARIA');
INSERT INTO `creature_text` VALUES (64822, 2, 0, 'I feel the same way about Azeroth herself.', 12, 0, 100, 0, 0, 29126, 0, 65111, 0, 'WRATHION_ABOUT_PROTECT_AZEROTH');
