INSERT INTO `creature_text` VALUES (64901, 0, 0, 'Good to see you, $n! Do you have a minute?', 14, 0, 100, 0, 0, 0, 0, 62265, 0, 'brewmaster intro');
INSERT INTO `creature_text` VALUES (64901, 1, 0, 'That\'s the last of the ingredients! Good work, $p! Now the festival can begin!', 14, 0, 100, 0, 0, 0, 0, 62249, 0, 'brewmaster breewing');
