INSERT INTO `creature_text` VALUES (73303, 0, 0, 'Long ago, the Jade Serpent instructed me to purify my spirit and become one with the land, my doubts were vanquished.', 14, 0, 100, 0, 0, 38036, 0, 75933, 0, 'Emperor Shaohao - Yulon Intro');
INSERT INTO `creature_text` VALUES (73303, 1, 0, 'When my journey began, I was reckless in my strength. The White Tiger taught me control. My hatred, anger, and violence were vanquished.', 14, 0, 100, 0, 0, 38037, 0, 75934, 0, 'Emperor Shaohao - Xuen Intro');
INSERT INTO `creature_text` VALUES (73303, 2, 0, 'The Red Crane saw hope in me, and instructed me to look inward. Despite my visage of despair, I found this hope, and the despair was vanquished.', 14, 0, 100, 0, 0, 38038, 0, 75935, 0, 'Emperor Shaohao - Chi-Ji');
INSERT INTO `creature_text` VALUES (73303, 3, 0, 'In the face of great fear, the Black Ox taught me fortitude. Through terror and darkness it persevered. The fear was vanquished.', 14, 0, 100, 0, 0, 38039, 0, 75936, 0, 'Emperor Shaohao - Niuzao');
INSERT INTO `creature_text` VALUES (73303, 4, 0, 'You have walked the trial of wisdom, and learned of the path of the Jade Serpent. May you learn from this lesson, as I did.', 14, 0, 100, 0, 0, 38040, 0, 75937, 0, 'Emperor Shaohao - Yulon Outro');
INSERT INTO `creature_text` VALUES (73303, 5, 0, 'You have walked the trial of strength, and learned of the path of the White Tiger. May you remember this lesson, always.', 14, 0, 100, 0, 0, 38041, 0, 75938, 0, 'Emperor Shaohao - Xuen Outro');
INSERT INTO `creature_text` VALUES (73303, 6, 0, 'You have walked the trial of hope, and learned of the path of the Red Crane. May it guide your footsteps through time.', 14, 0, 100, 0, 0, 38042, 0, 75939, 0, 'Emperor Shaohao - Red Crane Outro');
INSERT INTO `creature_text` VALUES (73303, 7, 0, 'You have walked the trial of fortitude, and learned of the path of the Black Ox. May it bless your passage.', 14, 0, 100, 0, 0, 38043, 0, 75940, 0, 'Emperor Shaohao - Niuzao Outro');
INSERT INTO `creature_text` VALUES (73303, 8, 0, 'Welcome to a very special trial!  The wisdom of a leader is reflected in the greatness of his followers. Let us see if your training has truly inspired those you lead...', 14, 0, 100, 0, 0, 38044, 0, 75309, 0, 'Emperor Shaohao - Intro');
